INSERT INTO `skills` (`id`, `skill_name_vi`, `skill_name_en`, `skill_desc_vi`, `skill_desc_en`, `skill_image`, `skill_video_link`, `champion_id`) VALUES
(26, 'BƯỚC CHÂN LONG KHUYỂN', 'DRAKEHOUND\'S STEP', 'Nhập một lệnh tung đòn đánh hoặc di chuyển khi thi triển kỹ năng sẽ khiến Ambessa lướt đi một khoảng ngắn khi kỹ năng tung ra, ban cho đòn đánh kế tiếp của bà thêm tầm đánh, sát thương, và tốc độ đánh, đồng thời hồi lại nội năng.', 'Entering an attack or movement command while casting an ability will cause Ambessa to dash a short distance once the ability is cast, granting her next attack bonus range, damage, and attack speed, and refunding energy.', 'Icon_Ambessa_Passive.Domina.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0799/ability_0799_P1.webm', 6),
(27, 'ĐÒN QUÉT XẢO QUYỆT / CÚ ĐẬP PHÂN TÁCH', 'CUNNING SWEEP / SUNDERING SLAM', 'Ambessa quét đao xích của bà theo hình bán nguyệt trước mặt, gây sát thương cộng thêm lên những kẻ địch trúng lưỡi đao. Đánh trúng một kẻ địch sẽ biến đổi lần thi triển tiếp theo của kỹ năng này trong thoáng chốc, khiến bà dậm mạnh đao xích theo đường thẳng trước mặt, gây sát thương cộng thêm lên kẻ', 'Ambessa sweeps her twin drakehounds in a semicircle in front of her, dealing bonus damage to enemies hit by the blades. Striking an enemy will transform the next cast of this ability for a short period of time, causing her to slam her twin drakehounds down in a line in front of her, dealing bonus da', 'AmbessaQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0799/ability_0799_Q1.webm', 6),
(28, 'THOÁI THÁC', 'REPUDIATION', 'Ambessa nhận một lá chắn, gồng mình trong thoáng chốc, sau đó dậm mạnh xuống mặt đất gây sát thương lên kẻ địch xung quanh. Nếu bà chặn được sát thương từ kẻ địch (trừ lính địch) khi đang gồng, kỹ năng này sẽ gây thêm sát thương.', 'Ambessa gains a shield, briefly braces herself, and then slams the ground to damage nearby enemies. If she blocked any non-minion damage while bracing herself, this ability will deal increased damage.', 'AmbessaW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0799/ability_0799_W1.webm', 6),
(29, 'LIỆT TRẢM', 'LACERATE', 'Ambessa quất đao xích của bà ra xung quanh, gây sát thương và làm chậm kẻ địch trúng chiêu. Kích hoạt Bước Chân Long Khuyển từ kỹ năng này sẽ khiến bà tung đòn đánh lần thứ hai tại điểm đến của cú lướt.', 'Ambessa whips her twin drakehounds around herself, damaging and slowing nearby enemies. Initiating Drakehound\'s Step from this ability causes her to strike a second time at the end of its dash.', 'AmbessaE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0799/ability_0799_E1.webm', 6),
(30, 'HÀNH QUYẾT CÔNG KHAI', 'PUBLIC EXECUTION', 'Ambessa dịch chuyển đến tướng địch ở xa nhất trên một đường thẳng và áp chế chúng khi đến đích. Sau đó bà quật kẻ địch xuống đất, khiến chúng chịu sát thương và làm choáng.', 'Ambessa blinks to the farthest enemy champion in a line of her choosing and suppresses them upon her arrival. She then slams the enemy into the ground where they take damage and are stunned.', 'AmbessaR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0799/ability_0799_R1.webm', 6),
(31, 'CÚ ĐẬP NGUYỀN RỦA', 'CURSED TOUCH', 'Đòn đánh của Amumu Nguyền Rủa kẻ địch, khiến chúng chịu thêm sát thương chuẩn khi trúng sát thương phép.', 'Amumu\'s basic attacks Curse his enemies, causing them to take bonus true damage from incoming magic damage.', 'Amumu_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0032/ability_0032_P1.webm', 7),
(32, 'QUĂNG DẢI BĂNG', 'BANDAGE TOSS', 'Amumu quăng một dải băng dính vào mục tiêu, làm choáng rồi kéo hắn lại bên cạnh nạn nhân.', 'Amumu tosses a sticky bandage at a target, stunning and damaging the target while he pulls himself to them.', 'BandageToss.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0032/ability_0032_Q1.webm', 7),
(33, 'TUYỆT VỌNG', 'DESPAIR', 'Đau đớn tột cùng, kẻ địch xung quanh mất một số phần trăm Máu tối đa của chúng mỗi giây và bị làm mới Nguyền Rủa trên người.', 'Overcome by anguish, nearby enemies lose a percentage of their maximum Health each second and have their Curses refreshed.', 'AuraofDespair.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0032/ability_0032_W1.webm', 7),
(34, 'GIẬN DỮ', 'TANTRUM', 'Vĩnh viễn giảm lượng sát thương vật lý mà Amumu gánh chịu. Amumu có thể phóng thích cơn giận, gây sát thương lên kẻ địch xung quanh. Mỗi khi Amumu bị đánh trúng, hồi chiêu của Giận Dữ được giảm đi.', 'Permanently reduces the physical damage Amumu would take. Amumu can unleash his rage, dealing damage to surrounding enemies. Each time Amumu is hit, the cooldown on Tantrum is reduced.', 'Tantrum.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0032/ability_0032_E1.webm', 7),
(35, 'LỜI NGUYỀN XÁC ƯỚP U SẦU', 'CURSE OF THE SAD MUMMY', 'Amumu trói những đơn vị địch xung quanh với băng gạc của mình, đặt hiệu ứng Nguyền Rủa, gây sát thương và làm choáng chúng.', 'Amumu entangles surrounding enemy units in bandages, applying his Curse, damaging and stunning them.', 'CurseoftheSadMummy.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0032/ability_0032_R1.webm', 7),
(36, 'TÁI SINH', 'REBIRTH', 'Sau khi bị hạ gục, Anivia hóa thành một quả trứng và hồi lại đầy bình máu.', 'Upon taking fatal damage, Anivia reverts to an egg and is reborn with full health.', 'Anivia_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0034/ability_0034_P1.webm', 8),
(37, 'QUẢ CẦU BĂNG', 'FLASH FROST', 'Anivia chập cánh lại và gọi ra một quả cầu băng bay về kẻ địch, gây trạng thái hạ nhiệt và sát thương bất cứ ai trên đường bay của nó. Khi quả cầu phát nổ, nó gây sát thương tầm trung trong bán kính, làm choáng bất cứ ai trong vòng ảnh hưởng.', 'Anivia brings her wings together and summons a sphere of ice that flies towards her opponents, chilling and damaging anyone in its path. When the sphere explodes it does moderate damage in a radius, stunning anyone in the area.', 'FlashFrost.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0034/ability_0034_Q1.webm', 8),
(38, 'TƯỜNG BĂNG', 'CRYSTALLIZE', 'Anivia ngưng đọng độ ẩm trong không khí thành một bức tường băng vững chắc chặn đường quân địch. Bức tường sẽ tồn tại trong một thời gian ngắn trước khi tan chảy.', 'Anivia condenses the moisture in the air into an impassable wall of ice to block all movement. The wall only lasts a short duration before it melts.', 'Crystallize.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0034/ability_0034_W1.webm', 8),
(39, 'TÊ CÓNG', 'FROSTBITE', 'Anivia đập cánh, tạt một luồng gió buốt giá vào mục tiêu, gây sát thương. Nếu mục tiêu vừa bị trúng Quả Cấu Băng hoặc bị dính Bão Tuyết toàn phần, sát thương sẽ nhân đôi.', 'With a flap of her wings, Anivia blasts a freezing gust of wind at her target, dealing damage. If the target was recently hit by Flash Frost or damaged by a fully formed Glacial Storm, the damage they take is doubled.', 'Frostbite.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0034/ability_0034_E1.webm', 8),
(40, 'BÃO TUYẾT', 'GLACIAL STORM', 'Anivia gọi xuống một cơn mưa băng sát thương kẻ địch và làm chậm chúng.', 'Anivia summons a driving rain of ice and hail to damage her enemies and slow their advance.', 'GlacialStorm.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0034/ability_0034_R1.webm', 8),
(41, 'HỎA CUỒNG', 'PYROMANIA', 'Sau khi dùng kỹ năng 4 lần, kỹ năng công kích tiếp theo của Annie sẽ làm choáng mục tiêu. Nhận sẵn Hỏa Cuồng khi Annie bắt đầu trận đấu và sau khi hồi sinh.', 'After casting 4 spells, Annie\'s next offensive spell will stun the target. Annie begins the game and respawns with Pyromania available.', 'Annie_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0001/ability_0001_P1.webm', 9),
(42, 'HỎA CẦU', 'DISINTEGRATE', 'Annie phóng một quả cầu lửa gây sát thương, và hồi lại năng lượng tổn thất nếu kết liễu được mục tiêu.', 'Annie hurls a Mana infused fireball, dealing damage and refunding the Mana cost if it destroys the target.', 'AnnieQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0001/ability_0001_Q1.webm', 9),
(43, 'THIÊU CHÁY', 'INCINERATE', 'Annie chưởng lửa theo hình nón, gây sát thương lên tất cả kẻ địch trong vùng ảnh hưởng.', 'Annie casts a blazing cone of fire, dealing damage to all enemies in the area.', 'AnnieW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0001/ability_0001_W1.webm', 9),
(44, 'KHIÊN LỬA', 'MOLTEN SHIELD', 'Tạo cho Annie hoặc một đồng minh lớp lá chắn, gia tăng Tốc Độ Di Chuyển và gây sát thương lên những kẻ địch tấn công cô bé bằng đòn đánh hoặc kỹ năng.', 'Grants Annie or an ally a shield, a burst of Move Speed, and damages enemies who strike her with attacks or spells.', 'AnnieE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0001/ability_0001_E1.webm', 9),
(45, 'TRIỆU HỒI: TIBBERS', 'SUMMON: TIBBERS', 'Annie gọi chú gấu Tibbers của mình ra, gây sát thương lên vùng ảnh hưởng. Tibbers có thể đánh và thiêu đốt kẻ địch quanh nó.', 'Annie wills her bear Tibbers to life, dealing damage to units in the area. Tibbers can attack and also burns enemies that stand near him.', 'AnnieR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0001/ability_0001_R1.webm', 9),
(46, 'SÁT THỦ VÀ TIÊN TRI', 'THE HITMAN AND THE SEER', 'Aphelios sử dụng 5 Vũ Khí Lunari do người chị Alune tạo ra. Anh có thể dùng hai cái một lúc: một chính và một phụ. Mỗi vũ khí có Đòn đánh và Kỹ năng riêng. Đòn đánh và kỹ năng tiêu hao đạn của vũ khí. Khi hết đạn, Aphelios bỏ vũ khí đó và Alune triệu hồi vũ khí tiếp theo trong số 5 vũ khí.', 'Aphelios wields 5 Lunari Weapons made by his sister Alune. He has access to two at a time: one main-hand and one off-hand. Each weapon has a unique Basic Attack and Ability. Attacks and abilities consume a weapon\'s ammo. When out of ammo, Aphelios discards the weapon and Alune summons the next of th', 'ApheliosP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0523/ability_0523_P1.webm', 10),
(47, 'KỸ NĂNG VŨ KHÍ', 'WEAPON ABILITES', 'Aphelios có 5 kỹ năng kích hoạt khác nhau, tùy theo vũ khí chính: Calibrum (Súng Thiên Lý): Bắn tầm xa, đánh dấu mục tiêu để có thể bắn thêm một phát từ bất kỳ đâu trên bản đồ. Severum (Súng Huyết Tinh): Chạy thật nhanh và tấn công các kẻ địch xung quanh bằng cả hai vũ khí. Gravitum (Pháo Trọng Trườ', 'Aphelios has 5 different activated abilities, based on his main-hand weapon: Calibrum (Rifle): Long range shot that marks its target for a long-range follow-up attack. Severum (Scythe Pistol): Run fast while attacking nearby enemies with both weapons. Gravitum (Cannon): Root all enemies slowed by th', 'ApheliosQ_ClientTooltipWrapper.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0523/ability_0523_Q1.webm', 10),
(48, 'ĐỔI', 'PHASE', 'Aphelios đổi vũ khí chính sang vũ khí phụ, cùng với đó là đòn đánh và kỹ năng kích hoạt.', 'Aphelios swaps his main-hand gun with his off-hand gun, replacing his basic attack and activated ability.', 'ApheliosW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0523/ability_0523_W1.webm', 10),
(49, 'HỆ THỐNG CHUỖI VŨ KHÍ', 'WEAPON QUEUE SYSTEM', 'Aphelios không có kỹ năng thứ ba. Chỗ này để hiển thị vũ khí kế tiếp Alune sẽ triệu hồi cho anh. Thứ tự vũ khí ban đầu là cố định, nhưng có thể thay đổi dần -- khi một vũ khí hết đạn, nó được đặt xuống cuối danh sách', 'Aphelios has no third ability. This slot shows the next weapon Alune will give him. Weapon order begins fixed but may change over game time -- when a weapon is out of ammo it goes to the end of the order.', 'ApheliosE_ClientTooltipWrapper.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0523/ability_0523_E1.webm', 10),
(50, 'ÁNH TRĂNG DẪN LỐI', 'MOONLIGHT VIGIL', 'Bắn một viên nguyệt đạn phát nổ trên tướng địch. Đặt hiệu ứng của vũ khí chính của Aphelios.', 'Fire a concentrated blast of moonlight that explodes on enemy champions. Applies the unique effect of Aphelios\' main-hand gun.', 'ApheliosR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0523/ability_0523_R1.webm', 10),
(61, 'THANH TẨY LINH HỒN', 'SPIRIT ABJURATION', 'Đòn đánh và kỹ năng của Aurora sẽ thanh tẩy linh hồn trên những kẻ địch mà cô gây sát thương lên. Linh hồn được thanh tẩy sẽ đi theo Aurora, hồi máu và tăng thêm tốc độ di chuyển cho cô.', 'Aurora\'s spells and attacks exorcise spirits from the enemies she damages. Exorcised spirits follow Aurora around, healing her and granting her bonus movement speed.', 'AuroraPassive.Aurora.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0893/ability_0893_P1.webm', 13),
(62, 'BÙA CHÚ TÂM LINH', 'TWOFOLD HEX', 'Aurora bắn ra một bùa chú, nguyền rủa bất cứ kẻ địch nào trúng chiêu. Cô ấy có thể tái kích hoạt kỹ năng này để thu những lời nguyền đang kích hoạt về bản thân, gây sát thương lên những kẻ địch trúng chiêu trên đường thu về.', 'Aurora sends out a missile that curses any enemies it hits. She can then recast the ability to draw active curses back toward herself, damaging foes who are hit along the way.', 'AuroraQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0893/ability_0893_Q1.webm', 13),
(63, 'BÊN KIA BỨC MÀN', 'ACROSS THE VEIL', 'Aurora nhảy về hướng chỉ định, bước vào cõi linh giới khi tới nơi và trở nên tàng hình trong một khoảng thời gian ngắn.', 'Aurora leaps in a direction of her choosing, entering the spirit realm upon landing and becoming invisible for a short duration of time.', 'AuroraW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0893/ability_0893_W1.webm', 13),
(64, 'HIỆN TƯỢNG LẠ KỲ', 'THE WEIRDING', 'Aurora hội tụ cõi linh giới, giải phóng năng lượng linh hồn, gây sát thương phép và làm chậm những kẻ địch trúng chiêu. Sau đó cô ấy sẽ nhảy lùi lại để vào vị trí an toàn.', 'Aurora converges the realms, sending out a blast of spirit magic that damages and slows enemies before Aurora hops backward to safety.', 'AuroraE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0893/ability_0893_E1.webm', 13),
(65, 'GIỮA HAI THẾ GIỚI', 'BETWEEN WORLDS', 'Aurora nhảy về hướng chỉ định, phóng ra một sóng âm gây sát thương và làm chậm những kẻ địch trúng chiêu. Sau đó, cô ấy tạo ra một khu vực có khả năng giữ chân kẻ địch ở bên trong và cho phép Aurora dịch chuyển từ đầu này sang đầu kia.', 'Aurora leaps in a direction of her choosing, releasing a shockwave that damages and slows any enemies it hits. Afterward, she creates an area that traps enemies within it and allows Aurora to teleport from one side of the area to the other.', 'AuroraR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0893/ability_0893_R1.webm', 13),
(66, 'DI SẢN CỦA SHURIMA', 'SHURIMA\'S LEGACY', 'Azir có thể triệu hồi Đĩa Mặt Trời từ tàn tích trụ.', 'Azir can summon the Disc of the Sun from the ruins of allied or enemy turrets.', 'Azir_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0268/ability_0268_P1.webm', 14),
(67, 'CÁT CÀN QUÉT', 'CONQUERING SANDS', 'Azir lệnh cho tất cả Binh Sĩ Cát lao về hướng đã chọn. Binh Sĩ Cát gây sát thương phép lên tất cả mục tiêu chúng lướt qua và gây hiệu ứng làm chậm trong 1 giây.', 'Azir sends all Sand Soldiers towards a location. Sand Soldiers deal magic damage to enemies they pass through and apply a slow for 1 second.', 'AzirQWrapper.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0268/ability_0268_Q1.webm', 14),
(68, 'TRỖI DẬY!', 'ARISE!', 'Azir triệu hồi ra một Binh Sĩ Cát và sử dụng nó như một công cụ để tấn công các kẻ địch trong tầm đánh của quân lính. Những đòn đánh này sẽ gây sát thương phép lên kẻ địch theo đường thẳng. Trỗi Dậy! Cũng tăng tốc độ đánh nội tại cho Azir và các Binh Sĩ Cát.', 'Azir summons a Sand Soldier to attack nearby targets for him, replacing his basic attack against targets within the soldier\'s range. Their attacks deal magic damage to enemies in a line. Arise! also passively grants attack speed to Azir and his Sand Soldiers.', 'AzirW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0268/ability_0268_W1.webm', 14),
(69, 'CÁT LƯU ĐỘNG', 'SHIFTING SANDS', 'Azir tạo lá chắn cho bản thân và lướt tới chỗ một Binh Sĩ Cát, gây sát thương lên kẻ địch. Nếu lướt trúng tướng, ngài ngay lập tức dừng lướt và nhận được một tích trữ Binh Sĩ Cát.', 'Azir shields himself briefly and dashes to one of his Sand Soldiers, damaging enemies. If he hits an enemy champion, he instantly readies a new Sand Soldier for deployment and halts his dash.', 'AzirEWrapper.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0268/ability_0268_E1.webm', 14),
(70, 'PHÂN CHIA THIÊN HẠ', 'EMPEROR\'S DIVIDE', 'Azir triệu hồi ra một bức tường những binh sĩ xông tới, đẩy lùi và sát thương kẻ địch.', 'Azir summons a wall of soldiers which charge forward, knocking back and damaging enemies.', 'AzirR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0268/ability_0268_R1.webm', 14),
(71, 'THẾ GIỚI MẦU NHIỆM', 'TRAVELER\'S CALL', 'Tinh Linh Chuông: Bard thu hút các tinh linh nhỏ bé hỗ trợ ông đánh thường, gây thêm sát thương phép. Khi Bard thu thập đủ số Chuông, các tinh linh sẽ gây thêm sát thương diện rộng và làm chậm kẻ địch. Chuông: Chuông xuất hiện ngẫu nhiên cho Bard thu thập. Chúng cho kinh nghiệm, hồi phục năng lượng,', 'Meeps: Bard attracts lesser spirits that assist with his basic attacks to deal extra magic damage. When Bard has collected enough Chimes, his meeps will also deal damage in an area and slow enemies hit. Chimes: Ancient chimes randomly appear for Bard to collect. These grant experience, restore mana,', 'Bard_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0432/ability_0432_P1.webm', 15),
(72, 'MẮT XÍCH KHÔNG GIAN', 'COSMIC BINDING', 'Bard bắn ra một luồng sóng năng lượng làm chậm kẻ địch đầu tiên trúng chiêu, rồi tiếp tục bay đi. Nếu va phải tường, nó sẽ làm choáng mục tiêu khởi điểm; nếu va phải một tướng địch khác, nó sẽ làm choáng cả hai.', 'Bard fires a missile which will slow the first enemy struck, and continue onward. If it strikes a wall, it will stun the initial target; if it strikes another enemy, it will stun them both.', 'BardQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0432/ability_0432_Q1.webm', 15),
(73, 'ĐIỆN AN LẠC', 'CARETAKER\'S SHRINE', 'Tạo ra một ngôi điện giúp hồi Máu và có thể được cường hóa theo thời gian. Nó sẽ biến mất sau khi hồi máu và tăng tốc cho đồng minh đầu tiên chạm vào.', 'Reveals a Health shrine which powers up over a short time, disappearing after healing and speeding up the first ally that touches it.', 'BardW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0432/ability_0432_W1.webm', 15),
(74, 'HÀNH TRÌNH KÌ DIỆU', 'MAGICAL JOURNEY', 'Bard mở ra một cánh cổng tại vùng địa hình cạnh bên. Tất cả kẻ địch lẫn đồng minh đều có thể tham gia vào chuyến hành trình một chiều xuyên địa hình này.', 'Bard opens a portal in nearby terrain. Allies and enemies alike can take a one-way trip through that terrain by moving into the portal.', 'BardE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0432/ability_0432_E1.webm', 15),
(75, 'THIÊN MỆNH KHẢ BIẾN', 'TEMPERED FATE', 'Bard phóng thích năng lượng vào một vị trí khiến tất cả các tướng, lính, quái và trụ trong vùng ảnh hưởng đều phải rơi vào trạng thái tĩnh trong thoáng chốc.', 'Bard sends spirit energy arcing to a location, putting all champions, minions, monsters, and turrets hit into stasis for a brief time.', 'BardR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0432/ability_0432_R1.webm', 15),
(81, 'LÁ CHẮN NĂNG LƯỢNG', 'MANA BARRIER', 'Blitzcrank nhận được một lá chắn dựa theo số năng lượng khi thấp máu.', 'Blitzcrank gains a shield based on their mana when dropping to low health.', 'Blitzcrank_ManaBarrier.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0053/ability_0053_P1.webm', 17),
(82, 'BÀN TAY HỎA TIỄN', 'ROCKET GRAB', 'Blitzcrank bắn tay phải ra chộp lấy kẻ địch trên đường, gây sát thương rồi kéo mục tiêu về phía mình.', 'Blitzcrank fires their right hand to grab an opponent on its path, dealing damage and dragging it back to them.', 'RocketGrab.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0053/ability_0053_Q1.webm', 17),
(83, 'TĂNG TỐC', 'OVERDRIVE', 'Blitzcrank vận hành bộ biến áp khiến nó được tăng mạnh Tốc độ Di chuyển và Tốc độ Đánh. Nó tạm thời bị làm chậm sau khi hết thời gian hiệu lực.', 'Blitzcrank super charges themself to get dramatically increased Move and Attack Speed. They are temporarily slowed after the effect ends.', 'Overdrive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0053/ability_0053_W1.webm', 17),
(84, 'ĐẤM MÓC', 'POWER FIST', 'Blitzcrank gồng nắm đấm khiến đòn đánh kế gây gấp hai sát thương và hất kẻ địch lên trời.', 'Blitzcrank charges up their fist to make the next attack deal double damage and pop their target up in the air.', 'PowerFist.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0053/ability_0053_E1.webm', 17),
(85, 'TRƯỜNG ĐIỆN TỪ', 'STATIC FIELD', 'Kẻ địch bị Blitzcrank tấn công bị đánh dấu và chịu sát thương điện từ sau 1 giây. Ngoài ra, Blitzcrank có thể kích hoạt kỹ năng này để phá lá chắn, gây sát thương và làm câm lặng kẻ địch xung quanh.', 'Enemies attacked by Blitzcrank are marked and take lightning damage after 1 second. Additionally, Blitzcrank can activate this ability to remove nearby enemies\' shields, damage them, and silence them briefly.', 'StaticField.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0053/ability_0053_R1.webm', 17),
(86, 'BỎNG', 'BLAZE', 'Các kỹ năng của Brand khiến mục tiêu bị bỏng, gây sát thương trong vòng 4 giây, cộng dồn nhiều nhất 3 lần. Nếu Brand hạ gục kẻ địch đang bị bỏng, hắn hồi lại năng lượng. Khi Bỏng đạt cộng dồn tối đa trên một tướng hoặc quái to, nó trở nên bất ổn và phát nổ sau 2 giây, đặt hiệu ứng kỹ năng và gây lượ', 'Brand\'s spells light his targets ablaze, dealing damage over 4 seconds, stacking up to 3 times. If Brand kills an enemy while it is ablaze he regains mana. When Blaze reaches max stacks on a Champion or large monster, it becomes unstable. It detonates in 2 seconds, applying spell effects and dealing', 'BrandP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0063/ability_0063_P1.webm', 18),
(87, 'VỆT LỬA', 'SEAR', 'Brand phóng quả cầu lửa tới phía trước gây sát thương phép thuật. Nếu mục tiêu đang bị bỏng, Vệt Lửa sẽ làm choáng nạn nhân trong 1.5 giây.', 'Brand launches a ball of fire forward that deals magic damage. If the target is ablaze, Sear will stun the target for 1.5 seconds.', 'BrandQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0063/ability_0063_Q1.webm', 18),
(88, 'CỘT LỬA', 'PILLAR OF FLAME', 'Sau một thoáng, Brand tạo ra một cột lửa tại vị trí đã chọn, gây sát thương phép lên những kẻ địch trong vùng ảnh hưởng. Những nạn nhân đang bị bỏng trúng chiêu sẽ phải chịu thêm 25% sát thương.', 'After a short delay, Brand creates a Pillar of Flame at a target area, dealing magic damage to enemy units within the area. Units that are ablaze take an additional 25% damage.', 'BrandW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0063/ability_0063_W1.webm', 18),
(89, 'BÙNG CHÁY', 'CONFLAGRATION', 'Brand gọi ra một vụ nổ lớn ngay trên mục tiêu, lan sang những kẻ địch ở gần và gây sát thương phép lên chúng. Nếu mục tiêu bị bỏng, Bùng Cháy sẽ lan gấp đôi.', 'Brand conjures a powerful blast at his target that spreads to nearby enemies, dealing magic damage. If the target is ablaze, Conflagration\'s spread is doubled.', 'BrandE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0063/ability_0063_E1.webm', 18),
(90, 'BÃO LỬA', 'PYROCLASM', 'Brand phóng ra một luồng lửa hủy diệt có thể nảy tối đa 5 lần giữa Brand và những kẻ địch ở gần, gây sát thương phép lên chúng mỗi lần nảy. Luồng lửa sẽ ưu tiên những Tướng chịu nhiều cộng dồn Bỏng. Nếu mục tiêu bị bỏng, Bão Lửa sẽ thoáng làm chậm chúng.', 'Brand unleashes a devastating torrent of fire that bounces up to 5 times off of Brand and nearby enemies, dealing magic damage to enemies each time bounce. Bounces prioritize stacking Blaze to max on Champions. If a target is ablaze, Pyroclasm will briefly slow them.', 'BrandR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0063/ability_0063_R1.webm', 18),
(91, 'ĐÁNH NGẤT NGƯ', 'CONCUSSIVE BLOWS', 'Đòn đánh của Braum gây hiệu ứng Đánh Ngất Ngư. Sau khi đã kích phát điểm tích trữ đầu tiên, các đòn đánh từ đồng minh cũng sẽ cộng dồn hiệu ứng Đánh Ngất Ngư. Khi đã đủ 4 điểm, mục tiêu sẽ bị choáng và chịu sát thương phép. Trong vài giây kế tiếp nạn nhân sẽ không thể chịu thêm điểm cộng dồn hiệu ứn', 'Braum\'s basic attacks apply Concussive Blows. Once the first stack is applied, ally basic attacks also stack Concussive Blows. Upon reaching 4 stacks, the target is stunned and takes magic damage. For the next few seconds they cannot receive new stacks, but take bonus magic damage from Braum\'s attac', 'Braum_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0201/ability_0201_P1.webm', 19),
(92, 'TUYẾT TÊ TÁI', 'WINTER\'S BITE', 'Braum phóng băng từ chiếc khiên của hắn, gây sát thương phép và làm chậm. Gây một điểm tích trữ hiệu ứng Đánh Ngất Ngư.', 'Braum propels freezing ice from his shield, slowing and dealing magic damage. Applies a stack of Concussive Blows.', 'BraumQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0201/ability_0201_Q1.webm', 19),
(93, 'NẤP SAU TA', 'STAND BEHIND ME', 'Braum phóng tới một mục tiêu tướng hoặc lính đồng minh. Khi đến nơi Braum và đơn vị đồng minh đó sẽ được nhận thêm Giáp và Kháng Phép trong vài giây.', 'Braum leaps to a target allied champion or minion. On arrival, Braum and the ally gain Armor and Magic Resist for a few seconds.', 'BraumW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0201/ability_0201_W1.webm', 19),
(94, 'TỐI KIÊN CƯỜNG', 'UNBREAKABLE', 'Braum giương chiếc khiên của mình lên theo một phương hướng trong vài giây, tất cả các tia đạn đạo bay về phía đấy khi va vào hắn sẽ bị tiêu hủy hoàn toàn. Ngoài ra Braum còn chặn lại toàn bộ sát thương từ đòn đánh đầu tiên đồng thời giảm lại sát thương của toàn bộ các đòn đánh về sau tung ra từ hướ', 'Braum raises his shield in a direction for several seconds, intercepting all projectiles causing them to hit him and be destroyed. He negates the damage of the first attack completely and reduces the damage of all subsequent attacks from this direction.', 'BraumE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0201/ability_0201_E1.webm', 19),
(95, 'BĂNG ĐỊA CHẤN', 'GLACIAL FISSURE', 'Braum dậm mạnh xuống mặt đất, hất văng những kẻ địch cạnh bên, và trong đường thẳng phía trước hắn lên không. Một vệt rạn nứt sẽ hiện ra theo đường thẳng đó gây hiệu ứng làm chậm lên các nạn nhân.', 'Braum slams the ground, knocking up enemies nearby and in a line in front of him. A fissure is left along the line that slows enemies.', 'BraumRWrapper.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0201/ability_0201_R1.webm', 19),
(101, 'THIỆN XẠ', 'HEADSHOT', 'Sau một vài phát bắn, hoặc khi bắn vào mục tiêu dẫm bẫy hay dính lưới, Caitlyn bắn một phát xuyên táo gây thêm sát thương (gia tăng theo tỉ lệ chí mạng). Với mục tiêu dẫm bẫy hay dính lưới, tầm bắn Thiện Xạ tăng gấp đôi.', 'Every few basic attacks, or against a target she has trapped or netted, Caitlyn will fire a headshot dealing bonus damage that scales with her critical strike chance. On trapped or netted targets, Caitlyn\'s Headshot attack range is doubled.', 'Caitlyn_Headshot.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0051/ability_0051_P1.webm', 21),
(102, 'BẮN XUYÊN TÁO', 'PILTOVER PEACEMAKER', 'Caitlyn nâng khẩu súng trường của mình lên trong 1 giây rồi bắn ra một phát đạn xuyên phá gây sát thương vật lí (sát thương giảm dần lên các mục tiêu trúng đòn tiếp theo).', 'Caitlyn revs up her rifle for 1 second to unleash a penetrating shot that deals physical damage (deals less damage to subsequent targets).', 'CaitlynQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0051/ability_0051_Q1.webm', 21),
(103, 'BẪY YORDLE', 'YORDLE SNAP TRAP', 'Caitlyn đặt ra một chiếc bẫy, tướng địch khi dẫm vào bẫy sẽ bị lộ diện và bất động trong 1.5 giây, và cho Caitlyn một phát bắn Thiện Xạ.', 'Caitlyn sets a trap that, when sprung, reveals and immobilizes the enemy champion for 1.5 seconds, granting Caitlyn an empowered Headshot.', 'CaitlynW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0051/ability_0051_W1.webm', 21),
(104, 'LƯỚI 90', '90 CALIBER NET', 'Caitlyn bắn ra một tấm lưới làm chậm mục tiêu của cô ta. Còn Caitlyn sẽ bay ngược về phía sau.', 'Caitlyn fires a heavy net to slow her target. The recoil knocks Caitlyn back.', 'CaitlynE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0051/ability_0051_E1.webm', 21),
(105, 'BÁCH PHÁT BÁCH TRÚNG', 'ACE IN THE HOLE', 'Caitlyn ngắm cẩn thận cho một cú bắn chuẩn xác, gây sát thương khủng khiếp lên một mục tiêu ở cự ly xa. Tướng đối phương có thể hứng đạn thế cho đồng đội của chúng.', 'Caitlyn takes time to line up the perfect shot, dealing massive damage to a single target at a huge range. Enemy champions can intercept the bullet for their ally.', 'CaitlynR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0051/ability_0051_R1.webm', 21),
(106, 'THÍCH ỨNG PHÒNG NGỰ', 'ADAPTIVE DEFENSES', 'Đòn đánh lên tướng giúp tạo lá chắn tỉ lệ theo máu tối đa của Camille dựa trên loại sát thương của mục tiêu (Vật lý hoặc Phép) trong một khoảng thời gian ngắn.', 'Basic attacks on champions grant a shield equal to a percentage of Camille\'s maximum health against their damage type (Physical or Magic) for a brief duration.', 'Camille_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0164/ability_0164_P1.webm', 22),
(107, 'GIAO THỨC CHUẨN XÁC', 'PRECISION PROTOCOL', 'Đòn đánh kế của Camille gây thêm sát thương và tăng Tốc độ Di chuyển. Kỹ năng này có thể tái sử dụng sau chốc lát, gây thêm nhiều sát thương nếu Camille ngừng lại một chút giữa hai đòn tấn công.', 'Camille\'s next attack deals bonus damage and grants bonus Move Speed. This spell can be recast for a short period of time, doing significantly increased bonus damage if Camille delays a period of time between the two attacks.', 'CamilleQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0164/ability_0164_Q1.webm', 22),
(108, 'ĐÁ QUÉT CHIẾN THUẬT', 'TACTICAL SWEEP', 'Camille quét theo hình nón sau một khoảng trễ, gây sát thương. Kẻ địch ở nửa ngoài vùng quét bị làm chậm và chịu thêm sát thương, đồng thời Camille được hồi máu.', 'Camille blasts in a cone after a delay, dealing damage. Enemies in the outer half are slowed and take extra damage, while also healing Camille.', 'CamilleW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0164/ability_0164_W1.webm', 22),
(109, 'BẮN DÂY MÓC', 'HOOKSHOT', 'Camille đu đến chỗ tường, rồi nhảy khỏi đó và hất tung tướng địch khi chạm đất.', 'Camille pulls herself to a wall, leaping off and knocking up enemy champions upon landing.', 'CamilleE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0164/ability_0164_E1.webm', 22),
(110, 'TỐI HẬU THƯ', 'THE HEXTECH ULTIMATUM', 'Camille lướt tới tướng chỉ định, nhốt chúng trong một khu vực. Cô cũng gây thêm sát thương phép trên đòn đánh lên mục tiêu.', 'Camille dashes to target champion, anchoring them to the area. She also deals bonus magic damage to the target with her basic attacks.', 'CamilleR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0164/ability_0164_R1.webm', 22),
(121, 'ĐẠN HEXTECH', 'HEXTECH MUNITIONS', 'Một phần sát thương đòn đánh cơ bản của Corki sẽ được gây ra dưới dạng sát thương chuẩn cộng thêm.', 'A percentage of Corki\'s basic attack damage is dealt as bonus true damage.', 'Corki_RapidReload.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0042/ability_0042_P1.webm', 25),
(122, 'BOM PHỐT-PHO', 'PHOSPHORUS BOMB', 'Corki bắn một quả bom sáng vào vị trí mục tiêu, gây sát thương phép lên những nạn nhân trúng chiêu. Vụ nổ làm lộ diện các đơn vị trong vùng ảnh hưởng trong thoáng chốc.', 'Corki fires a flash bomb at a target location, dealing magic damage to enemies in the area. This attack additionally reveals units in the area for a duration.', 'PhosphorusBomb.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0042/ability_0042_Q1.webm', 25),
(123, 'THẢM LỬA', 'VALKYRIE', 'Corki bay một quãng ngắn, thả bom tạo ra một vệt lửa gây sát thương lên kẻ địch đứng trên đó.', 'Corki flies a short distance, dropping bombs that create a trail of fire that damages opponents who remain in it.', 'CarpetBomb.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0042/ability_0042_W1.webm', 25),
(124, 'SÚNG MÁY', 'GATLING GUN', 'Súng Máy của Corki bắn liên tục theo hình nón phía trước hắn, gây sát thương và giảm Giáp lẫn Kháng Phép của kẻ địch.', 'Corki\'s gatling gun rapidly fires in a cone in front of him, dealing damage and reducing enemy Armor and Magic Resist.', 'GGun.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0042/ability_0042_E1.webm', 25),
(125, 'TÊN LỬA ĐỊNH HƯỚNG', 'MISSILE BARRAGE', 'Corki bắn một quả hỏa tiễn vào vị trí mục tiêu sẽ nổ tung sau khi khi va chạm, gây sát thương lên những kẻ địch quanh vùng ảnh hưởng. Corki tích trữ một hỏa tiễn mỗi 12 giây lên đến tổng cộng 7 quả. Mỗi quả thứ 3 sẽ là quả Cực Đại, gây sát thương cao hơn.', 'Corki fires a missile toward his target location that explodes on impact, dealing damage to enemies in an area. Corki stores missiles over time, up to a maximum. Every 3rd missile fired will be a Big One, dealing extra damage.', 'MissileBarrage.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0042/ability_0042_R1.webm', 25),
(126, 'XUẤT HUYẾT', 'HEMORRHAGE', 'Đòn đánh và kỹ năng sát thương của Darius khiến kẻ địch xuất huyết, chịu sát thương vật lý trong vòng 5 giây, cộng dồn tối đa 5 lần. Khi tướng địch đạt tối đa cộng dồn, Darius hóa cuồng và nhận lượng lớn Sức mạnh Công kích.', 'Darius\' attacks and damaging abilities cause enemies to bleed for physical damage over 5 seconds, stacking up to 5 times. Darius enrages and gains massive Attack Damage when his target reaches max stacks.', 'Darius_Icon_Hemorrhage.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0122/ability_0122_P1.webm', 26),
(127, 'TÀN SÁT', 'DECIMATE', 'Darius múa rìu xung quanh mình. Những kẻ địch trúng phải lưỡi rìu sẽ cho nhiều sát thương hơn những kẻ bị trúng phải cán rìu. Darius sẽ hồi máu lại dựa vào số tướng và quái to bị lưỡi rìu chém phải.', 'Darius winds up and swings his axe in a wide circle. Enemies struck by the blade take more damage than those struck by the handle. Darius heals based on enemy champions and large monsters hit by the blade.', 'DariusCleave.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0122/ability_0122_Q1.webm', 26),
(128, 'ĐÁNH THỌT', 'CRIPPLING STRIKE', 'Đòn đánh tiếp theo của Darius nhắm vào động mạch kẻ địch. Khi bị chảy máu trầm trọng, Tốc Độ Di Chuyển của chúng sẽ chậm đi đáng kể.', 'Darius\'s next attack strikes an enemy\'s crucial artery. As they bleed out, their Move Speed is slowed.', 'DariusNoxianTacticsONH.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0122/ability_0122_W1.webm', 26),
(129, 'BẮT GIỮ', 'APPREHEND', 'Darius cường hóa chiếc rìu của mình, khiến sát thương vật lí của hắn bỏ qua một số phần trăm Giáp của mục tiêu. Khi được kích hoạt, Darius sẽ quét kẻ địch bằng chiếc rìu của mình và kéo chúng đến cạnh bên hắn.', 'Darius hones his axe, passively causing his physical damage to ignore a percentage of his target\'s Armor. When activated, Darius sweeps up his enemies with his axe\'s hook and pulls them to him.', 'DariusAxeGrabCone.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0122/ability_0122_E1.webm', 26),
(130, 'MÁY CHÉM NOXUS', 'NOXIAN GUILLOTINE', 'Darius nhảy xổ vào một tướng địch rồi tấn công bằng một cú bổ chí tử, gây sát thương chuẩn. Lượng sát thương sẽ gia tăng dựa trên những điểm cộng dồn Xuất Huyết trên mục tiêu. Nếu Máy Chém Noxus là đòn kết liễu, kĩ năng này có thể được tái sử dụng trong một khoảng thời gian ngắn.', 'Darius leaps to an enemy champion and strikes a lethal blow, dealing true damage. This damage is increased for each stack of Hemorrhage on the target. If Noxian Guillotine is a killing blow, its cooldown is refreshed for a brief duration.', 'DariusExecute.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0122/ability_0122_R1.webm', 26),
(146, 'ĐỘNG CƠ Z CỘNG HƯỞNG', 'Z-DRIVE RESONANCE', 'Mỗi đòn đánh hoặc kỹ năng sát thương thứ ba lên cùng mục tiêu sẽ khiến chúng chịu thêm sát thương phép, và tăng cho Ekko lượng lớn tốc độ di chuyển nếu đó là tướng.', 'Every third attack or damaging spell on the same target deals bonus magic damage, and grants Ekko a burst of speed if the target is a champion.', 'Ekko_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0245/ability_0245_P1.webm', 30),
(147, 'DÂY CÓT THỜI GIAN', 'TIMEWINDER', 'Ekko tung ra một quả lựu đạn, thứ sẽ phát nổ tạo ra một trường thời gian bóp méo khi trúng một tướng địch, làm chậm và gây sát thương lên mọi đơn vị bên trong. Sau đó, nó sẽ quay trở lại với Ekko, gây sát thương trên đường về.', 'Ekko throws a temporal grenade that expands into a time-distortion field upon hitting an enemy champion, slowing and damaging anyone caught inside. After a delay, the grenade rewinds back to Ekko, dealing damage on its return.', 'EkkoQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0245/ability_0245_Q1.webm', 30),
(148, 'LƯỠNG GIỚI ĐỒNG QUY', 'PARALLEL CONVERGENCE', 'Đòn đánh của Ekko gây thêm sát thương phép lên các kẻ địch thấp máu. Cậu có thể dùng Lưỡng Giới Đồng Quy để tạo ra một vùng thời không sau vài giây giúp làm chậm kẻ địch đứng trong đó. Nếu Ekko đi vào vùng thời không, cậu có lá chắn và làm choáng kẻ địch.', 'Ekko\'s basic attacks deal bonus magic damage to low health enemies. He can cast Parallel Convergence to split the timeline, creating an anomaly after a few seconds that slows enemies caught inside. If Ekko enters the anomaly, he gains shielding and stuns enemies by suspending them in time.', 'EkkoW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0245/ability_0245_W1.webm', 30),
(149, 'BIẾN CHUYỂN PHA', 'PHASE DIVE', 'Ekko lướt đi né tránh sát thương và kích hoạt Động Cơ Zero. Đòn tấn công tiếp theo của cậu sẽ gây thêm sát thương và bóp méo thời gian, dịch chuyển tức thời cậu đến mục tiêu.', 'Ekko rolls evasively while charging up his Z-Drive. His next attack deals bonus damage and warps reality, teleporting him to his target.', 'EkkoE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0245/ability_0245_E1.webm', 30),
(150, 'ĐỘT PHÁ THỜI GIAN', 'CHRONOBREAK', 'Ekko tự đảo lộn dòng thời gian của bản thân, khiến cậu trở nên không thể bị chọn làm mục tiêu và quay về thời điểm thích hợp trong quá khứ. Cậu ta biến trở lại địa điểm vài giây trước, và hồi máu một phần sát thương nhận vào trong thời gian đó. Kẻ địch đứng gần địa điểm này sẽ phải chịu rất nhiều sá', 'Ekko shatters his timeline, becoming untargetable and rewinding to a more favorable point in time. He returns to whenever he was a few seconds ago, and heals for a percentage of the damage received in that duration. Enemies near his arrival zone take massive damage.', 'EkkoR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0245/ability_0245_R1.webm', 30),
(161, 'PHÁP LỰC GIA TĂNG', 'RISING SPELL FORCE', 'Ezreal nhận thêm Tốc độ Đánh mỗi khi dùng kỹ năng trúng đích, cộng dồn tối đa 5 lần.', 'Ezreal gains increasing Attack Speed each time he successfully hits a spell, stacking up to 5 times.', 'Ezreal_RisingSpellForce.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0081/ability_0081_P1.webm', 33),
(162, 'PHÁT BẮN THẦN BÍ', 'MYSTIC SHOT', 'Ezreal bắn ra một luồng năng lượng giúp làm giảm hồi chiêu nếu trúng kẻ địch.', 'Ezreal fires a damaging bolt of energy which reduces all of his cooldowns slightly if it strikes an enemy unit.', 'EzrealQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0081/ability_0081_Q1.webm', 33),
(163, 'TINH HOA TUÔN CHẢY', 'ESSENCE FLUX', 'Ezreal bắn ra một quả cầu dính vào tướng hoặc mục tiêu đầu tiên trúng phải. Nếu Ezreal tấn công kẻ địch có quả cầu, nó sẽ phát nổ và gây sát thương.', 'Ezreal fires an orb that sticks to the first champion or objective hit. If Ezreal hits an enemy with the orb, it detonates and deals damage.', 'EzrealW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0081/ability_0081_W1.webm', 33),
(164, 'DỊCH CHUYỂN CỔ HỌC', 'ARCANE SHIFT', 'Ezreal bay đến một chỗ gần đó và bắn ra một luồng năng lượng vào kẻ địch gần nhất. Ưu tiên kẻ địch bị trúng Tinh Hoa Tuôn Chảy.', 'Ezreal teleports to a target nearby location and fires a homing bolt which strikes the nearest enemy unit. Prioritizes enemies stuck with Essence Flux.', 'EzrealE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0081/ability_0081_E1.webm', 33),
(165, 'CUNG ÁNH SÁNG', 'TRUESHOT BARRAGE', 'Ezreal giương cung rồi bắn ra một làn sóng năng lượng gây sát thương cực lớn lên mọi kẻ địch nó đi qua (sát thương giảm đi với lính và quái thường).', 'Ezreal winds up before firing a powerful barrage of energy that deals massive damage to each unit it passes through (damage is reduced for minions and non-epic monsters).', 'EzrealR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0081/ability_0081_R1.webm', 33),
(171, 'VŨ ĐIỆU KIẾM SƯ', 'DUELIST\'S DANCE', 'Fiora vạch ra một Điểm Yếu trên Tướng này. Nếu tấn công trúng Điểm Yếu, cô được Hồi Máu và nhận thêm Tốc Độ Di Chuyển.', 'Fiora has revealed a Vital on this Champion. If she hits the Vital, she restores Health and gains Move Speed.', 'Fiora_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0114/ability_0114_P1.webm', 35),
(172, 'LAO TỚI', 'LUNGE', 'Fiora lao tới trước và chém một kẻ địch gần kề, gây sát thương vật lí và các hiệu ứng trên đòn đánh.', 'Fiora lunges in a direction and stabs a nearby enemy, dealing physical damage and applying on-hit effects.', 'FioraQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0114/ability_0114_Q1.webm', 35),
(173, 'PHẢN ĐÒN', 'RIPOSTE', 'Fiora chặn lại toàn bộ sát thương và các hiệu ứng khống chế nhận vào trong một khoảng thời gian ngắn, rồi đâm về một hướng. Cú đâm này sẽ làm chậm tướng địch đầu tiên trúng chiêu, hoặc làm choáng chúng nếu như Fiora đã chặn được một hiệu ứng khống chế cứng bằng kĩ năng này.', 'Fiora parries all incoming damage and disables for a short time, then stabs in a direction. This stab slows the first enemy champion hit, or stuns them if Fiora blocked an immobilizing effect with this ability.', 'FioraW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0114/ability_0114_W1.webm', 35),
(174, 'NHẤT KIẾM NHỊ DỤNG', 'BLADEWORK', 'Fiora được tăng tốc độ đánh cho hai đòn tiếp theo. Đòn đánh đầu tiên sẽ làm chậm mục tiêu, còn đòn đánh thứ hai sẽ gây chí mạng.', 'Fiora has increased attack speed for the next two attacks. The first attack slows the target, and the second attack will critically strike.', 'FioraE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0114/ability_0114_E1.webm', 35),
(175, 'ĐẠI THỬ THÁCH', 'GRAND CHALLENGE', 'Fiora vạch ra cả bốn Điểm Yếu trên một tướng địch và nhận thêm Tốc Độ Di Chuyển khi ở gần chúng. Nếu Fiora phá vỡ cả bốn điểm yếu hoặc nếu kẻ địch bị hạ gục sau khi Fiora tấn công ít nhất một điểm yếu, Fiora và đồng minh trong khu vực sẽ được hồi máu trong vòng vài giây.', 'Fiora reveals all four Vitals on an enemy champion and gains Move Speed while near them. If Fiora hits all four Vitals or if the target dies after she has hit at least one, Fiora and her allies in the area are healed over the next few seconds.', 'FioraR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0114/ability_0114_R1.webm', 35),
(176, 'CHIẾN BINH LANH LỢI', 'NIMBLE FIGHTER', 'Fizz có thể đi xuyên vật thể và giảm một lượng sát thương gánh chịu cố định từ mọi nguồn.', 'Fizz can move through units and takes a flat amount of reduced damage from all sources', 'Fizz_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0105/ability_0105_P1.webm', 36),
(177, 'ĐÂM LAO', 'URCHIN STRIKE', 'Fizz lướt qua mục tiêu, gây sát thương phép và đặt hiệu ứng trên đòn đánh.', 'Fizz dashes through his target, dealing magic damage and applying on hit effects.', 'FizzQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0105/ability_0105_Q1.webm', 36),
(178, 'ĐINH BA HẢI THẠCH', 'SEASTONE TRIDENT', 'Đòn đánh của Fizz làm chảy máu kẻ địch, gây sát thương phép trong vài giây. Fizz cường hóa đòn đánh tiếp theo để gây thêm sát thương và gia tăng sức mạnh cho các đòn đánh sau đó trong một thời gian ngắn.', 'Fizz\'s attacks bleed his enemies, dealing magic damage over several seconds. Fizz can empower his next attack to deal bonus damage and empower his further attacks for a short time.', 'FizzW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0105/ability_0105_W1.webm', 36),
(179, 'TUNG TĂNG / NHẢY MÚA', 'PLAYFUL / TRICKSTER', 'Fizz nhảy lên rồi duyên dáng đáp xuống trên cây đinh ba, trở nên không thể bị chỉ định. Từ vị trí này, Fizz có thể dậm xuống mặt đất hoặc nhảy một lần nữa trước khi đập ra sau.', 'Fizz hops into the air, landing gracefully upon his spear and becoming untargetable. From this position, Fizz can either slam the ground or choose to jump again before smashing back down.', 'FizzE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0105/ability_0105_E1.webm', 36),
(180, 'TRIỆU HỒI THỦY QUÁI', 'CHUM THE WATERS', 'Fizz quăng cá vào hướng chỉ định, bám dính lấy bất kỳ tướng địch nào chạm phải và làm chậm mục tiêu. Sau chốc lát, thủy quái trồi lên, hất tung mục tiêu và đẩy kẻ địch xung quanh sang bên. Mọi kẻ địch trúng phải chịu sát thương phép và bị làm chậm.', 'Fizz tosses a fish in a direction that attaches to any champion that touches it, slowing the target. After a short delay, a shark erupts from the ground, knocking up the target and knocking any nearby enemies aside. All enemies hit are dealt magic damage and slowed.', 'FizzR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0105/ability_0105_R1.webm', 36),
(181, 'CÚ NỆN KHỔNG LỒ', 'COLOSSAL SMASH', 'Cứ sau vài giây, đòn đánh kế tiếp của Galio gây thêm sát thương phép trong một vùng.', 'Every few seconds, Galio\'s next basic attack deals bonus magic damage in an area.', 'Galio_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0003/ability_0003_P1.webm', 37),
(182, 'ĐÔI CÁNH CHIẾN TRẬN', 'WINDS OF WAR', 'Galio tung ra hai luồng gió hội tụ lại thành bão gây sát thương theo thời gian.', 'Galio fires two windblasts that converge into a large tornado that deals damage over time.', 'GalioQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0003/ability_0003_Q1.webm', 37),
(183, 'LÁ CHẮN DURAND', 'SHIELD OF DURAND', 'Galio di chuyển chậm lại và tiến vào thế thủ. Khi dừng vận sức, Galio sẽ khiêu khích và gây sát thương các kẻ địch gần đó.', 'Galio charges a defensive stance, moving slowly. Upon releasing the charge, Galio will taunt and damage nearby enemies.', 'GalioW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0003/ability_0003_W1.webm', 37),
(184, 'CÚ ĐẤM CÔNG LÝ', 'JUSTICE PUNCH', 'Galio lùi lại một bước rồi lao tới, hất tung tướng địch đầu tiên đụng phải.', 'Galio will briefly step back and charge, knocking up the first enemy champion he encounters.', 'GalioE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0003/ability_0003_E1.webm', 37),
(185, 'SIÊU HÙNG GIÁNG THẾ', 'HERO\'S ENTRANCE', 'Galio chọn vị trí một đồng minh làm điểm đáp, tạo cho tất cả đồng minh trong khu vực một lớp giáp phép. Sau chốc lát, Galio đáp xuống vị trí đó, hất tung các kẻ địch xung quanh.', 'Galio designates an ally\'s position as his landing spot, granting all allies in the area a magic shield. After a delay Galio smashes down location, knocking up nearby enemies.', 'GalioR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0003/ability_0003_R1.webm', 37),
(191, 'BỀN BỈ', 'PERSEVERANCE', 'Nếu Garen không bị dính sát thương hoặc kỹ năng của địch, anh hồi lại một phần máu tối đa mỗi giây.', 'If Garen has not recently been struck by damage or enemy abilities, he regenerates a percentage of his total health each second.', 'Garen_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0086/ability_0086_P1.webm', 39),
(192, 'ĐÒN QUYẾT ĐỊNH', 'DECISIVE STRIKE', 'Garen nhận lượng lớn Tốc Độ Di Chuyển, xóa bỏ tất cả các hiệu ứng làm chậm trên bản thân. Đòn đánh tiếp theo tấn công vào điểm yếu của kẻ địch, gây thêm sát thương và khiến chúng câm lặng.', 'Garen gains a burst of Move Speed, breaking free of all slows affecting him. His next attack strikes a vital area of his foe, dealing bonus damage and silencing them.', 'GarenQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0086/ability_0086_Q1.webm', 39),
(193, 'LÒNG CAN ĐẢM', 'COURAGE', 'Garen được tăng giáp và kháng phép khi hạ gục kẻ địch. Anh có thể kích hoạt kỹ năng để tạo lá chắn và kháng hiệu ứng trong chốc lát, sau đó là một lượng chống chịu nhỏ hơn nhưng lâu hơn.', 'Garen passively increases his armor and magic resist by killing enemies. He may also activate this ability to give him a shield and tenacity for a brief moment followed by a lesser amount of damage reduction for a longer duration.', 'GarenW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0086/ability_0086_W1.webm', 39);
INSERT INTO `skills` (`id`, `skill_name_vi`, `skill_name_en`, `skill_desc_vi`, `skill_desc_en`, `skill_image`, `skill_video_link`, `champion_id`) VALUES
(194, 'PHÁN QUYẾT', 'JUDGMENT', 'Garen xoay kiếm quanh mình, gây sát thương vật lý lên các kẻ địch gần đó.', 'Garen rapidly spins his sword around his body, dealing physical damage to nearby enemies.', 'GarenE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0086/ability_0086_E1.webm', 39),
(195, 'CÔNG LÝ DEMACIA', 'DEMACIAN JUSTICE', 'Garen triệu hồi sức mạnh Demacia để kết liễu tướng địch.', 'Garen calls upon the might of Demacia to attempt to execute an enemy champion.', 'GarenR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0086/ability_0086_R1.webm', 39),
(196, 'ĐỘT BIẾN GIEN', 'RAGE GENE', 'Gnar tăng cộng dồn Cuồng khi giao tranh. Ở tối đa Cuồng, kỹ năng kế tiếp sẽ biến Gnar thành Gnar Khổng Lồ, tăng mạnh khả năng sống sót và kích hoạt bộ kỹ năng hoàn toàn mới.', 'While in combat Gnar generates Rage. At maximum Rage his next ability will transform him into Mega Gnar, granting increased survivability and access to new spells.', 'Gnar_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0150/ability_0150_P1.webm', 40),
(197, 'NÉM BOOMERANG / NÉM ĐÁ', 'BOOMERANG THROW / BOULDER TOSS', 'Gnar quẳng đi chiếc boomerang thứ gây sát thương và làm chậm kẻ địch trúng phải trước khi quay về với hắn. Nếu hắn bắt lại được chiếc boomerang sẽ khiến thời gian hồi chiêu giảm đi. Gnar Khổng Lồ ném đi một tảng đá, thứ sẽ đập vào một đơn vị đầu tiên trúng phải, gây sát thương và làm chậm mọi thứ cạ', 'Gnar throws a boomerang that damages and slows enemies it hits before returning to him. If he catches the boomerang its cooldown is reduced. Mega Gnar instead throws a boulder that stops on the first unit hit, damaging and slowing everything nearby. It can then be picked up to reduce the cooldown.', 'GnarQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0150/ability_0150_Q1.webm', 40),
(198, 'QUÁ KHÍCH / ĐẬP PHÁ', 'HYPER / WALLOP', 'Các đòn đánh và kĩ năng của Gnar khiến hắn cảm thấy phấn khích, giúp gây thêm sát thương và tăng Tốc độ Di chuyển. Gnar Khổng Lồ không hề cảm thấy phấn khích, mà chỉ đơn giản là muốn đập phá mọi thứ phía trước hắn, làm choáng các nạn nhân trúng chiêu.', 'Gnar\'s attacks and spells hype him up, dealing bonus damage and granting him Move Speed. Mega Gnar is too enraged to be hyper and instead can rear up on his hind legs and smash down on the area in front of him, stunning enemies in an area.', 'GnarW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0150/ability_0150_W1.webm', 40),
(199, 'NHÚN NHẢY / NGHIỀN NÁT', 'HOP / CRUNCH', 'Gnar nhảy tới điểm đã chọn rồi nhún nhảy khỏi đầu bất kì đơn vị nào hắn đáp xuống, để tiếp tục di chuyển xa hơn. Gnar Khổng Lồ quá lớn để nhún nhảy nên phải đáp mạnh xuống mặt đất, gây sát thương xung quanh hắn.', 'Gnar leaps to a location and bounces off the head of any unit he lands on, traveling further. Mega Gnar is too large to bounce and instead lands with earth-shattering force, dealing damage in an area around him.', 'GnarE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0150/ability_0150_E1.webm', 40),
(200, 'GNAR!', 'GNAR!', 'Gnar Khổng Lồ ném mọi thứ quanh hắn theo hướng đã chọn, gây sát thương và làm chậm các nạn nhân trúng chiêu. Bất kì kẻ địch nào trúng phải vách tường đều bị làm choáng và chịu thêm sát thương.', 'Mega Gnar throws everything around him in a chosen direction, dealing damage and slowing them. Any enemy that hits a wall is stunned and takes bonus damage.', 'GnarR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0150/ability_0150_R1.webm', 40),
(201, 'GIỜ KHUYẾN MÃI', 'HAPPY HOUR', 'Sau vài giây, Gragas lại được hồi máu khi sử dụng kỹ năng.', 'Gragas periodically heals upon using a skill.', 'GragasPassiveHeal.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0079/ability_0079_P1.webm', 41),
(202, 'LĂN THÙNG RƯỢU', 'BARREL ROLL', 'Gragas lăn thùng rượu đến một vị trí, có thể kích hoạt để phát nổ hoặc tự phát nổ sau 4 giây. Sức mạnh của vụ nổ sẽ tăng dần theo thời gian. Những kẻ địch trúng vụ nổ sẽ bị làm chậm Tốc độ Di chuyển.', 'Gragas rolls his cask to a location, which can be activated to explode or will explode on its own after 4 seconds. The potency of the explosion increases over time. Enemies struck by the blast have their Move Speed slowed.', 'GragasQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0079/ability_0079_Q1.webm', 41),
(203, 'SAY QUÁ HÓA CUỒNG', 'DRUNKEN RAGE', 'Gragas nốc rượu trong bình trong 1 giây. Hậu quả là hắn trở nên say xỉn khiến cú đánh kế tiếp gây thêm sát thương phép cho tất cả kẻ địch xung quanh và được giảm sát thương gánh chịu.', 'Gragas guzzles down brew from his cask for 1 second. After finishing, he becomes drunkenly empowered, dealing magic damage to all nearby enemies on his next basic attack and reducing damage received.', 'GragasW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0079/ability_0079_W1.webm', 41),
(204, 'LĂN BỤNG', 'BODY SLAM', 'Gragas lao đến vị trí đã chọn và va chạm với kẻ địch đầu tiên hắn đụng phải, gây sát thương lên tất cả kẻ địch xung quanh và làm choáng chúng.', 'Gragas charges to a location and collides with the first enemy unit he comes across, dealing damage to all nearby enemy units and stunning them.', 'GragasE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0079/ability_0079_E1.webm', 41),
(205, 'THÙNG RƯỢU NỔ', 'EXPLOSIVE CASK', 'Gragas quẳng bình rượu đến vị trí mục tiêu, phát nổ khi tiếp xúc, gây sát thương và hất tung kẻ địch trong vụ nổ.', 'Gragas hurls his cask to a location, dealing damage and knocking back enemies caught in the blast radius.', 'GragasR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0079/ability_0079_R1.webm', 41),
(206, 'VẬN MỆNH THAY ĐỔI', 'NEW DESTINY', 'Khẩu shotgun của Graves có một vài điểm đặc biệt. Graves phải nạp lại khi bắn hết đạn. Phát bắn sẽ bắn ra 4 viên đạn và không thể xuyên qua mục tiêu. Những mục tiêu không phải tướng khi dính nhiều đạn cùng lúc sẽ bị đẩy lùi.', 'Graves\' shotgun has some unique properties. He must reload when he runs out of ammo. Attacks fire 4 bullets, which cannot pass through units. Non-champions struck by multiple bullets are knocked back.', 'GravesTrueGrit.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0104/ability_0104_P1.webm', 42),
(207, 'ĐẠN XUYÊN MỤC TIÊU', 'END OF THE LINE', 'Graves bắn ra một viên đạn sẽ nổ sau 2 giây hoặc sau 0.2 giây khi va phải địa hình.', 'Graves fires an explosive shell that detonates after 2 seconds, or 0.2 seconds if it strikes terrain.', 'GravesQLineSpell.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0104/ability_0104_Q1.webm', 42),
(208, 'BOM MÙ', 'SMOKE SCREEN', 'Graves bắn một bọc khói vào khu vực chỉ định, tạo ra một đám mù làm giảm tầm nhìn. Kẻ địch trúng bọc khói chịu sát thương phép và bị giảm Tốc độ Di chuyển trong thoáng chốc.', 'Graves fires a smoke canister at the target area creating a cloud of smoke that reduces sight range. Enemies caught in the initial impact are dealt magic damage and have their Move Speed reduced briefly.', 'GravesSmokeGrenade.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0104/ability_0104_W1.webm', 42),
(209, 'RÚT SÚNG NHANH', 'QUICKDRAW', 'Graves lướt tới trước, nhận thêm Giáp trong vài giây. Nếu lướt về phía tướng địch, Graves được hai điểm Lì Đòn. Đòn đánh thường trúng kẻ địch giảm hồi chiêu kỹ năng này và làm mới thời gian tăng chống chịu.', 'Graves dashes forward gaining an Armor boost for several seconds. If Graves dashes towards an enemy champion, gain two stacks of True Grit instead. Hitting enemies with basic attacks lowers the cooldown of this skill and refreshes the resistance boost.', 'GravesMove.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0104/ability_0104_E1.webm', 42),
(210, 'ĐẠN NỔ THẦN CÔNG', 'COLLATERAL DAMAGE', 'Graves bắn một viên đạn cực mạnh gây sát thương khủng lên mục tiêu đầu tiên trúng phải. Sau khi trúng một tướng hoặc bay hết tầm xa, viên đạn phát nổ gây sát thương theo hình nón.', 'Graves fires an explosive shell dealing heavy damage to the first champion it hits. After hitting a champion or reaching the end of its range, the shell explodes dealing damage in a cone.', 'GravesChargeShot.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0104/ability_0104_R1.webm', 42),
(211, 'NGÀN NHÁT CẮT', 'A THOUSAND CUTS', 'Đòn đánh của Gwen gây sát thương phép cộng thêm dựa theo số máu của mục tiêu. Cô được hồi lại một phần lượng sát thương hiệu ứng này gây ra lên tướng.', 'Gwen\'s attacks deal bonus magic damage based on the targets health. She heals for a portion of the damage dealt to champions by this effect.', 'Gwen_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0887/ability_0887_P1.webm', 43),
(212, 'XOẸT XOẸT!', 'SNIP SNIP!', 'Gwen nhấp kéo theo hình nón tối đa 6 lần, gây sát thương phép. Gwen gây sát thương chuẩn lên các đơn vị ở trung tâm và áp dụng sát thương nội tại lên chúng trong mỗi nhát cắt.', 'Gwen snips her scissors in a cone up to 6 times dealing magic damage. Gwen deals true damage to units in the center and applies her passive to them on each snip.', 'GwenQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0887/ability_0887_Q1.webm', 43),
(213, 'SƯƠNG LAM BẤT BẠI', 'HALLOWED MIST', 'Gwen triệu hồi màn sương bảo vệ bản thân khỏi những kẻ địch bên ngoài. Cô chỉ có thể bị chỉ định bởi những kẻ địch bước vào trong màn sương.', 'Gwen summons mist that protects her from enemies outside of it. She can only be targeted by enemies who enter the mist.', 'GwenW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0887/ability_0887_W1.webm', 43),
(214, 'XÉN XÉN', 'SKIP \'N SLASH', 'Gwen lướt đi một quãng ngắn, sau đó tăng tốc độ đánh, tầm đánh, và sát thương phép mỗi Đòn Đánh trong vài giây. Nếu tấn công kẻ địch trong thời gian này, hồi chiêu của Kỹ năng sẽ được trả lại một phần.', 'Gwen dashes a short distance then gains Attack Speed, attack range, and magic damage On-Hit for a few seconds. If she hits an enemy during that time, this Ability\'s cooldown is partially refunded.', 'GwenE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0887/ability_0887_E1.webm', 43),
(215, 'XE CHỈ LUỒN KIM', 'NEEDLEWORK', 'Gwen phóng một cây kim làm chậm kẻ địch, gây sát thương phép và áp dụng Ngàn Nhát Cắt lên tướng địch trúng chiêu. Kỹ năng này có thể được sử dụng hai lần nữa, với mỗi lần sau sẽ phóng ra nhiều kim và gây nhiều sát thương hơn. Gwen buộc phải tấn công kẻ địch giữa mỗi lần tung chiêu để mở khóa lần sử ', 'Gwen hurls a needle that slows enemies hit, deals magic damage, and applies A Thousand Cuts to champions hit. This ability can be cast up to two more times, with each cast throwing additional needles and dealing more damage. Gwen must hit an enemy between each cast to unlock the next one.', 'GwenR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0887/ability_0887_R1.webm', 43),
(221, 'CƠ GIỚI MA PHÁP', 'HEXTECH AFFINITY', 'Nhận thêm Tốc độ Di chuyển khi ở gần trụ đồng minh và Ụ Súng được Heimerdinger thiết lập.', 'Gain Move Speed while near allied towers and turrets deployed by Heimerdinger.', 'Heimerdinger_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0074/ability_0074_P1.webm', 45),
(222, 'Ụ SÚNG TÂN TIẾN H-28G', 'H-28 G EVOLUTION TURRET', 'Heimerdinger đặt một ụ súng bắn đại bác liên thanh kèm tia chớp xuyên thấu (ụ súng gây nửa sát thương lên trụ).', 'Heimerdinger lays down a rapid-fire cannon turret equipped with a secondary pass-through beam attack (turrets deal half damage to towers).', 'HeimerdingerQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0074/ability_0074_Q1.webm', 45),
(223, 'TÊN LỬA HEXTECH CỠ NHỎ', 'HEXTECH MICRO-ROCKETS', 'Heimerdinger bắn tên lửa tầm xa hội tụ ở chỗ trỏ chuột.', 'Heimerdinger fires long-range rockets that converge on his cursor.', 'HeimerdingerW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0074/ability_0074_W1.webm', 45),
(224, 'LỰU ĐẠN BÃO ĐIỆN TỬ CH-2', 'CH-2 ELECTRON STORM GRENADE', 'Heimerdinger ném lựu đạn vào một chỗ, gây sát thương lên kẻ địch đồng thời làm choáng những kẻ đứng ở tâm và làm chậm những kẻ đứng ngoài rìa.', 'Heimerdinger lobs a grenade at a location, dealing damage to enemy units, as well as stunning anyone directly hit and slowing surrounding units.', 'HeimerdingerE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0074/ability_0074_E1.webm', 45),
(225, 'NÂNG CẤP!!!', 'UPGRADE!!!', 'Heimerdinger nảy ra sáng kiến cực ngầu, khiến kĩ năng kế tiếp của hắn được gia cường hiệu lực.', 'Heimerdinger invents an upgrade, causing his next spell to have increased effects.', 'HeimerdingerR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0074/ability_0074_R1.webm', 45),
(226, 'CHỮ KÝ CỦA HỌA SĨ', 'SIGNATURE OF THE VISIONARY', 'Hwei ký họa chữ ký của mình lên những tướng địch nhận sát thương từ kỹ năng của hắn. Chữ ký sẽ hoàn thành và hiện lên dưới chân kẻ địch nếu chúng dính một kỹ năng gây sát thương thứ hai. Chữ ký phát nổ sau một khoảng chờ ngắn, gây sát thương phép lên tất cả kẻ địch gần đó.', 'Hwei primes enemy champions he damages with his abilities for his signature finishing touch. Hitting an enemy with a second damaging spell completes his signature, leaving it beneath them. The signature detonates after a short delay, dealing magic damage to all enemies in range.', 'HweiPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0910/ability_0910_P1.webm', 46),
(227, 'TRƯỜNG PHÁI: THẢM HỌA', 'SUBJECT: DISASTER', 'Hwei hình dung về những thảm họa, cho phép hắn vẽ ra một cảnh tượng kinh hoàng. Kỹ năng này thay thế các kỹ năng của Hwei bằng những kỹ năng gây sát thương: Lửa Tàn Phá, Sấm Sét Hủy Diệt và Vết Nứt Bỏng Cháy.', 'Hwei envisions a series of disasters, allowing him to paint a devastating blow. This ability replaces Hwei\'s abilities with damaging abilities: Devastating Fire, Severing Bolt, and Molten Fissure.', 'HweiQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0910/ability_0910_Q1.webm', 46),
(228, 'TRƯỜNG PHÁI: BÌNH YÊN', 'SUBJECT: SERENITY', 'Hwei hình dung về một khung cảnh bình yên, cho phép hắn vẽ ra những bức họa khích lệ tinh thần. Kỹ năng này thay thế các kỹ năng của Hwei bằng những kỹ năng hỗ trợ: Nước Chảy Thoáng Qua, Ánh Nước Phản Chiếu và Tia Sáng Khuấy Động.', 'Hwei envisions a series of serenities, allowing him to paint invigorating settings. This ability replaces Hwei\'s abilities with utility abilities: Fleeting Current, Pool of Reflection, and Stirring Lights.', 'HweiW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0910/ability_0910_W1.webm', 46),
(229, 'TRƯỜNG PHÁI: THỐNG KHỔ', 'SUBJECT: TORMENT', 'Hwei hình dung về sự dằn vặt và khổ đau, cho phép hắn vẽ ra những khuôn mặt có khả năng khống chế kẻ địch. Kỹ năng này thay thế các kỹ năng với hiệu ứng khống chế của Hwei: Khuôn Mặt U Ám, Địa Ngục Nhãn và Hàm Nhai Kinh Hãi.', 'Hwei envisions a series of torments, allowing him to paint controlling visages. This ability replaces Hwei\'s abilities with crowd control abilities: Grim Visage, Gaze of the Abyss, and Crushing Maw.', 'HweiE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0910/ability_0910_E1.webm', 46),
(230, 'VÒNG XOÁY TUYỆT VỌNG', 'SPIRALING DESPAIR', 'Hwei vẽ ra một khung cảnh tuyệt vọng cùng cực. Tướng địch đầu tiên trúng chiêu sẽ trở thành trung tâm của một bức họa mở rộng dần, gây sát thương và làm chậm những kẻ địch gần bên. Khung cảnh sẽ phát nổ sau khi đạt kích thước tối đa hoặc nếu tướng địch hy sinh.', 'Hwei paints a vision of pure despair. The first enemy champion struck becomes the center of an expanding painting that slows and damages nearby enemies. The vision explodes after reaching its maximum size or when the champion dies.', 'HweiR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0910/ability_0910_R1.webm', 46),
(231, 'TIÊN TRI THƯỢNG THẦN', 'PROPHET OF AN ELDER GOD', 'Illaoi và Tế Phẩm bà tạo ra sẽ sinh Xúc Tu trên địa hình gần đó. Xúc Tu đập vào linh hồn, Tế Phẩm, và mục tiêu Lời Răn Khắc Nghiệt của Illaoi. Tế Phẩm gây sát thương vật lý lên kẻ địch trúng phải, và hồi máu cho Illaoi nếu nó đập trúng một tướng.', 'Illaoi and the Vessels she creates spawn Tentacles on nearby impassible terrain. Tentacles swing at spirits, Vessels, and victims of Illaoi\'s Harsh lesson. Tentacles deal physical damage to enemies hit, and will heal Illaoi if they damage a champion.', 'Illaoi_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0420/ability_0420_P1.webm', 47),
(232, 'XÚC TU TÀN PHÁ', 'TENTACLE SMASH', 'Tăng sát thương từ Xúc Tu. Khi kích hoạt, Illaoi đập xuống một Xúc Tu gây sát thương vật lý.', 'Increases the damage dealt by Tentacles. When activated, Illaoi smashes down a Tentacle that deals physical damage.', 'IllaoiQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0420/ability_0420_Q1.webm', 47),
(233, 'LỜI RĂN KHẮC NGHIỆT', 'HARSH LESSON', 'Illaoi lao tới chỗ mục tiêu, gây sát thương vật lý và khiến các Xúc Tu gần đó quất vào mục tiêu.', 'Illaoi leaps to her target, dealing physical damage and causing nearby Tentacles to also swing at the target.', 'IllaoiW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0420/ability_0420_W1.webm', 47),
(234, 'THỬ THÁCH LINH HỒN', 'TEST OF SPIRIT', 'Illaoi rút Linh Hồn ra khỏi thể xác kẻ địch, buộc nó phải đứng trước bà ta. Một phần sát thương Linh Hồn phải chịu cũng sẽ được gây ra cho thể xác. Nếu bị tiêu diệt hoặc nếu mục tiêu đi quá xa khỏi Linh Hồn, hắn sẽ trở thành Tế Phẩm và bắt đầu tạo ra Xúc Tu.', 'Illaoi rips the spirit from a foe\'s body, forcing it to stand before her. Spirits echo a percentage of the damage they take to the original target. If killed, or if the target gets too far from the spirit, the target will become a Vessel and begin spawning Tentacles.', 'IllaoiE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0420/ability_0420_E1.webm', 47),
(235, 'BƯỚC NHẢY NIỀM TIN', 'LEAP OF FAITH', 'Illaoi đập tượng thần xuống đất, gây sát thương vật lý lên kẻ địch xung quanh. Một Xúc Tu được tạo ra với mỗi Tướng địch trúng phải.', 'Illaoi smashes her idol into the ground, dealing physical damage to nearby enemies. A Tentacle spawns for each enemy champion hit.', 'IllaoiR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0420/ability_0420_R1.webm', 47),
(236, 'Ý CHÍ IONIA', 'IONIAN FERVOR', 'Khi Irelia dùng kỹ năng trúng kẻ địch, cô nhận thêm Tốc độ Đánh cộng dồn. Ở số cộng dồn tối đa, cô gây thêm sát thương trên mỗi đòn đánh.', 'When Irelia strikes enemies with spells she gains stacking bonus Attack Speed. At maximum stacks she also gains bonus damage on hit.', 'Irelia_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0039/ability_0039_P1.webm', 48),
(237, 'ĐÂM KIẾM', 'BLADESURGE', 'Irelia lướt tới tấn công mục tiêu và hồi máu cho bản thân. Nếu mục tiêu bị đánh dấu hoặc chết bởi Đâm Kiếm, nó hồi chiêu lại ngay lập tức.', 'Irelia dashes forward to strike her target, healing herself. If the target is Marked or dies to Bladesurge, its cooldown refreshes.', 'IreliaQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0039/ability_0039_Q1.webm', 48),
(238, 'VŨ ĐIỆU THÁCH THỨC', 'DEFIANT DANCE', 'Irelia vận sức gây sát thương cho kẻ địch dựa theo thời gian vận. Cô được giảm sát thương vật lý gánh chịu trong lúc vận.', 'Irelia charges a strike that deals more damage as she charges. She takes reduced physical damage during the charge.', 'IreliaW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0039/ability_0039_W1.webm', 48),
(239, 'BƯỚC NHẢY HOÀN VŨ', 'FLAWLESS DUET', 'Irelia tung ra hai lưỡi kiếm bay về phía nhau. Kẻ địch đứng trên đường thẳng đó chịu sát thương, bị làm choáng và đánh dấu.', 'Irelia sends out two blades which converge on each other. Enemies caught in between are damaged, stunned and Marked.', 'IreliaE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0039/ability_0039_E1.webm', 48),
(240, 'THANH KIẾM TIÊN PHONG', 'VANGUARD\'S EDGE', 'Irelia phóng ra vô số lưỡi kiếm lan tỏa khi trúng phải một tướng địch. Đối phương dính chiêu sẽ nhận sát thương và bị Đánh Dấu. Sau đó, những lưỡi kiếm sẽ tạo thành một bức tường, gây sát thương và làm chậm đối phương bước qua nó.', 'Irelia fires a massive number of blades that explode outward upon hitting an enemy champion. Enemies hit by the blades are damaged and Marked. Afterwards the blades form a wall that will damage and slow enemies that walk through it.', 'IreliaR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0039/ability_0039_R1.webm', 48),
(241, 'NGƯỜI BẠN CỦA RỪNG GIÀ', 'FRIEND OF THE FOREST', 'Ivern không thể tấn công hoặc bị tấn công bởi quái thường. Ivern có thể tạo ra lùm cây ma thuật lớn dần theo thời gian ở bãi quái rừng. Khi lùm cây lớn hẳn, Ivern có thể trả tự do cho quái để nhận vàng và kinh nghiệm.', 'Ivern cannot attack or be attacked by non-epic monsters. Ivern can create magical groves on jungle camps which grow over time. When the grove is fully grown, Ivern may free the monsters to receive gold and experience.', 'IvernP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0427/ability_0427_P1.webm', 49),
(242, 'RỄ RẮC RỐI', 'ROOTCALLER', 'Ivern tạo ra một rễ cây gây sát thương và trói chân mục tiêu địch trúng phải. Đồng minh của Ivern có thể lướt tới mục tiêu bị trói.', 'Ivern conjures a vine, dealing damage and rooting enemy targets hit. Ivern\'s allies can dash to the rooted target.', 'IvernQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0427/ability_0427_Q1.webm', 49),
(243, 'KIẾN TẠO BỤI', 'BRUSHMAKER', 'Khi ở trong bụi, đòn đánh của Ivern và đồng minh gần bên sẽ gây sát thương phép cộng thêm. Ivern có thể kích hoạt kỹ năng này để tạo một bụi cỏ.', 'In brush, Ivern and his nearby allies attacks deal bonus magic damage. Ivern can activate this ability to create a patch of brush.', 'IvernW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0427/ability_0427_W1.webm', 49),
(244, 'HẠT HƯ HỎNG', 'TRIGGERSEED', 'Ivern đặt lá chắn lên một đồng minh, lá chắn sẽ phát nổ sau chốc lát để làm chậm và gây sát thương lên kẻ địch. Lá chắn sẽ được hồi lại nếu không nổ trúng tướng địch.', 'Ivern places a shield on an ally which explodes after a short duration slowing and damaging enemies. The shield refreshes if it hits no enemies.', 'IvernE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0427/ability_0427_E1.webm', 49),
(245, 'DAISY!', 'DAISY!', 'Ivern triệu hồi người bạn hộ pháp Daisy để hỗ trợ chiến đấu. Tái kích hoạt để điều khiển Daisy tấn công hoặc di chuyển.', 'Ivern summons his Sentinel friend Daisy to fight with him. Re-cast command Daisy to attack or move.', 'IvernR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0427/ability_0427_R1.webm', 49),
(251, 'THƯƠNG THUẬT', 'MARTIAL CADENCE', 'Đòn đánh đầu tiên của Jarvan lên 1 kẻ địch gây thêm sát thương vật lý dựa theo máu hiện tại của mục tiêu. Hiệu ứng không thể xảy ra trên cùng 1 kẻ địch trong vài giây.', 'Jarvan\'s first basic attack on an enemy deals bonus physical damage based on their current Health. This effect cannot occur again on the same enemy for a few seconds.', 'JarvanIVP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0059/ability_0059_P1.webm', 51),
(252, 'GIÁNG LONG KÍCH', 'DRAGON STRIKE', 'Jarvan IV đâm thương về phía trước, gây sát thương vật lí và giảm giáp của tất cả kẻ địch trúng phải. Ngoài ra, kỹ năng này sẽ kéo Jarvan IV đến điểm rơi của Hoàng Kì Demacia, hất tung những kẻ địch trúng chiêu lên không.', 'Jarvan IV extends his lance, dealing physical damage and lowering the Armor of enemies in its path. Additionally, this will pull Jarvan to his Demacian Standard, knocking up enemies in his path.', 'JarvanIVDragonStrike.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0059/ability_0059_Q1.webm', 51),
(253, 'HOÀNG KIM GIÁP', 'GOLDEN AEGIS', 'Jarvan IV viện cầu sức mạnh từ những vì vua cổ đại của Demacia giúp bảo vệ anh khỏi sát thương và làm chậm kẻ địch xung quanh.', 'Jarvan IV calls upon the ancient kings of Demacia to shield him from harm and slow surrounding enemies.', 'JarvanIVGoldenAegis.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0059/ability_0059_W1.webm', 51),
(254, 'HOÀNG KỲ DEMACIA', 'DEMACIAN STANDARD', 'Jarvan IV mang theo niềm tự hào của Demacia giúp tăng một lượng Tốc độ Đánh nội tại. Kích hoạt Hoàng Kì Demacia cho phép Jarvan IV phóng đi lá cờ của Demacia gây sát thương tại điểm tiếp xúc, đồng thời tăng cho tướng đồng minh cạnh bên Tốc độ Đánh.', 'Jarvan IV carries the pride of Demacia, passively granting him bonus Attack Speed. Activating Demacian Standard allows Jarvan IV to place a Demacian flag that deals magic damage on impact and grants Attack Speed to nearby allied champions.', 'JarvanIVDemacianStandard.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0059/ability_0059_E1.webm', 51),
(255, 'ĐẠI ĐỊA CHẤN', 'CATACLYSM', 'Jarvan IV anh dũng lao vào một mục tiêu mạnh đến mức địa hình xung quanh anh bị biến thành một vùng đấu trường. Những đơn vị đối phương cạnh bên cũng nhận sát thương tại cùng thời điểm.', 'Jarvan IV heroically leaps into battle at a target with such force that he terraforms the surrounding area to create an arena around them. Nearby enemies are damaged at the moment of impact.', 'JarvanIVCataclysm.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0059/ability_0059_R1.webm', 51),
(256, 'KHÔNG KHOAN NHƯỢNG', 'RELENTLESS ASSAULT', 'Các đòn đánh cơ bản liên tục của Jax gia tăng cho hắn tốc độ đánh.', 'Jax\'s consecutive basic attacks continuously increase his Attack Speed.', 'Armsmaster_MasterOfArms.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0024/ability_0024_P1.webm', 52),
(257, 'NHẢY VÀ NỆN', 'LEAP STRIKE', 'Jax nhảy bổ vào một mục tiêu. Nếu đó là kẻ địch, hắn tấn công nạn nhân bằng vũ khí của mình.', 'Jax leaps toward a unit. If they are an enemy, he strikes them with his weapon.', 'JaxQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0024/ability_0024_Q1.webm', 52),
(258, 'VẬN SỨC', 'EMPOWER', 'Jax tích tụ năng lượng vào vũ khí, khiến đòn đánh kế gây thêm sát thương.', 'Jax charges his weapon with energy, causing his next attack to deal additional damage.', 'JaxW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0024/ability_0024_W1.webm', 52),
(259, 'PHẢN CÔNG', 'COUNTER STRIKE', 'Sự thiện chiến của Jax giúp hắn có thể tránh khỏi tất cả các đòn đánh trong một thời gian ngắn rồi nhanh chóng phản công gây choáng lên kẻ địch xung quanh.', 'Jax\'s combat prowess allows him to dodge all incoming attacks for a short duration and then quickly counterattack, stunning all surrounding enemies.', 'JaxE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0024/ability_0024_E1.webm', 52),
(260, 'BẬC THẦY VŨ KHÍ', 'GRANDMASTER-AT-ARMS', 'Mỗi đòn đánh thứ ba liên tiếp gây thêm Sát Thương Phép. Ngoài ra, Jax có thể kích hoạt kĩ năng này để gây sát thương ra xung quanh và phóng thích sức mạnh tiềm tàng, giúp gia tăng Giáp và Kháng Phép trong một thời gian ngắn.', 'Every third consecutive attack deals additional Magic Damage. Additionally, Jax can activate this ability to deal damage around himself and strengthen his resolve, increasing his Armor and Magic Resist for a short duration.', 'JaxR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0024/ability_0024_R1.webm', 52),
(261, 'TỤ ĐIỆN HEXTECH', 'HEXTECH CAPACITOR', 'Khi Jayce đổi vũ khí, anh nhận thêm Tốc độ Di chuyển trong một vài giây.', 'When Jayce swaps weapons he gains Move Speed for a short duration.', 'Jayce_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0126/ability_0126_P1.webm', 53),
(262, 'CHỈ THIÊN! / CẦU SẤM', 'TO THE SKIES! / SHOCK BLAST', 'Dạng Búa: Bay tới một kẻ địch gây sát thương vật lí và làm chậm chúng. Dạng Pháo: Bắn ra một quả cầu điện tích, thứ sẽ phát nổ khi chạm phải kẻ địch (hoặc hết tầm bay) gây sát thương vật lí lên tất cả những kẻ địch trong vùng ảnh hưởng của vụ nổ.', 'Hammer Stance: Leaps to an enemy dealing physical damage and slowing enemies. Cannon Stance: Fires an orb of electricity that detonates upon hitting an enemy (or reaching the end of its path) dealing physical damage to all enemies hit.', 'JayceToTheSkies.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0126/ability_0126_Q1.webm', 53),
(263, 'SẤM CHỚP RỀN VANG / TÍCH TỤ', 'LIGHTNING FIELD / HYPER CHARGE', 'Dạng Búa: Nội tại: Hồi năng lượng theo mỗi đòn đánh. Kích hoạt: Tạo ra một vùng sấm chớp rền vang gây sát thương lên những kẻ địch xung quanh trong vài giây. Dạng Pháo: Nhận một cú sốc năng lượng giúp tăng Tốc độ Đánh lên mức tối đa trong một vài đòn đánh kế tiếp.', 'Hammer Stance: Passive: Restores Mana per strike. Active: Creates a field of lightning damaging nearby enemies for several seconds. Cannon Stance: Gains a burst of energy, increasing Attack Speed to maximum for several attacks.', 'JayceStaticField.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0126/ability_0126_W1.webm', 53),
(264, 'LÔI PHẠT / CỔNG TĂNG TỐC', 'THUNDERING BLOW / ACCELERATION GATE', 'Dạng Búa: Gây sát thương phép lên một kẻ địch và hất nạn nhân lùi lại một cự li ngắn. Dạng Pháo: Tạo ra một chiếc Cổng Tăng Tốc giúp gia tăng Tốc độ Di chuyển cho những đồng minh bước qua đấy. Nếu Cầu Sấm được bắn xuyên qua cánh cổng này thì tốc độ bay, tầm bay và sát thương nó gây ra đều được gia t', 'Hammer Stance: Deals magic damage to an enemy and knocks them back a short distance. Cannon Stance: Deploys an Acceleration Gate increasing the Move Speed of all allied champions who pass through it. If Shock Blast is fired through the gate the missile speed, range, and damage will increase.', 'JayceThunderingBlow.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0126/ability_0126_E1.webm', 53),
(265, 'PHÁO THỦY NGÂN / BÚA THỦY NGÂN', 'MERCURY CANNON / MERCURY HAMMER', 'Dạng Búa: Chuyển sang Búa giúp nhận kĩ năng mới và được tăng tầm đánh. Đòn đánh đầu tiên ở dạng này sẽ giảm Giáp, Kháng Phép của nạn nhân. Dạng Pháo: Chuyển sang Pháo giúp nhận kĩ năng mới và tăng Giáp, Kháng Phép. Đòn đánh đầu tiên ở dạng này sẽ gây thêm sát thương phép.', 'Hammer Stance: Transforms the Mercury Hammer into the Mercury Cannon gaining new abilities and increased range. The first attack in this form reduces the target\'s Armor and Magic Resist. Cannon Stance: Transforms the Mercury Cannon into the Mercury Hammer gaining new abilities and increasing Armor a', 'JayceStanceHtG.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0126/ability_0126_R1.webm', 53),
(276, 'BẢN NĂNG CHIẾN BINH', 'DAUNTLESS INSTINCT', 'Kỹ Năng của K\'Sante đánh dấu kẻ địch, khiến chúng nhận thêm sát thương từ Đòn Đánh tiếp theo của anh. Khi đang Khô Máu, K\'Sante gây thêm sát thương trên tất cả Đòn Đánh và Kỹ Năng.', 'K\'Sante\'s Abilities mark targets to take more damage on his next Attack. When All Out, K\'Sante deals more damage with all Attacks and Abilities.', 'Icons_KSante_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0897/ability_0897_P1.webm', 56),
(277, 'THẦN KHÍ NTOFO', 'NTOFO STRIKES', 'K\'Sante dậm vũ khí, gây sát thương và làm chậm kẻ địch trong một khoảng ngắn. Nếu trúng mục tiêu, nhận 1 cộng dồn Thần Khí Ntofo. Ở 2 cộng dồn, K\'Sante bắn ra một sóng năng lượng kéo kẻ địch lại gần. Khi đang Khô Máu, hồi chiêu được giảm bớt.', 'K\'Sante strikes, damaging and slowing enemies in a short line. On hit, grants a stack of Ntofo Strikes. At 2 stacks, K\'Sante fires a shockwave that pulls enemies in. When All Out, cooldown is reduced.', 'KSanteQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0897/ability_0897_Q1.webm', 56),
(278, 'MỞ ĐƯỜNG', 'PATH MAKER', 'K\'Sante thủ thế, giảm sát thương nhận vào và sau đó lướt tới, đẩy lùi và làm choáng kẻ địch. Khi đang Khô Máu, gây thêm sát thương nhưng không còn đẩy lùi hoặc làm choáng.', 'K\'Sante charges up, taking reduced damage, then dashes, knocking back and stunning enemies. When All Out, deals increased damage and no longer knocks back or stuns.', 'KSanteW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0897/ability_0897_W1.webm', 56),
(279, 'BƯỚC CHÂN DŨNG MÃNH', 'FOOTWORK', 'K\'Sante lướt đi và nhận một Lá Chắn. Nếu mục tiêu là đồng minh, K\'Sante lướt tới đồng minh với tầm đánh xa gia tăng và cả hai cùng nhận Lá Chắn. Khi đang Khô Máu, hồi chiêu được giảm bớt và tốc độ được gia tăng.', 'K\'Sante dashes and gains a shield. If targeting an ally, K\'Sante dashes to the ally with increased range and both gain a shield. When All Out, cooldown is reduced and speed is increased.', 'KSanteE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0897/ability_0897_E1.webm', 56),
(280, 'CHƠI KHÔ MÁU', 'ALL OUT', 'K\'Sante đẩy lùi một kẻ địch, hất chúng xuyên qua tường trên đường bay. Sau đó K\'Sante tiến vào trạng thái Khô Máu và lướt đi theo chúng, được tăng sát thương, hồi máu và mở khóa kỹ năng mới tuy nhiên bị giảm sức phòng thủ.', 'K\'Sante knocks an enemy back, launching them through any wall in their path. Then, K\'Sante goes All Out and dashes after them, gaining increased damage, healing, and transformed abilities at the cost of reduced defenses.', 'KSanteR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0897/ability_0897_R1.webm', 56),
(291, 'TỤ HỎA', 'GATHERING FIRE', 'Gây sát thương bằng kỹ năng sẽ giảm hồi chiêu của Kinh Mantra.', 'Karma\'s damaging abilities will reduce the cooldown of Mantra.', 'Karma_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0043/ability_0043_P1.webm', 59),
(292, 'NỘI HỎA', 'INNER FLAME', 'Karma phóng ra một quả câu năng lượng tâm linh, thứ sẽ phát nổ và sát thương khi trúng phải một đơn vị kẻ địch. Mantra Cường Hóa: Sau vụ nổ, Kinh Mantra gia cường sức mạnh hủy diệt của kĩ năng Nội Hỏa, giúp tạo ra một vụ chấn động gây thêm sát thương sau thoáng chốc.', 'Karma sends forth a ball of spirit energy that explodes and deals damage upon hitting an enemy unit. Mantra Bonus: In addition to the explosion, Mantra increases the destructive power of her Inner Flame, creating a cataclysm which deals damage after a short delay.', 'KarmaQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0043/ability_0043_Q1.webm', 59),
(293, 'CHUYÊN TÂM', 'FOCUSED RESOLVE', 'Karma tạo ra một sợi dây kết nối giữa bản thân với một kẻ địch, gây sát thương và làm nạn nhân bị lộ diện. Nếu mối liên kết không bị phá vỡ, kẻ địch sẽ bị trói chân và gây sát thương lần nữa. Mantra Cường Hóa: Karma cường hóa mối liên kết, hồi máu bản thân và tăng thời gian trói chân.', 'Karma creates a tether between herself and a targeted enemy, dealing damage and revealing them. If the tether is not broken, the enemy will be rooted and damaged again. Mantra Bonus: Karma strengthens the link, healing herself and extending the root duration.', 'KarmaSpiritBind.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0043/ability_0043_W1.webm', 59),
(294, 'LINH GIÁP', 'INSPIRE', 'Karma triệu hồi một lá chắn chặn sát thương và tăng Tốc độ Di chuyển cho đồng minh được bảo vệ. Kinh Mantra: Năng lượng tỏa ra từ mục tiêu ban đầu, cường hóa giá trị cho lá chắn ban đầu và áp dụng Linh Giáp cho đồng minh xung quanh.', 'Karma summons a protective shield that absorbs incoming damage and increases the Move Speed of the protected ally. Mantra Bonus: Energy radiates out from her target, strengthening the initial shield and applying Inspire to nearby allied champions.', 'KarmaSolKimShield.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0043/ability_0043_E1.webm', 59),
(295, 'KINH MANTRA', 'MANTRA', 'Karma cường hóa kĩ năng kế giúp gây thêm hiệu ứng. Kinh Mantra xuất hiện từ ngay cấp 1 và không cần điểm kĩ năng để tăng cấp.', 'Karma empowers her next ability to do an additional effect. Mantra is available at level 1 and does not require a skill point.', 'KarmaMantra.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0043/ability_0043_R1.webm', 59),
(311, 'THƯỢNG NHÂN CẢNH GIỚI', 'DIVINE ASCENT', 'Đòn đánh của Kayle được cường hóa khi cô lên cấp và cộng điểm kỹ năng. Đôi cánh của cô ngày một rực rỡ hơn, dần dần tăng Tốc độ Đánh, Tốc độ Di chuyển, tầm đánh, và bắn sóng lửa khi đánh.', 'Kayle\'s attacks are empowered by the heavens as she levels up and spends skill points. Her wings are lit aflame as she progressively gains Attack Speed, Move Speed, Attack Range, and waves of fire on her attacks.', 'Kayle_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0010/ability_0010_P1.webm', 63),
(312, 'HÀO QUANG TRỪNG PHẠT', 'RADIANT BLAST', 'Kayle triệu hồi thánh kiếm xuyên qua kẻ địch, gây sát thương, làm chậm và giảm chống chịu.', 'Kayle conjures a portal, summoning a celestial sword that pierces through enemies, slowing, damaging, and reducing the resistances of all hit.', 'KayleQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0010/ability_0010_Q1.webm', 63),
(313, 'THIÊN GIỚI BAN PHƯỚC', 'CELESTIAL BLESSING', 'Được thiên giới ban phước, Kayle hồi máu và tăng Tốc độ Di chuyển cho bản thân cùng đồng minh gần nhất.', 'Blessed by the divine, Kayle heals and grants Move Speed to herself and the nearest ally.', 'KayleW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0010/ability_0010_W1.webm', 63),
(314, 'KIẾM TINH HỎA', 'STARFIRE SPELLBLADE', 'Nội tại: Thánh kiếm của Kayle, Thiên Đạo, gây thêm sát thương phép lên kẻ địch cô tấn công. Kích hoạt: Đòn đánh kế tiếp của Kayle trừng phạt kẻ địch bằng lửa sao trời, gây thêm sát thương tùy theo máu đã mất của chúng.', 'Passive: Kayle\'s celestial sword, Virtue, deals bonus magic damage to enemies she attacks. Active: Kayle\'s next attack smites her target with celestial fire, dealing bonus damage proportionate to their missing health.', 'KayleE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0010/ability_0010_E1.webm', 63),
(315, 'THẦN LINH ĐỊNH ĐOẠT', 'DIVINE JUDGMENT', 'Kayle khiến một tướng đồng minh bất tử và gọi Thượng Nhân Công Lý xuống thanh tẩy khu vực quanh người cô chọn.', 'Kayle makes an ally invulnerable and calls upon former Aspects of Justice to purify the area around her target with a holy rain of swords.', 'KayleR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0010/ability_0010_R1.webm', 63),
(321, 'DẤU ẤN SẤM SÉT', 'MARK OF THE STORM', 'Kennen sẽ làm choáng kẻ địch có 3 cộng dồn Dấu Ấn Sấm Sét.', 'Kennen stuns enemies he hits 3 times with his abilities.', 'Kennen_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0085/ability_0085_P1.webm', 65),
(322, 'PHI TIÊU SÉT', 'THUNDERING SHURIKEN', 'Kennen phóng ám khí vào kẻ địch, gây sát thương và khắc một Dấu Ấn Sấm Sét vào nạn nhân.', 'Kennen throws a fast moving shuriken towards a location, causing damage and adding a Mark of the Storm to any opponent that it hits.', 'KennenShurikenHurlMissile1.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0085/ability_0085_Q1.webm', 65),
(323, 'GIẬT SÉT', 'ELECTRICAL SURGE', 'Đòn đánh của Kennen có thể gây thêm sát thương và khắc một Dấu Ấn Sấm Sét lên mục tiêu sau mỗi cú đánh, đồng thời hắn có thể kích hoạt kỹ năng này để sát thương và khắc thêm một Dấu Ấn Sấm Sét lên những nạn nhân đang bị khắc dấu ấn này.', 'Kennen passively deals extra damage and adds a Mark of the Storm to his target every few attacks, and he can activate this ability to damage and add another Mark of the Storm to targets who are already marked.', 'KennenBringTheLight.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0085/ability_0085_W1.webm', 65),
(324, 'TỐC ĐỘ SẤM SÉT', 'LIGHTNING RUSH', 'Kennen biến thành dạng sét, có thể đi xuyên vật thể và đặt Dấu Ấn Sấm Sét. Trong dạng này, Kennen được tăng Tốc độ Di chuyển. Khi trở về dạng thường, Kennen được tăng tốc độ đánh.', 'Kennen morphs into a lightning form, enabling him to pass through units and apply a Mark of the Storm. Kennen gains Move Speed when entering this form, and attack speed when leaving it.', 'KennenLightningRush.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0085/ability_0085_E1.webm', 65),
(325, 'BÃO SẤM SÉT', 'SLICING MAELSTROM', 'Kennen triệu hồi một cơn bão tấn công ngẫu nhiên lên các tướng địch cạnh bên gây sát thương phép.', 'Kennen summons a storm that strikes at nearby enemy champions for magical damage.', 'KennenShurikenStorm.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0085/ability_0085_R1.webm', 65),
(351, 'LOẠN ĐẢ', 'FLURRY', 'Sau khi Lee Sin sử dụng một kỹ năng, 2 đòn đánh kế tiếp tăng Tốc độ Đánh và hồi Nội Năng.', 'After Lee Sin uses an ability, his next 2 basic attacks gain Attack Speed and return Energy.', 'LeeSinPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0064/ability_0064_P1.webm', 71),
(352, 'SÓNG ÂM / VÔ ẢNH CƯỚC', 'SONIC WAVE / RESONATING STRIKE', 'Sóng Âm: Lee Sin chưởng ra một luồng sóng nghịch âm nhằm định vị mục tiêu, gây sát thương vật lí lên kẻ địch đầu tiên trúng phải. Nếu Sóng Âm trúng mục tiêu, Lee Sin có thể sử dụng Vô Ảnh Cước trong 3 giây kế tiếp. Vô Ảnh Cước: Lee Sin phóng tới kẻ địch trúng phải Sóng Âm, gây sát thương vật lý dựa', 'Sonic Wave: Lee Sin projects a discordant wave of sound to locate his enemies, dealing physical damage to the first enemy it encounters. If Sonic Wave hits, Lee Sin can cast Resonating Strike for the next 3 seconds. Resonating Strike: Lee Sin dashes to the enemy hit by Sonic Wave, dealing physical d', 'LeeSinQOne.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0064/ability_0064_Q1.webm', 71),
(353, 'HỘ THỂ / KIÊN ĐỊNH', 'SAFEGUARD / IRON WILL', 'Hộ Thể: Lee Sin lướt đến một đơn vị đồng minh, bảo vệ bản thân khỏi sát thương. Nếu mục tiêu là tướng đồng minh, cả hai đều được bảo vệ. Sau khi sử dụng Hộ Thể, Lee Sin có thể niệm Kiên Định. Kiên Định: Sự dày công khổ luyện giúp Lee Sin tung hoành trên chiến trường. Lee Sin được tăng Hút Máu, Hút ', 'Safeguard: Lee Sin rushes to target ally, shielding himself from damage. If the ally is a champion, they are also shielded. After using Safeguard, Lee Sin can cast Iron Will. Iron Will: Lee Sin\'s intense training allows him to thrive in battle. Lee Sin gains Life Steal and Spell Vamp.', 'LeeSinWOne.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0064/ability_0064_W1.webm', 71),
(354, 'ĐỊA CHẤN / DƯ CHẤN', 'TEMPEST / CRIPPLE', 'Địa Chấn: Lee Sin dậm xuống đất tạo sóng âm gây sát thương phép và làm lộ diện kẻ địch trúng phải. Nếu Địa Chấn đánh trúng một kẻ địch, Lee Sin có thể thi triển Dư Chấn. Dư Chấn: Lee Sin làm tê liệt kẻ địch chịu sát thương từ Địa Chấn, làm giảm Tốc Độ Di Chuyển của chúng. Tốc Độ Di Chuyển sẽ hồi ', 'Tempest: Lee Sin smashes the ground, sending out a shockwave that deals magic damage and reveals enemy units hit. If Tempest hits an enemy, Lee Sin can cast cripple. Cripple: Lee Sin cripples nearby enemies damaged by Tempest, reducing their Move Speed. Move Speed recovers gradually over the duratio', 'LeeSinEOne.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0064/ability_0064_E1.webm', 71),
(355, 'NỘ LONG CƯỚC', 'DRAGON\'S RAGE', 'Lee Sin tung cước đá văng mục tiêu về phía sau, gây sát thương vật lí lên nạn nhân và bất cứ kẻ địch nào bị va phải. Những kẻ địch mà bị mục tiêu va phải trên đường bay sẽ bị hất văng lên không trung trong một thời gian ngắn. Chiêu thức này được truyền dạy bởi Jesse Perring, mặc dù Lee Sin không đá ', 'Lee Sin performs a powerful roundhouse kick launching his target back, dealing physical damage to the target and any enemies they collide with. Enemies the target collides with are knocked into the air for a short duration. This technique was taught to him by Jesse Perring, although Lee Sin does not', 'LeeSinR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0064/ability_0064_R1.webm', 71),
(356, 'ÁNH SÁNG MẶT TRỜI', 'SUNLIGHT', 'Các kỹ năng sát thương đặt Ánh Sáng Mặt Trời lên kẻ địch trong 1.5 giây. Khi tướng đồng minh gây sát thương lên các mục tiêu này, họ kích phát Ánh Sáng Mặt Trời để gây thêm sát thương phép.', 'Damaging spells afflict enemies with Sunlight for 1.5 seconds. When allied Champions deal damage to those targets, they consume the Sunlight to deal additional magic damage.', 'LeonaSunlight.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0089/ability_0089_P1.webm', 72),
(357, 'KHIÊN MẶT TRỜI', 'SHIELD OF DAYBREAK', 'Leona đánh đòn tiếp theo bằng khiên của mình, gây thêm sát thương phép và làm choáng kẻ thù.', 'Leona uses her shield to perform her next basic attack, dealing bonus magic damage and stunning the target.', 'LeonaShieldOfDaybreak.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0089/ability_0089_Q1.webm', 72),
(358, 'NHẬT THỰC', 'ECLIPSE', 'Leona nâng cao chiếc khiên giúp giảm sát thương nhận được và gia tăng Giáp và Kháng Phép. Khi hiệu ứng kết thúc lần đầu, nếu xung quanh có kẻ địch cô sẽ gây sát thương phép lên chúng và kéo dài thêm thời gian hiệu lực của hiệu ứng.', 'Leona raises her shield to gain Damage Reduction, Armor, and Magic Resist. When the duration first ends, if there are nearby enemies, she will deal magic damage to them and prolong the duration of the effect.', 'LeonaSolarBarrier.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0089/ability_0089_W1.webm', 72),
(359, 'THIÊN ĐỈNH KIẾM', 'ZENITH BLADE', 'Leona phóng ra một lưỡi kiếm mang ánh sáng mặt trời, gây sát thương lên tất cả kẻ địch trên đường thẳng. Khi ánh sáng mờ dần, kẻ địch cuối cùng bị tác động sẽ bị giữ chân trong thời gian ngắn và Leona lướt đến cạnh bên mục tiêu.', 'Leona projects a solar image of her sword, dealing magic damage to all enemies in a line. When the image fades, the last enemy champion struck will be briefly immobilized and Leona will dash to them.', 'LeonaZenithBlade.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0089/ability_0089_E1.webm', 72),
(360, 'THÁI DƯƠNG HẠ SAN', 'SOLAR FLARE', 'Leona triệu hồi sức mạnh của thái dương, gây sát thương lên những kẻ địch trong vùng ảnh hưởng. Những nạn nhân ở khu vực trung tâm sẽ bị choáng, còn những kẻ ngoài rìa sẽ bị làm chậm.', 'Leona calls down a beam of solar energy, dealing damage to enemies in an area. Enemies in the center of the area are stunned, while enemies on the outside are slowed.', 'LeonaSolarFlare.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0089/ability_0089_R1.webm', 72),
(361, 'QUYỀN TRƯỢNG MỘNG MỊ', 'DREAM-LADEN BOUGH', 'Tấn công tướng hoặc quái vật bằng kỹ năng sẽ gây thêm một lượng sát thương theo thời gian tương đương Máu tối đa của mục tiêu.', 'Hitting a champion or monster with a skill will deal additional max Health damage over time.', 'Lillia_Icon_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0876/ability_0876_P1.webm', 73),
(362, 'TRƯỢNG HOA XOAY TRÒN', 'BLOOMING BLOWS', 'Nội tại sẽ cho phép Lillia nhận cộng dồn tăng Tốc độ Di chuyển mỗi khi tấn công kẻ địch với kỹ năng. Cô có thể kích hoạt để gây sát thương lên những kẻ địch cạnh bên, và gây thêm sát thương chuẩn lên kẻ địch ở ngoài rìa kỹ năng.', 'Passively, Lillia gains stacking Move Speed when hitting enemies with spells. She can activate this to deal magic damage to nearby enemies, dealing extra true damage on the edge.', 'LilliaQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0876/ability_0876_Q1.webm', 73),
(363, 'NỆN CÁI NÈ!', 'WATCH OUT! EEP!', 'Lillia tấn công vào khu vực chỉ định ở gần, và gây lượng lớn sát thương tại điểm trung tâm.', 'Lillia deals damage in a nearby area, dealing heavy damage in the center.', 'LilliaW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0876/ability_0876_W1.webm', 73),
(364, 'HẠT LĂN LÓC', 'SWIRLSEED', 'Lillia đẩy hạt cây về phía trước, gây sát thương và làm chậm mục tiêu nó chạm vào. Nếu không trúng gì cả, nó sẽ tiếp tục lăn đến khi chạm vào tường hoặc mục tiêu nào đó.', 'Lillia hurls a seed that damages and slows those it lands on. If it doesn\'t hit anything, it will continue rolling until it hits a wall or target.', 'LilliaE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0876/ability_0876_E1.webm', 73),
(365, 'KHÚC RU RỪNG THẲM', 'LILTING LULLABY', 'Lillia khiến tất cả kẻ địch chịu ảnh hưởng bởi Phấn Hoa Mộng Mị trở nên Buồn Ngủ trước khi rơi vào giấc mộng. Những kẻ địch đó sẽ chịu thêm sát thương khi bị ép phải thức dậy.', 'Lillia causes all enemies with Dream Dust on them to become Drowsy before falling asleep. Those enemies will take extra damage on being forcibly woken up.', 'LilliaR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0876/ability_0876_R1.webm', 73),
(371, 'XẠ THỦ ÁNH SÁNG', 'LIGHTSLINGER', 'Mỗi khi Lucian sử dụng kỹ năng, đòn đánh tiếp theo của anh sẽ bắn hai phát đạn Khi nhận được hồi máu hay tạo lá chắn từ đồng minh, hoặc khi một tướng địch gần đó bị làm bất động, hai đòn đánh tiếp theo của Lucian gây thêm sát thương phép.', 'Whenever Lucian uses an ability, his next attack becomes a double-shot. When Lucian is healed or shielded by an ally, or when a nearby enemy Champion is immobilized, his next 2 basic attacks will deal bonus magic damage.', 'Lucian_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0236/ability_0236_P1.webm', 75),
(372, 'TIA SÁNG XUYÊN THẤU', 'PIERCING LIGHT', 'Lucian bắn ra một tia sáng xuyên thấu qua mục tiêu.', 'Lucian shoots a bolt of piercing light through a target.', 'LucianQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0236/ability_0236_Q1.webm', 75);
INSERT INTO `skills` (`id`, `skill_name_vi`, `skill_name_en`, `skill_desc_vi`, `skill_desc_en`, `skill_image`, `skill_video_link`, `champion_id`) VALUES
(373, 'TIA SÁNG RỰC CHÁY', 'ARDENT BLAZE', 'Lucian bắn ra một viên đạn phát nổ thành hình sao, đánh dấu và làm lộ diện kẻ địch. Lucian được tăng Tốc độ Di chuyển về phía kẻ địch bị đánh dấu.', 'Lucian shoots a missile that explodes in a star shape, marking and briefly revealing enemies. Lucian gains Move Speed for attacking marked enemies.', 'LucianW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0236/ability_0236_W1.webm', 75),
(374, 'TRUY CÙNG DIỆT TẬN', 'RELENTLESS PURSUIT', 'Lucian lướt đi một đoạn ngắn. Các đòn tấn công từ nội tại Xạ Thủ Ánh Sáng sẽ giảm thời gian hồi của Truy Cùng Diệt Tận.', 'Lucian quickly dashes a short distance. Lightslinger attacks reduce Relentless Pursuit\'s cooldown.', 'LucianE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0236/ability_0236_E1.webm', 75),
(375, 'THANH TRỪNG', 'THE CULLING', 'Lucian phóng thích ra một luồng đạn từ vũ khí của mình.', 'Lucian unleashes a torrent of shots from his weapons.', 'LucianR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0236/ability_0236_R1.webm', 75),
(376, 'BẠN ĐỒNG HÀNH PIX', 'PIX, FAERIE COMPANION', 'Pix bắn tia ma thuật mỗi khi tướng cậu đi theo tấn công 1 kẻ địch. Tia ma thuật đuổi theo mục tiêu nhưng có thể bị các đơn vị khác chặn lại.', 'Pix fires magical bolts of energy whenever the champion he\'s following attacks another enemy unit. These bolts are homing, but can be intercepted by other units.', 'Lulu_PixFaerieCompanion.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0117/ability_0117_P1.webm', 76),
(377, 'NGỌN THƯƠNG ÁNH SÁNG', 'GLITTERLANCE', 'Pix và Lulu thay nhau bắn các tia chớp gây sát thương và làm chậm những kẻ địch trúng phải.', 'Pix and Lulu each fire a bolt of magical energy that damages and heavily slows all enemies it hits.', 'LuluQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0117/ability_0117_Q1.webm', 76),
(378, 'BIẾN HÓA', 'WHIMSY', 'Nếu niệm lên một đồng minh, tăng cho họ Tốc độ Di chuyển trong một thời gian ngắn. Nếu niệm lên một kẻ địch, biến nạn nhân thành một sinh vật không thể tấn công hay niệm phép.', 'If cast on an ally, grants them Attack Speed and Move Speed for a short time. If cast on an enemy, turns them into an adorable critter that can\'t attack or cast spells.', 'LuluW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0117/ability_0117_W1.webm', 76),
(379, 'GIÚP NÀO PIX!', 'HELP, PIX!', 'Nếu niệm lên đồng minh, Pix sẽ bám vào mục tiêu và tạo lá chắn đồng thời hỗ trợ các đòn tấn công của họ. Nếu niệm lên kẻ địch, Pix sẽ bám vào nạn nhân và sát thương chúng, ngoài ra nó còn làm lộ diện kẻ địch với cả đội của bạn.', 'If cast on an ally, commands Pix to jump to an ally and shield them. He then follows them and aids their attacks. If cast on an enemy, commands Pix to jump to an enemy and damage them. He then follows them and grants you vision of that enemy.', 'LuluE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0117/ability_0117_E1.webm', 76),
(380, 'KHỔNG LỒ HÓA', 'WILD GROWTH', 'Lulu hóa lớn một đồng minh, hất kẻ địch xung quanh mục tiêu lên không đồng thời tăng cho mục tiêu một lượng máu lớn. Trong vòng vài giây kế tiếp, đơn vị đồng minh đó sở hữu hào quang làm chậm kẻ địch xung quanh.', 'Lulu enlarges an ally, knocking nearby enemies into the air and granting the ally a large amount of bonus health. For the next few seconds, that ally gains an aura that slows nearby enemies.', 'LuluR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0117/ability_0117_R1.webm', 76),
(381, 'CHỚP LỬA SOI RỌI', 'ILLUMINATION', 'Các kỹ năng sát thương của Lux phủ năng lượng lên kẻ địch trong một vài giây. Đòn đánh kế tiếp của Lux đốt cháy năng lượng này, gây thêm sát thương (tùy theo cấp của Lux) lên mục tiêu.', 'Lux\'s damaging spells charge the target with energy for a few seconds. Lux\'s next attack ignites the energy, dealing bonus magic damage (depending on Lux\'s level) to the target.', 'LuxIlluminatingFraulein.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0099/ability_0099_P1.webm', 77),
(382, 'KHÓA ÁNH SÁNG', 'LIGHT BINDING', 'Lux phóng ra một quả cầu ánh sáng làm trói chân và gây sát thương tối đa 2 mục tiêu.', 'Lux releases a sphere of light that binds and deals damage to up to two enemy units.', 'LuxLightBinding.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0099/ability_0099_Q1.webm', 77),
(383, 'LĂNG KÍNH PHÒNG HỘ', 'PRISMATIC BARRIER', 'Lux ném chiếc gậy phép của mình làm bẻ cong tia sáng quanh các đơn vị đồng minh chạm phải, bảo vệ họ khỏi sát thương nhận phải.', 'Lux throws her wand and bends the light around any friendly target it touches, protecting them from enemy damage.', 'LuxPrismaticWave.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0099/ability_0099_W1.webm', 77),
(384, 'QUẢ CẦU ÁNH SÁNG', 'LUCENT SINGULARITY', 'Bắn một vòng xoáy năng lượng đến vị trí đã chọn, làm chậm kẻ địch cạnh bên. Lux có thể kích nổ gây sát thương tại vị trí đó.', 'Fires an anomaly of twisted light to an area, which slows nearby enemies. Lux can detonate it to damage enemies in the area of effect.', 'LuxLightStrikeKugel.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0099/ability_0099_E1.webm', 77),
(385, 'CẦU VỒNG TỐI THƯỢNG', 'FINAL SPARK', 'Sau khi tích tụ năng lượng, Lux bắn một dải sáng gây sát thương lên tất cả mục tiêu trong vùng ảnh hưởng. Ngoài ra tuyệt chiêu này còn kích hoạt nội tại của Lux và đặt lại thời gian bùa hại Chớp Lửa Soi Rọi trên mục tiêu.', 'After gathering energy, Lux fires a beam of light that deals damage to all targets in the area. In addition, triggers Lux\'s passive ability and refreshes the Illumination debuff duration.', 'LuxR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0099/ability_0099_R1.webm', 77),
(391, 'CHUYỂN ĐỔI HƯ KHÔNG', 'VOID SHIFT', 'Khi không chịu sát thương hoặc bị khống chế trong một khoảng thời gian, Malzahar nhận lượng lớn giảm sát thương và miễn nhiễm với hiệu ứng khống chế. Hiệu ứng sẽ kéo dài trong một khoảng thời gian ngắn sau khi chịu sát thương.', 'When he hasn\'t recently taken damage or been crowd controlled, Malzahar gains massive damage reduction and crowd control immunity, lingering for a short period after taking damage.', 'Malzahar_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0090/ability_0090_P1.webm', 79),
(392, 'TIẾNG GỌI HƯ KHÔNG', 'CALL OF THE VOID', 'Malzahar mở ra hai cánh cổng đến Hư Không. Sau thoáng chốc, chúng bắn ra những tia gây sát thương phép và làm câm lặng tướng địch.', 'Malzahar opens up two portals to the Void. After a short delay, they fire projectiles that deal Magic Damage and silence enemy champions.', 'MalzaharQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0090/ability_0090_Q1.webm', 79),
(393, 'BẦY BỌ HƯ KHÔNG', 'VOID SWARM', 'Malzahar triệu hồi Bọ Hư Không để tấn công các kẻ địch gần đó.', 'Malzahar summons Voidlings to attack nearby enemies.', 'MalzaharW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0090/ability_0090_W1.webm', 79),
(394, 'ÁM ẢNH KINH HOÀNG', 'MALEFIC VISIONS', 'Malzahar ếm lên tâm trí mục tiêu những ảo ảnh ghê rợn và tàn độc, gây sát thương theo thời gian. Dùng các kỹ năng khác của Malzahar lên mục tiêu sẽ làm mới lại thời gian ám ảnh. Nếu mục tiêu bị hạ trong lúc đang chịu ám ảnh, nó chuyển sang kẻ địch gần đó và Malzahar được hồi lại Năng lượng. Bọ Hư Kh', 'Malzahar infects his target\'s mind with cruel visions of their demise, dealing damage over time. Using Malzahar\'s other spells on the target will refresh the visions. If the target dies while afflicted by the visions, they pass on to a nearby enemy unit and Malzahar gains Mana. Malzahar\'s Voidlings ', 'MalzaharE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0090/ability_0090_E1.webm', 79),
(395, 'ÂM TI TRÓI BUỘC', 'NETHER GRASP', 'Malzahar vận dụng sức mạnh của Hư Không, áp chế tướng địch trong một vùng năng lượng độc hại gây sát thương.', 'Malzahar channels the essence of the Void to suppress an enemy champion over a zone of damaging negative energy.', 'MalzaharR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0090/ability_0090_R1.webm', 79),
(401, 'CHÉM ĐÔI', 'DOUBLE STRIKE', 'Sau vài đòn đánh liên tiếp, Master Yi chém hai lần liên tiếp.', 'Every few consecutive basic attack, Master Yi strikes twice.', 'MasterYi_Passive1.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0011/ability_0011_P1.webm', 81),
(402, 'TUYỆT KỸ ALPHA', 'ALPHA STRIKE', 'Master Yi bay khắp chiến trường với tốc độ lóa mắt, gây sát thương vật lý lên nhiều kẻ địch, đồng thời trở nên không thể bị chỉ định. Tuyệt Kỹ Alpha có thể chí mạng và gây thêm sát thương vật lý lên quái. Đánh thường giảm hồi chiêu Tuyệt Kỹ Alpha', 'Master Yi teleports across the battlefield with blinding speed, dealing physical damage to multiple units in his path, while simultaneously becoming untargetable. Alpha Strike can critically strike and deals bonus physical damage to monsters. Basic attacks reduce Alpha Strike\'s cooldown.', 'AlphaStrike.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0011/ability_0011_Q1.webm', 81),
(403, 'THIỀN', 'MEDITATE', 'Master Yi nhập thiền, hồi Máu và giảm sát thương gánh chịu trong chốc lát. Ngoài ra, Master Yi sẽ nhận cộng dồn Chém Đôi và ngưng thời gian tác dụng còn lại của Kiếm Thuật Wuju và Chiến Binh Sơn Cước với mỗi giây thiền định.', 'Master Yi rejuvenates his body by focus of mind, restoring Health and taking reduced damage for a short time. In addition, Master Yi will gain stacks of Double Strike and pause the remaining duration on Wuju Style and Highlander for each second he channels.', 'Meditate.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0011/ability_0011_W1.webm', 81),
(404, 'VÕ THUẬT WUJU', 'WUJU STYLE', 'Tăng thêm sát thương chuẩn cho đòn đánh thường.', 'Grants bonus true damage on basic attacks.', 'WujuStyle.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0011/ability_0011_E1.webm', 81),
(405, 'CHIẾN BINH SƠN CƯỚC', 'HIGHLANDER', 'Master Yi di chuyển với sự linh hoạt vô bì, tạm thời tăng Tốc độ Di chuyển và Tốc độ Đánh cũng như được miễn nhiễm làm chậm. Khi kích hoạt, hạ gục hoặc hỗ trợ kéo dài thời gian tác dụng của Chiến Binh Sơn Cước. Nội tại giảm hồi chiêu của các kỹ năng khác khi hạ gục hoặc hỗ trợ.', 'Master Yi moves with unparalleled agility, temporarily increasing his Move Speed and Attack Speed as well as making him immune to all slowing effects. While active, Champion kills or assists extend Highlander\'s duration. Passively reduces cooldown for his other abilities on a kill or assist.', 'Highlander.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0011/ability_0011_R1.webm', 81),
(406, 'NỔI LỬA!', 'FIRED UP!', 'Các kỹ năng của Milio phù phép cho đồng minh khi chạm vào họ, khiến đòn đánh tiếp theo của đồng minh gây thêm sát thương và thiêu đốt mục tiêu.', 'Milio\'s abilities enchant allies on touch, making their next damage deal a burst of extra damage and burn the target.', 'Milio_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0902/ability_0902_P1.webm', 82),
(407, 'HỎA CƯỚC SIÊU CƯỜNG', 'ULTRA MEGA FIRE KICK', 'Sút một quả bóng đẩy lùi kẻ địch. Quả bóng nảy về hướng kẻ địch, gây sát thương và làm chậm kẻ địch trong vùng ảnh hưởng khi va chạm.', 'Kick a ball that knocks back an enemy. The ball launches upward on hit and falls toward the enemy, damaging and slowing enemies in the area upon impact.', 'MilioQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0902/ability_0902_Q1.webm', 82),
(408, 'LỬA TRẠI ẤM CÚNG', 'COZY CAMPFIRE', 'Tạo một vùng cường hóa có khả năng hồi máu và tăng Tầm Đánh cho các đồng minh ở bên trong. Vùng này sẽ bám theo đồng minh đứng gần vị trí thi triển nhất.', 'Create an empowering zone that heals allies and increases attack range to those inside. The zone follows the ally nearest to the cast point.', 'MilioW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0902/ability_0902_W1.webm', 82),
(409, 'CÁI ÔM ẤM ÁP', 'WARM HUGS', 'Milio tạo lá chắn cho một đồng minh, tăng Tốc Độ Di Chuyển của đồng minh đó trong thời gian ngắn. Kĩ năng này có 2 tích trữ.', 'Milio tosses a shield to an ally, temporarily increasing their movement speed. This ability has 2 charges.', 'MilioE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0902/ability_0902_E1.webm', 82),
(410, 'HƠI THỞ SỰ SỐNG', 'BREATH OF LIFE', 'Milio giải phóng một đợt sóng lửa trị thương có khả năng hồi máu và loại bỏ hiệu ứng khống chế cho đồng minh trong phạm vi.', 'Milio unleashes a wave of soothing flames that heal and remove crowd control effects from allies in range.', 'MilioR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0902/ability_0902_R1.webm', 82),
(431, 'DẬY SÓNG', 'SURGING TIDES', 'Khi Kỹ năng của Nami trúng tướng đồng minh, họ sẽ nhận thêm Tốc độ Di chuyển trong một khoảng ngắn.', 'When Nami\'s Abilities hit allied champions they gain Move Speed for a short duration.', 'NamiPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0267/ability_0267_P1.webm', 87),
(432, 'THỦY NGỤC', 'AQUA PRISON', 'Bắn một cái bong bóng khổng lồ vào điểm đã chọn, gây sát thương và làm choáng tất cả nạn nhân trúng phải.', 'Sends a bubble to a target area, dealing damage and stunning all enemies on impact.', 'NamiQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0267/ability_0267_Q1.webm', 87),
(433, 'THỦY TRIỀU', 'EBB AND FLOW', 'Phóng ra một luồng nước nảy giữa đồng minh và kẻ địch, hồi máu cho đồng minh và sát thương kẻ địch.', 'Unleashes a stream of water that bounces back and forth between allied and enemy champions, healing allies and damaging enemies.', 'NamiW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0267/ability_0267_W1.webm', 87),
(434, 'ƯỚC NGUYỆN TIÊN CÁ', 'TIDECALLER\'S BLESSING', 'Cường hóa một tướng đồng minh trong thoáng chốc, khiến các đòn đánh và kỹ năng của mục tiêu gây thêm sát thương phép và làm chậm mục tiêu.', 'Empowers an allied champion for a short duration. The ally\'s basic attacks and spells deal bonus magic damage and slow the target.', 'NamiE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0267/ability_0267_E1.webm', 87),
(435, 'SÓNG THẦN', 'TIDAL WAVE', 'Tạo ra một cơn Sóng Thần khổng lồ hất văng lên không, làm chậm, và sát thương các kẻ địch trúng phải. Tướng đồng minh đứng trong phạm vi của Sóng Thần sẽ được nhận hiệu ứng Dậy Sóng gấp 2 lần.', 'Summons a massive Tidal Wave that knocks up, slows, and damages enemies. Allies hit gain double the effect of Surging Tides.', 'NamiR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0267/ability_0267_R1.webm', 87),
(436, 'KẺ NUỐT CHỬNG LINH HỒN', 'SOUL EATER', 'Nasus hút năng lượng tinh thần của đối thủ, cho ông thêm Hút Máu.', 'Nasus drains his foe\'s spiritual energy, giving him bonus Life Steal.', 'Nasus_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0075/ability_0075_P1.webm', 88),
(437, 'QUYỀN TRƯỢNG LINH HỒN', 'SIPHONING STRIKE', 'Nasus tấn công kẻ địch, gây sát thương và cường hóa sức mạnh của các cú Quyền Trượng Linh Hồn về sau nếu ông hạ gục được mục tiêu.', 'Nasus strikes his foe, dealing damage and increasing the power of his future Siphoning Strikes if he slays his target.', 'NasusQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0075/ability_0075_Q1.webm', 88),
(438, 'LÃO HÓA', 'WITHER', 'Nasus làm lão hóa mục tiêu, khiến nạn nhân bị giảm Tốc độ Di chuyển và Tốc độ Đánh theo thời gian.', 'Nasus ages an enemy champion, decelerating their Move Speed and Attack Speed over time.', 'NasusW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0075/ability_0075_W1.webm', 88),
(439, 'LỬA TÂM LINH', 'SPIRIT FIRE', 'Nasus tạo ra một ngọn lửa tâm linh tại vị trí đã chọn, gây sát thương và làm giảm Giáp của những kẻ địch đứng trên đó.', 'Nasus unleashes a spirit flame at a location, dealing damage and reducing the Armor of enemies who stand on it.', 'NasusE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0075/ability_0075_E1.webm', 88),
(440, 'CƠN THỊNH NỘ SA MẠC', 'FURY OF THE SANDS', 'Nasus giải phóng sức mạnh tối thượng từ bão cát, liên tục sát thương kẻ địch ở gần. Trong thời gian hiệu lực, ông nhận thêm Máu, Tầm Đánh, gây sát thương lên kẻ địch ở gần, và hồi chiêu của Quyền Trượng Linh Hồn được giảm, cũng như được nhận thêm Giáp và Kháng Phép.', 'Nasus unleashes a mighty sandstorm that batters nearby enemies. While the storm rages, he gains increased Health, Attack Range, damages nearby enemies, has a reduced cooldown on Siphoning Strike, and gains bonus Armor and Magic Resistance.', 'NasusR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0075/ability_0075_R1.webm', 88),
(441, 'MỎ NEO NGÀN CÂN', 'STAGGERING BLOW', 'Đòn đánh đầu tiên của Nautilus lên mục tiêu gây thêm sát thương vật lý và trói chân chúng trong thoáng chốc.', 'Nautilus\' first Attack against a target deals increased physical damage and roots them briefly.', 'Nautilus_StaggeringBlow.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0111/ability_0111_P1.webm', 89),
(442, 'PHÓNG MỎ NEO', 'DREDGE LINE', 'Nautilus ném mỏ neo về phía trước. Nếu va chạm với tướng, nó sẽ kéo mục tiêu và Nautilus lại với nhau, gây sát thương phép. Nếu va chạm với địa hình, nó sẽ kéo Nautilus đến đó.', 'Nautilus hurls his anchor forward. Colliding with an enemy pulls them and Nautilus together, dealing magic damage. Colliding with terrain pulls Nautilus towards it.', 'NautilusAnchorDrag.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0111/ability_0111_Q1.webm', 89),
(443, 'CƠN GIẬN CỦA NGƯỜI KHỔNG LỒ', 'TITAN\'S WRATH', 'Nautilus nhận một Lá chắn tạm thời. Khi còn tồn tại, Đòn đánh của Nautilus sẽ gây sát thương theo thời gian lên mục tiêu và cả những kẻ địch xung quanh.', 'Nautilus gains a temporary Shield. While it persists, his Attacks deal damage over time to his target and surrounding enemies.', 'NautilusPiercingGaze.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0111/ability_0111_W1.webm', 89),
(444, 'THỦY TRIỀU DỮ DỘI', 'RIPTIDE', 'Nautilus dậm xuống đất và tạo ra ba đợt sóng địa chấn xung quanh bản thân. Mỗi lần đều gây sát thương và làm chậm kẻ địch.', 'Nautilus creates three exploding waves around himself. Each explosion damages and slows enemies.', 'NautilusSplashZone.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0111/ability_0111_E1.webm', 89),
(445, 'THỦY LÔI TẦM NHIỆT', 'DEPTH CHARGE', 'Nautilus bắn ra một cơn sóng chấn động xuống mặt đất đuổi theo kẻ địch. Cơn sóng này sẽ phá nát mặt đất phía trên, hất văng kẻ địch lên trời. Khi đuổi kịp đối phương, cơn sóng bùng nổ, hất mục tiêu lên không và làm choáng chúng.', 'Nautilus fires a shockwave into the earth that chases an opponent. This shockwave rips up the earth above it, knocking enemies into the air. When it reaches the opponent, the shockwave erupts, knocking his target into the air and stunning them.', 'NautilusGrandLine.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0111/ability_0111_R1.webm', 89),
(446, 'HUYỀN ẢNH', 'INHERENT GLAMOUR', 'Neeko có thể cải trang thành một tướng đồng minh. Đánh nhau với tướng hoặc sử dụng kĩ năng gây sát thương sẽ làm mất lớp cải trang.', 'Neeko can look like an ally champion. Taking damage from enemy Champions or casting damaging spells breaks the disguise.', 'Neeko_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0518/ability_0518_P1.webm', 90),
(447, 'PHÁO HẠT', 'BLOOMING BURST', 'Neeko ném ra một hạt giống gây sát thương phép thuật. Hạt giống sẽ phát nổ thêm một lần nữa mỗi khi trúng tướng hoặc hạ gục lính.', 'Neeko throws a seed dealing magic damage. The seed blooms again on hitting champions or killing units.', 'NeekoQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0518/ability_0518_Q1.webm', 90),
(448, 'PHÂN HÌNH', 'SHAPESPLITTER', 'Nội tại Neeko gây thêm sát thương mỗi đòn đánh thứ ba. Neeko có thể tạo ra một bản sao di chuyển theo hướng chỉ định.', 'Neeko passively deals bonus magic damage every third attack. Neeko can activate to send a clone in a direction.', 'NeekoW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0518/ability_0518_W1.webm', 90),
(449, 'XOẮN TRÓI', 'TANGLE-BARBS', 'Neeko ném một vòng xoắn gây sát thương và trói mọi thứ nó đi qua. Nếu vòng xoắn tiêu diệt một kẻ địch hoặc đi qua một tướng, nó trở nên to hơn, nhanh hơn, và trói lâu hơn.', 'Neeko slings a tangle that damage and root everything it passes through. If the tangle kills an enemy or passes through a champion, it becomes larger, faster, and roots for longer.', 'NeekoE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0518/ability_0518_E1.webm', 90),
(450, 'NỔ HOA', 'POP BLOSSOM', 'Sau một thoáng chuẩn bị, Neeko nhảy lên không trung. Khi cô hạ cánh, đối phương xung quanh sẽ nhận sát thương và bị hất tung. Quá trình chuẩn bị sẽ không bị lộ nếu Neeko đang cải trang.', 'After a short preparation, Neeko leaps into the air. Upon landing, nearby enemies are damaged and knocked up. The preparation is hidden if Neeko is disguised.', 'NeekoR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0518/ability_0518_R1.webm', 90),
(451, 'MÌNH ĐỒNG DA SẮT', 'STONE SKIN', 'Ngộ Không nhận cộng dồn, tăng giáp và hồi máu tối đa khi giao tranh với tướng cũng như quái vật.', 'Wukong gains stacking armor and max health regeneration while fighting champions and monsters.', 'MonkeyKingStoneSkin.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0062/ability_0062_P1.webm', 91),
(452, 'THIẾT BẢNG NGÀN CÂN', 'CRUSHING BLOW', 'Đòn đánh kế tiếp của Ngộ Không được tăng tầm, gây thêm sát thương và giảm giáp của mục tiêu trong vài giây.', 'Wukong\'s next attack gains attack range, deals bonus damage, and reduces the target\'s armor for a few seconds.', 'MonkeyKingDoubleAttack.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0062/ability_0062_Q1.webm', 91),
(453, 'CHIẾN BINH TINH QUÁI', 'WARRIOR TRICKSTER', 'Ngộ Không trở nên Vô Hinh và lướt về hướng chỉ định, để lại một phân thân tấn công kẻ địch gần đó.', 'Wukong becomes Invisible and dashes in a direction, leaving behind a clone that will attack nearby enemies.', 'MonkeyKingDecoy.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0062/ability_0062_W1.webm', 91),
(454, 'CÂN ĐẨU VÂN', 'NIMBUS STRIKE', 'Ngộ Không phóng tới kẻ địch chỉ định và ra lệnh cho phân thân tấn công kẻ địch gần mục tiêu, gây sát thương lên tất cả kẻ địch trúng chiêu.', 'Wukong dashes to a targeted enemy and sends out images to attack enemies near his target, dealing damage to each enemy struck.', 'MonkeyKingNimbus.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0062/ability_0062_E1.webm', 91),
(455, 'LỐC XOÁY', 'CYCLONE', 'Ngộ Không làm cho thiết bảng dài ra rồi múa nó quanh bản thân, nhận thêm Tốc độ Di chuyển. Tất cả kẻ địch dính chiêu sẽ chịu sát thương và bị hất tung.', 'Wukong\'s extends his staff and spins it around repeatedly, gaining Move Speed. Enemies struck take damage and are knocked up.', 'MonkeyKingSpinToWin.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0062/ability_0062_R1.webm', 91),
(456, 'RÌNH RẬP', 'PROWL', 'Đi qua bụi cỏ tăng tốc chạy của Nidalee lên 10% trong 2 giây, tăng lên 30% về phía tướng địch nhìn thấy được trong tầm 1400. Gây sát thương lên tướng và quái bằng Phóng Lao và Bẫy Rập khiến cô Săn Đuổi các mục tiêu này, cho Tầm Nhìn Chuẩn lên chúng trong 4 giây. Trong thời gian này, Nidalee nhận 10%', 'Moving through brush increases Nidalee\'s Move Speed by 10% for 2 seconds, increased to 30% toward visible enemy champions within 1400 range. Hitting champions or monsters with Javelin Toss or Bushwhack triggers a Hunt, granting True Sight of them for 4 seconds. During this time, Nidalee gains 10% Mo', 'Nidalee_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0076/ability_0076_P1.webm', 92),
(457, 'PHÓNG LAO / CẮN XÉ', 'JAVELIN TOSS / TAKEDOWN', 'Trong dạng người, Nidalee phóng một mũi lao gai vào mục tiêu gây sát thương theo quãng đường bay. Trong lốt báo, đòn đánh kế tiếp sẽ làm trọng thương mục tiêu, gây sát thương càng cao khi kẻ địch còn càng ít máu.', 'In human form, Nidalee throws a spiked javelin at her target that gains damage as it flies. As a cougar, her next attack will attempt to fatally wound her target, dealing more damage the less life they have.', 'JavelinToss.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0076/ability_0076_Q1.webm', 92),
(458, 'BẪY RẬP / VỒ', 'BUSHWHACK / POUNCE', 'Trong dạng người, Nidalee đặt một cái bẫy, kẻ địch dẫm trúng sẽ bị phát hiện và chịu sát thương. Trong lốt báo, cô nhảy tới trước, gây sát thương khi tiếp đất.', 'In human form, Nidalee lays a trap for unwary opponents that, when sprung, damages and reveals its target. As a cougar, she jumps in a direction, dealing damage in an area where she lands.', 'Bushwhack.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0076/ability_0076_W1.webm', 92),
(459, 'SỨC MẠNH HOANG DÃ / VẢ', 'PRIMAL SURGE / SWIPE', 'Trong dạng người, Nidalee triệu tập tinh linh của loài báo để hồi máu cho đồng đội và giúp tăng Tốc độ Đánh trong thời gian ngắn. Trong lốt báo, cô tát và gây sát thương vào phía trước của mình.', 'In human form, Nidalee channels the spirit of the cougar to heal her allies and imbue them with Attack Speed for a short duration. As a cougar, she claws in a direction, dealing damage to enemies in front of her.', 'PrimalSurge.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0076/ability_0076_E1.webm', 92),
(460, 'LỐT BÁO', 'ASPECT OF THE COUGAR', 'Nidalee hóa báo giúp nhận dàn kĩ năng mới.', 'Nidalee transforms into a cougar, gaining new abilities.', 'AspectOfTheCougar.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0076/ability_0076_R1.webm', 92),
(461, 'NIỀM VUI BẤT TẬN', 'JOY UNENDING', 'Nilah nhận thêm kinh nghiệm từ việc kết liễu lính, đồng thời cường hóa hiệu ứng hồi máu và lá chắn của đồng minh xung quanh.', 'Nilah gains increased experience from last-hitting minions along with the ability to enhance and share nearby healing and shielding from her allies.', 'NIlahP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0895/ability_0895_P1.webm', 93),
(462, 'THỦY KIẾM VÔ DẠNG', 'FORMLESS BLADE', 'Nilah quất lưỡi roi kiếm, gây sát thương lên kẻ địch theo một đường thẳng. Kỹ năng này cũng sẽ tăng tầm đánh cho Nilah trong thoáng chốc.', 'With a snap of her whip-blade, Nilah damages any enemies she hits in a straight line in her chosen direction. This action extends her attack range for a short duration.', 'NilahQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0895/ability_0895_Q1.webm', 93),
(463, 'THỦY GIÁP BẢO HỘ', 'JUBILANT VEIL', 'Nilah tạo một màn nước bao bọc quanh bản thân, tăng tốc độ di chuyển và cho phép cô né tránh tất cả đòn đánh lên bản thân. Đồng minh được Nilah chạm vào trong thời gian hiệu lực cũng sẽ nhận hiệu ứng kỹ năng này.', 'Nilah envelops herself in mist, increasing her movement speed and allowing her to gracefully dodge all incoming attacks. Any allies she touches during the mist\'s duration will also gain this effect.', 'NilahW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0895/ability_0895_W1.webm', 93),
(464, 'LƯỚT TRÊN MẶT NƯỚC', 'SLIPSTREAM', 'Nilah lướt về phía mục tiêu, gây sát thương phép lên tất cả kẻ địch cô lướt qua trên đường.', 'Nilah enthusiastically dashes toward her target, dealing damage to any enemies she passes through on the way.', 'NilahE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0895/ability_0895_E1.webm', 93),
(465, 'VŨ ĐIỆU HÂN HOAN', 'APOTHEOSIS', 'Nilah thực hiện một vũ điệu, xoay vòng chiếc roi kiếm và gây sát thương lên kẻ địch xung quanh trước khi kéo chúng gần lại với vũ khí của mình.', 'Twirling her whip-blade in joyful exuberance, Nilah deals damage to enemies around her before pulling them closer with her weapon.', 'NilahR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0895/ability_0895_R1.webm', 93),
(471, 'TIẾNG GỌI NƠI BĂNG GIÁ', 'CALL OF THE FRELJORD', 'Nunu tăng tốc độ đánh và tốc độ di chuyển cho Willump và một đồng minh gần đó, và khiến đòn đánh của Willump gây sát thương các kẻ địch quanh mục tiêu', 'Nunu increases the attack speed and Move Speed of Willump and a nearby ally, and causes Willump\'s basic attacks to damage enemies around the target.', 'NunuPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0020/ability_0020_P1.webm', 95),
(472, 'NUỐT CHỬNG', 'CONSUME', 'Willump cắn kẻ địch một cái, gây sát thương và hồi máu cho bản thân.', 'Willump takes a bite out of a minion, monster, or enemy champion, dealing damage and healing himself.', 'NunuQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0020/ability_0020_Q1.webm', 95),
(473, 'CẦU TUYẾT TRONG TRUYỀN THUYẾT', 'BIGGEST SNOWBALL EVER!', 'Willump tạo ra một quả cầu tuyết ngày một to hơn và nhanh hơn. Cầu tuyết gây sát thương và hất tung kẻ địch.', 'Willump creates a snowball that grows in size and speed as he rolls it. The snowball damages and knocks up enemies.', 'NunuW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0020/ability_0020_W1.webm', 95),
(474, 'PHÁO TUYẾT', 'SNOWBALL BARRAGE', 'Nunu ném ra một loạt cầu tuyết gây sát thương và làm chậm mục tiêu. Willump sẽ trói chân những mục tiêu vẫn còn đứng gần nó lúc kỹ năng này kết thúc.', 'Nunu throws multiple snowballs that damage enemies. When he\'s finished, Willump roots any champions or large monsters that were hit by a snowball.', 'NunuE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0020/ability_0020_E1.webm', 95),
(475, 'TUYỆT ĐỈNH BĂNG GIÁ', 'ABSOLUTE ZERO', 'Nunu & Willump tạo ra một cơn bão tuyết cực mạnh trong một khu vực, làm chậm kẻ địch và gây sát thương khủng khiếp khi kết thúc.', 'Nunu & Willump create a powerful blizzard in an area that slows enemies and deals massive damage at the end.', 'NunuR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0020/ability_0020_R1.webm', 95),
(476, 'ĐIÊN CUỒNG', 'BERSERKER RAGE', 'Olaf nhận thêm Tốc Độ Đánh và Hút Máu dựa trên Máu đã mất.', 'Olaf gains Attack Speed and Life Steal based on his missing Health.', 'Olaf_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0002/ability_0002_P1.webm', 96),
(477, 'PHÓNG RÌU', 'UNDERTOW', 'Olaf ném rìu đến điểm chỉ định, gây sát thương lên kẻ địch chúng đi qua, Làm Chậm và giảm Giáp của chúng. Nếu Olaf nhặt được rìu đã ném, hồi chiêu của kỹ năng này sẽ được tái tạo.', 'Olaf throws an axe into the ground at a target location, dealing damage to enemies it passes through and reducing their Armor and Move Speed. If Olaf picks up the axe, the ability\'s cooldown is reset.', 'OlafAxeThrowCast.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0002/ability_0002_Q1.webm', 96),
(478, 'NỔI KHÙNG', 'TOUGH IT OUT', 'Olaf được tăng Tốc Độ Đánh và nhận một Lá Chắn.', 'Olaf\'s Attack Speed is increased and he gains a Shield.', 'OlafFrenziedStrikes.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0002/ability_0002_W1.webm', 96),
(479, 'BỔ CỦI', 'RECKLESS SWING', 'Đòn đánh của Olaf mạnh tới mức gây sát thương chuẩn lên cả hắn và mục tiêu, và được trả lại số Máu đã tổn thất nếu hắn tiêu diệt được mục tiêu.', 'Olaf attacks with such force that it deals true damage to his target and himself, refunding the Health cost if he destroys the target.', 'OlafRecklessStrike.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0002/ability_0002_E1.webm', 96),
(480, 'TẬN THẾ RAGNAROK', 'RAGNAROK', 'Olaf nhận giáp và kháng phép nội tại. Gã có thể kích hoạt kỹ năng này để trở nên kháng mọi hiệu ứng, miễn là gã tiếp tục tấn công.', 'Olaf passively gains increased armor and magic resist. He can activate this ability to become immune to disables for as long as he keeps attacking.', 'OlafRagnarok.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0002/ability_0002_R1.webm', 96),
(481, 'LÊN DÂY CÓT', 'CLOCKWORK WINDUP', 'Đòn đánh của Orianna gây thêm sát thương phép. Sát thương tăng theo số lần đánh của Orianna lên cùng một mục tiêu.', 'Orianna\'s Attacks deal additional magic damage. This damage increases the more Orianna Attacks the same target.', 'OriannaPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0061/ability_0061_P1.webm', 97),
(482, 'LỆNH: TẤN CÔNG', 'COMMAND: ATTACK', 'Orianna hiệu lệnh cho khối cầu bắn đến vị trí mục tiêu, gây sát thương phép lên những đơn vị va phải trong đường bay (các đơn vị nối tiếp nhau sẽ nhận ít sát thương hơn). Khối cầu sẽ yên vị tại địa điểm đó.', 'Orianna commands her Ball to fire toward a target location, dealing magic damage to targets along the way (deals less damage to subsequent targets). Her Ball remains at the target location after.', 'OrianaIzunaCommand.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0061/ability_0061_Q1.webm', 97),
(483, 'LỆNH: PHÁT SÓNG', 'COMMAND: DISSONANCE', 'Orianna hiệu lệnh Quả Cầu của mình phóng ra năng lượng, gây sát thương phép quanh nó. Trường năng lượng tồn dư tăng tốc đồng minh hoặc làm chậm kẻ địch đi qua.', 'Orianna commands her Ball to release a pulse of energy, dealing magic damage around it. This leaves a field behind that speeds up allies and slows enemies.', 'OrianaDissonanceCommand.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0061/ability_0061_W1.webm', 97),
(484, 'LỆNH: BẢO VỆ', 'COMMAND: PROTECT', 'Orianna hiệu lệnh Quả Cầu bám vào một tướng đồng minh, tạo Lá chắn cho họ và gây sát thương phép lên những kẻ địch va phải trên đường bay. Ngoài ra, khối cầu còn tăng Giáp và Kháng Phép cho tướng nó bám vào.', 'Orianna commands her Ball to attach to an allied champion, Shielding them and dealing magic damage to any enemies it passes through on the way. Additionally, the Ball grants additional Armor and Magic Resist to the champion it is attached to.', 'OrianaRedactCommand.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0061/ability_0061_E1.webm', 97),
(485, 'LỆNH: SÓNG ÂM', 'COMMAND: SHOCKWAVE', 'Orianna hiệu lệnh khối cầu phóng thích ra sóng chấn động, gây sát thương phép và hút kẻ địch bay về phía khối cầu sau một khoảng thời gian ngắn.', 'Orianna commands her Ball to unleash a shockwave, dealing magic damage and launching nearby enemies towards the Ball after a short delay.', 'OrianaDetonateCommand.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0061/ability_0061_R1.webm', 97),
(486, 'CHẾ TẠO TẠI CHỖ', 'LIVING FORGE', 'Ornn nhận thêm Giáp và Kháng Phép cộng thêm từ mọi nguồn. Ornn có thể dùng vàng để rèn trang bị không tiêu thụ ở bất kỳ đâu. Ngoài ra, ông có thể tạo ra các trang bị tuyệt phẩm cho bản thân và đồng minh.', 'Ornn gains an additional bonus Armor and Magic Resist from all sources. Ornn can spend gold to forge non-consumable items anywhere. Additionally, he can create masterwork items for himself and for his allies.', 'OrnnP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0516/ability_0516_P1.webm', 98),
(487, 'NÚI LỬA PHUN TRÀO', 'VOLCANIC RUPTURE', 'Ornn dộng xuống đất, tạo ra một khe nứt gây sát thương vật lý và làm chậm kẻ địch. Sau một khoảng trễ ngắn, một cột dung nham trồi lên ở vị trí chỉ định.', 'Ornn slams the ground, sending out a fissure dealing damage and slowing enemies hit. After a small delay, a magma pillar forms at the end location.', 'OrnnQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0516/ability_0516_Q1.webm', 98),
(488, 'THỔI BỄ', 'BELLOWS BREATH', 'Ornn hiên ngang đi tới, thở ra lửa và không thể bị ngăn cản. Kẻ địch trúng luồng lửa cuối bị Nóng Giòn.', 'Ornn advances, breathing fire. Enemies hit by the final gout of flame become Brittle.', 'OrnnW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0516/ability_0516_W1.webm', 98),
(489, 'XUNG KÍCH BỎNG CHÁY', 'SEARING CHARGE', 'Ornn lao tới, gây sát thương lên các kẻ địch ông băng qua. Nếu Ornn đâm vào địa hình, cú va chạm tạo ra một làn sóng chấn động gây sát thương và hất tung kẻ địch.', 'Ornn charges, dealing damage to enemies he passes through. If Ornn collides with terrain while charging, the impact creates a shockwave around him which deals damage and knocks up enemies.', 'OrnnE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0516/ability_0516_E1.webm', 98),
(490, 'HỎA DƯƠNG HIỆU TRIỆU', 'CALL OF THE FORGE GOD', 'Ornn triệu hồi một con dê lửa khổng lồ ở địa điểm chỉ định. Sau đó, nó sẽ đi về phía ông với tốc độ tăng dần. Kẻ địch bị con dê băng qua chịu sát thương phép, bị làm chậm và Nóng Giòn. Ornn có thể tái kích hoạt kỹ năng để lao tới con dê, đổi hướng nó theo hướng ông đâm vào, khiến con dê hất tung bất', 'Ornn summons a massive elemental at a location which travels toward him with increasing speed. Enemies run over by the elemental take damage, are slowed and are made Brittle. Ornn can recast the ability to charge into the elemental, redirecting it in the direction he hits it, causing the elemental t', 'OrnnR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0516/ability_0516_R1.webm', 98),
(491, 'CHIẾN Ý', 'MORTAL WILL', 'Cứ sau một vài phép hoặc đòn đánh, phép kế tiếp của Pantheon được cường hóa.', 'Every few spells or attacks, Pantheon\'s next spell is empowered.', 'Pantheon_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0080/ability_0080_P1.webm', 99),
(492, 'NGỌN GIÁO SAO BĂNG', 'COMET SPEAR', 'Pantheon đâm giáo hoặc ném giáo theo hướng chỉ định.', 'Pantheon either thrusts his spear or throws his spear in the chosen direction.', 'PantheonQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0080/ability_0080_Q1.webm', 99),
(493, 'KHIÊN TRỜI GIÁNG', 'SHIELD VAULT', 'Pantheon nhảy vào mục tiêu, gây sát thương và làm choáng.', 'Pantheon dashes to a target, damaging and stunning them.', 'PantheonW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0080/ability_0080_W1.webm', 99),
(494, 'TIẾN CÔNG VŨ BÃO', 'AEGIS ASSAULT', 'Pantheon giương khiên chặn hết sát thương đến từ trước mặt và liên tục đâm giáo.', 'Pantheon sets his shield, becoming invulnerable to damage from the front and striking repeatedly with his spear.', 'PantheonE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0080/ability_0080_E1.webm', 99),
(495, 'TRỜI SẬP', 'GRAND STARFALL', 'Pantheon bay lên trời cao rồi rơi như thiên thạch xuống địa điểm chỉ định.', 'Pantheon composes himself and then leaps into the air, landing at a chosen location as a comet.', 'PantheonR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0080/ability_0080_R1.webm', 99),
(496, 'SỨ GIẢ THÉP', 'IRON AMBASSADOR', 'Poppy ném khiên đập vào mục tiêu rồi nảy ra. Cô có thể nhặt khiên để tạo cho mình một lá chắn tạm thời.', 'Poppy throws her buckler that bounces off the target. Poppy can pick it up to gain a temporary shield.', 'Poppy_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0078/ability_0078_P1.webm', 100),
(497, 'BÚA CHẤN ĐỘNG', 'HAMMER SHOCK', 'Poppy vung búa, gây sát thương và tạo ra một vùng làm chậm kẻ địch đồng thời phát nổ sau một thời gian.', 'Poppy swings her hammer, dealing damage and creating a zone that will slow enemies and explode after a delay.', 'PoppyQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0078/ability_0078_Q1.webm', 100),
(498, 'KHÔNG THỂ LAY CHUYỂN', 'STEADFAST PRESENCE', 'Poppy nhận thêm Giáp và Kháng Phép nội tại. Điểm thưởng tăng khi cô thấp Máu. Poppy có thể kích hoạt Không Thể Lay Chuyển để ngăn kẻ địch lướt xung quanh và nhận thêm Tốc độ Di chuyển.', 'Poppy passively gains Armor and Magic Resist. This bonus increases when she is low on Health. Poppy can activate Steadfast Presence to gain Move Speed and stop enemy dashes around her. If a dash is stopped, the enemy is slowed and grounded.', 'PoppyW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0078/ability_0078_W1.webm', 100),
(499, 'XUNG PHONG', 'HEROIC CHARGE', 'Poppy lao tới mục tiêu và đẩy hắn về phía sau. Nếu mục tiêu bị đẩy vào tường, hắn sẽ bị choáng.', 'Poppy dashes to the target and pushes it back. If the target is pushed into a wall, it is stunned.', 'PoppyE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0078/ability_0078_E1.webm', 100),
(500, 'SỨ GIẢ PHÁN QUYẾT', 'KEEPER\'S VERDICT', 'Poppy dồn sức mạnh cho một phát búa đẩy bay kẻ địch đi xa.', 'Poppy channels a hammer strike that knocks enemies very far away.', 'PoppyR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0078/ability_0078_R1.webm', 100),
(511, 'CHIM SĂN MỒI', 'HARRIER', 'Valor, chú đại bàng Demacia của Quinn, đánh dấu Tiêu Điểm lên kẻ địch. Đòn đánh đầu tiên của Quinn lên mục tiêu có Tiêu Điểm sẽ gây thêm sát thương vật lý.', 'Valor, Quinn\'s Demacian eagle, periodically marks enemies as Vulnerable. Quinn\'s first basic attack against Vulnerable targets will deal bonus physical damage.', 'Quinn_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0133/ability_0133_P1.webm', 103),
(512, 'KHÔNG KÍCH', 'BLINDING ASSAULT', 'Quinn gọi Valor lướt tới, đánh dấu và gây sát thương những kẻ địch trong vùng ảnh hưởng.', 'Quinn calls Valor to mark an enemy and hinder its vision before damaging all enemies in the immediate area.', 'QuinnQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0133/ability_0133_Q1.webm', 103),
(513, 'ƯNG NHÃN', 'HEIGHTENED SENSES', 'Quinn được tăng Tốc độ Đánh và Tốc độ Di chuyển sau khi tấn công một kẻ địch Tiêu Điểm. Kích hoạt để Valor làm lộ diện một vùng rộng lớn quanh đó.', 'Passively grants Quinn Attack Speed and Move Speed after she attacks a Vulnerable target. Activate to have Valor reveal a large area nearby.', 'QuinnW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0133/ability_0133_W1.webm', 103),
(514, 'ĐỘT KÍCH', 'VAULT', 'Quinn lướt tới một kẻ địch, gây sát thương vật lí và làm chậm Tốc độ Di chuyển của nạn nhân. Khi đến nơi cô lộn ngược lại ngắt quãng nạn nhân trong thoáng chốc, rồi đáp xuống cách mục tiêu một khoảng bằng tầm đánh tối đa của bản thân.', 'Quinn dashes to an enemy, dealing physical damage and slowing the target\'s Move Speed. Upon reaching the target, she leaps off the target, briefly interrupting it, and lands near her maximum Attack Range away from the target.', 'QuinnE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0133/ability_0133_E1.webm', 103),
(515, 'ĐI QUA ĐỒN ĐỊCH', 'BEHIND ENEMY LINES', 'Valor giúp Quinn bay với tốc độ cao. Kết thúc kỹ năng này sẽ tung ra Oanh Tạc, gây sát thương lên các kẻ địch xung quanh và đánh dấu Tiêu Điểm lên tướng.', 'Quinn and Valor team up to fly around at great speed. Ending the ability casts Skystrike, which deals damage to nearby enemies and marks champions as Vulnerable.', 'QuinnR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0133/ability_0133_R1.webm', 103),
(531, 'PHÁ VỠ KHUÔN PHÉP', 'BREAK THE MOLD', 'Đòn Đánh và Kỹ Năng của Rell đánh cắp Giáp và Kháng Phép khi trúng mục tiêu.', 'Rell\'s Attacks and Abilities steal Armor and Magic Resist on hit.', 'RellP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0526/ability_0526_P1.webm', 107),
(532, 'XUYÊN TÂM KÍCH', 'SHATTERING STRIKE', 'Rell gây sát thương phép lên các đơn vị theo đường thẳng, phá vỡ lá chắn và làm choáng chúng.', 'Rell deals magic damage to units in a line, breaking their shields and stunning them.', 'RellQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0526/ability_0526_Q1.webm', 107),
(533, 'HẮC KIM THUẬT: CHIẾN GIÁP', 'FERROMANCY: CRASH DOWN', 'Chiến Mã: Rell Xuống Ngựa, lao xuống đất cùng bộ giáp của mình, Hất Tung kẻ địch và nhận một lớp Lá Chắn lớn. Khi ở dạng chiến giáp, cô nhận thêm Giáp, Kháng Phép, Tốc Độ Đánh và Tầm Đánh nhưng bị Làm Chậm. Chiến Giáp: Rell biến lớp giáp thành Chiến Mã, được tăng tốc trong thoáng chốc và kẻ địch tiế', 'Mounted: Rell Dismounts, crashing down in her armor, Knocking enemies up and gaining a large Shield. While dismounted, she gains Armor, Magic Resist, Attack Speed, and Attack Range, but is Slowed. Dismounted: Rell forms her Mount, gaining a burst of speed and Knocking Up the next enemy she attacks.', 'RellW_Dismount.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0526/ability_0526_W1.webm', 107),
(534, 'PHI NƯỚC ĐẠI', 'FULL TILT', 'Nội tại: Rell nhận Tốc Độ Di Chuyển khi ngoài giao tranh. Kích hoạt: Rell và một đồng minh nhận Tốc Độ Di Chuyển tăng dần, nhận gấp đôi khi di chuyển về phía kẻ địch và về phía nhau. Đòn Đánh kế tiếp của cô và đồng minh sẽ phát nổ, gây sát thương phép.', 'Passive: Rell gains out of combat Move Speed. Active: Rell and an ally gain ramping Move Speed, doubled toward enemies and each other. Her next Attack explodes, dealing magic damage.', 'RellE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0526/ability_0526_E1.webm', 107),
(535, 'SỨC HÚT KINH HOÀNG', 'MAGNET STORM', 'Rell bùng nổ năng lượng từ trường, Giật mạnh các kẻ địch ở gần về phía mình. Sau đó liên tục Kéo chúng về phía mình trong một vài giây, gây sát thương phép trong thời gian hiệu lực.', 'Rell explodes in a magnetic fury, violently Pulling nearby enemies toward herself. Then Rell constantly Drags nearby enemies toward herself for a short while, dealing magic damage over time.', 'RellR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0526/ability_0526_R1.webm', 107),
(551, 'KIẾM CỔ NGỮ', 'RUNIC BLADE', 'Các kỹ năng của Riven tích sức mạnh cho thanh kiếm, và đòn đánh của cô dùng sức mạnh tích trữ đó để gây thêm sát thương.', 'Riven\'s abilities charge her blade, and her basic attacks expend charges to deal an additional damage.', 'RivenRunicBlades.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0092/ability_0092_P1.webm', 111),
(552, 'TAM BỘ KIẾM', 'BROKEN WINGS', 'Riven có thể tung ra một chuỗi những cú đánh cực mạnh. Kỹ năng này có thể tái kích hoạt 3 lần trong một khoảng thời gian ngắn, và cú đánh thứ ba sẽ hất văng những kẻ địch xung quanh đi.', 'Riven lashes out in a series of strikes. This ability can be reactivated three times in a short time frame with the third hit knocking back nearby enemies.', 'RivenTriCleave.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0092/ability_0092_Q1.webm', 111),
(553, 'KÌNH LỰC', 'KI BURST', 'Riven vận kình lực gây sát thương và làm choáng kẻ địch cạnh bên.', 'Riven emits a Ki Burst, damaging and stunning nearby enemies.', 'RivenMartyr.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0092/ability_0092_W1.webm', 111),
(554, 'ANH DŨNG', 'VALOR', 'Riven bước tới trước một bước ngắn và chặn sát thương nhận vào.', 'Riven steps forward a short distance and blocks incoming damage.', 'RivenFeint.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0092/ability_0092_E1.webm', 111),
(555, 'LƯỠI KIẾM LƯU ĐÀY', 'BLADE OF THE EXILE', 'Riven cường hóa vũ khí của bản thân bằng một luồng năng lượng giúp tăng Sát thương Vật lí và tầm đánh. Ngoài ra, trong khoảng thời gian này, cô có thể sử dụng Chém Gió, một kĩ năng công kích tầm xa cực mạnh.', 'Riven empowers her keepsake weapon with energy, and gains Attack Damage and Range. During this time, she also gains the ability to use Wind Slash, a powerful ranged attack, once.', 'RivenFengShuiEngine.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0092/ability_0092_R1.webm', 111),
(556, 'KHỔNG LỒ BÃI RÁC', 'JUNKYARD TITAN', 'Mỗi kĩ năng Rumble sử dụng sẽ tăng cho hắn thanh Nhiệt. Khi đạt đến 50% Nhiệt hắn sẽ tiến vào Ngưỡng Báo Động, khiến tất cả kĩ năng cơ bản của hắn được tăng cường. Khi đạt đến 100% nhiệt, hắn Quá Tải, tăng cho hắn Tốc độ Đánh và các đòn đánh cơ bản gây thêm sát thương, nhưng bù lại không thể sử dụng', 'Every spell Rumble casts gives him Heat. When he reaches 50% Heat he reaches Danger Zone, granting all his basic abilities bonus effects. When he reaches 100% Heat, he starts Overheating, gaining bonus Attack Speed and granting his basic attacks bonus damage, but making him unable to cast spells for', 'Rumble_JunkyardTitan1.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0068/ability_0068_P1.webm', 112),
(557, 'SÚNG PHUN LỬA', 'FLAMESPITTER', 'Rumble đốt cháy các kẻ địch trước mặt hắn, gây sát thương phép theo hình nón trong 3 giây. Khi đang trong Ngưỡng Báo Động sát thương sẽ được gia tăng.', 'Rumble torches opponents in front of him, dealing magic damage in a cone for 3 seconds. While in Danger Zone this damage is increased.', 'RumbleFlameThrower.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0068/ability_0068_Q1.webm', 112),
(558, 'KHIÊN PHẾ LIỆU', 'SCRAP SHIELD', 'Rumble dựng lên một tấm khiên, bảo vệ hắn khỏi sát thương và cho hắn tăng nhanh tốc độ. Khi trong Ngưỡng Báo Động, độ bền tấm khiên và tốc độ sẽ tăng thêm.', 'Rumble pulls up a shield, protecting him from damage and granting him a quick burst of speed. While in Danger Zone, the shield strength and speed bonus is increased.', 'RumbleShield.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0068/ability_0068_W1.webm', 112);
INSERT INTO `skills` (`id`, `skill_name_vi`, `skill_name_en`, `skill_desc_vi`, `skill_desc_en`, `skill_image`, `skill_video_link`, `champion_id`) VALUES
(559, 'LAO MÓC ĐIỆN', 'ELECTRO HARPOON', 'Rumble bắn lao móc điện vào mục tiêu, gây sát thương phép và làm chậm Tốc độ Di chuyển, và giảm Kháng Phép. Rumble có thể mang 2 lao móc cùng lúc. Khi đạt Ngưỡng Báo Động, sát thương và làm chậm được tăng thêm.', 'Rumble launches a harpoon, electrocuting his target with magic damage, slowing their Move Speed, and reducing their Magic Resist. Rumble can carry 2 harpoons at a time. While in Danger Zone the damage and slow percentage is increased.', 'RumbleGrenade.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0068/ability_0068_E1.webm', 112),
(560, 'MƯA TÊN LỬA', 'THE EQUALIZER', 'Rumble khai hỏa một loạt tên lửa, tạo ra một thảm lửa gây sát thương và làm chậm các kẻ địch trúng phải.', 'Rumble fires off a group of rockets, creating a wall of flames that damages and slows enemies.', 'RumbleCarpetBomb.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0068/ability_0068_R1.webm', 112),
(571, 'CƠN THỊNH NỘ PHƯƠNG BẮC', 'FURY OF THE NORTH', 'Sau khi rời giao tranh, Sejuani có Băng Giáp giúp tăng Giáp và Kháng Phép cũng như miễn nhiễm làm chậm. Băng Giáp tồn tại thêm chốc lát sau khi Sejuani chịu sát thương. Sejuani có thể sát thương một kẻ địch bị choáng để phá vỡ lớp băng, gây lượng lớn sát thương phép.', 'After being out of combat, Sejuani gains Frost Armor which grants Armor and Magic Resist and immunity to slows. Frost Armor persists for a short time after Sejuani takes damage. Sejuani can damage a stunned enemy to shatter it, dealing massive magic damage.', 'Sejuani_passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0113/ability_0113_P1.webm', 115),
(572, 'ĐỘT KÍCH BĂNG GIÁ', 'ARCTIC ASSAULT', 'Sejuani lao tới, hất tung kẻ địch lên không. Pha lao dừng lại sau khi trúng một tướng địch.', 'Sejuani charges forward, knocking enemies into the air. The charge stops after hitting an enemy champion.', 'SejuaniQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0113/ability_0113_Q1.webm', 115),
(573, 'MÙA ĐÔNG THỊNH NỘ', 'WINTER\'S WRATH', 'Sejuani vung chùy hai lần, gây sát thương, làm chậm kẻ địch và áp dụng cộng dồn hiệu ứng Băng.', 'Sejuani swings her mace twice, dealing damage, slowing enemies and applying Frost stacks.', 'SejuaniW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0113/ability_0113_W1.webm', 115),
(574, 'BĂNG GIÁ VĨNH CỬU', 'PERMAFROST', 'Sejuani đóng băng và làm choáng một tướng địch có tối đa cộng dồn Băng.', 'Sejuani freezes and stuns an enemy champion that has maximum Frost stacks.', 'SejuaniE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0113/ability_0113_E1.webm', 115),
(575, 'NHÀ NGỤC BĂNG GIÁ', 'GLACIAL PRISON', 'Sejuani ném bola đóng băng tướng địch đầu tiên trúng phải và tạo ra một cơn bão băng giá làm chậm các kẻ địch khác.', 'Sejuani throws her bola that freezes and stuns the first champion hit and creates an ice storm that slows other enemies.', 'SejuaniR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0113/ability_0113_R1.webm', 115),
(576, 'GIẢI THOÁT', 'ABSOLUTION', 'Khi có kẻ chết gần chỗ Senna, linh hồn của chúng bị Màn Sương Đen cầm tù. Senna có thể tấn công linh hồn đó để giải thoát và hấp thụ Màn Sương đang trói buộc chúng. Màn Sương giúp cô tăng Sức mạnh Công kích, Tầm Đánh, và Tỉ lệ Chí mạng. Đòn đánh của Senna mất nhiều thời gian hơn để bắn, gây thêm sát', 'When units die near Senna, their souls are periodically trapped by the Black Mist. Senna can attack these souls to free them, absorbing the Mist that held them in death. Mist fuels her Relic Cannon\'s power with increased Attack Damage, Attack Range, and Critical Strike Chance. Attacks from Senna\'s R', 'Senna_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0235/ability_0235_P1.webm', 116),
(577, 'BÓNG TỐI XUYÊN THẤU', 'PIERCING DARKNESS', 'Từ đôi nòng của Súng Thần Tích, Senna bắn ra một chùm ánh sáng và bóng tối kết hợp qua mục tiêu, hồi máu cho đồng minh và gây sát thương kẻ địch.', 'From the twin barrels of her Relic Cannon, Senna fires a unified beam of light and shadow through a target, healing allies and damaging enemies.', 'SennaQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0235/ability_0235_Q1.webm', 116),
(578, 'RÀNG BUỘC', 'LAST EMBRACE', 'Senna tung ra làn sóng Sương Đen. Nếu trúng một kẻ địch, nó trói chân kẻ đó và mọi thứ xung quanh sau chốc lát.', 'Senna sends forth a wave of Black Mist. If it hits an enemy it latches onto them hungrily, rooting them and everything nearby after a brief delay.', 'SennaW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0235/ability_0235_W1.webm', 116),
(579, 'LỜI NGUYỀN SƯƠNG ĐEN', 'CURSE OF THE BLACK MIST', 'Senna biến Sương Đen tích bên trong vũ khí thành cơn bão quanh mình, đón nhận bóng tối và trở thành một bóng ma của nó. Đồng minh bước vào vùng bão được ngụy trang và xuất hiện dưới dạng bóng ma. Bóng ma được tăng Tốc Độ Di Chuyển, không thể bị chọn làm mục tiêu, và che giấu danh tính.', 'Senna draws the Mist she has stored in her weapon into a storm around her, embracing darkness and becoming a wraith within. Allies who enter the area are camouflaged and also appear as wraiths as the Mist shrouds them. Wraiths gain increased Move Speed, are unselectable, and hide their identities.', 'SennaE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0235/ability_0235_E1.webm', 116),
(580, 'HẮC ÁM SOI RỌI', 'DAWNING SHADOW', 'Senna triệu tập đá thần tích của các Vệ Binh đã ngã xuống, biến khẩu súng của cô thành vầng hào quang của ánh sáng và bóng tối. Cô sẽ bắn một tia sáng đi khắp nơi, bảo vệ đồng minh và gây sát thương kẻ địch đứng ở trung tâm.', 'Senna calls upon the relic stones of fallen Sentinels, splitting her relic cannon into a holy array of shadow and light. She then fires a global beam that shields allies from harm, while damaging enemies caught in the center.', 'SennaR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0235/ability_0235_R1.webm', 116),
(581, 'NỮ HOÀNG SÂN KHẤU', 'STAGE PRESENCE', 'Mỗi kỹ năng cơ bản thứ ba sẽ khiến Seraphine thi triển nó hai lần. Thêm vào đó, thi triển kỹ năng khi có đồng minh ở gần sẽ giúp đòn đánh kế tiếp của cô nàng được tăng tầm và thêm sát thương phép.', 'Every third basic spell will cast twice from Seraphine. Additionally, casting spells near allies grants her bonus magic damage and range on her next basic attack.', 'Seraphine_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0147/ability_0147_P1.webm', 117),
(582, 'NỐT CAO', 'HIGH NOTE', 'Seraphine gây sát thương trong một khu vực', 'Seraphine deals damage in an area.', 'SeraphineQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0147/ability_0147_Q1.webm', 117),
(583, 'THANH ÂM BAO PHỦ', 'SURROUND SOUND', 'Seraphine tạo Lá chắn và Tăng tốc cho đồng minh ở gần. Nếu như cô nàng đã có Lá chắn thì sẽ Hồi máu cho cả đồng minh xung quanh.', 'Seraphine Shields and Hastes nearby allies. If she is already Shielded she will Heal nearby allies as well.', 'SeraphineW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0147/ability_0147_W1.webm', 117),
(584, 'ĐỔI NHỊP', 'BEAT DROP', 'Seraphine gây sát thương và làm suy yếu hành động di chuyển của kẻ địch trên một đường thẳng.', 'Seraphine deals damage and impairs the movement of enemies in a line.', 'SeraphineE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0147/ability_0147_E1.webm', 117),
(585, 'KHÚC CA LAN TỎA', 'ENCORE', 'Seraphine gây sát thương và mê hoặc kẻ địch trúng chiêu, tái tạo lại tầm thi triển với mỗi đồng minh hoặc tướng địch trúng phải.', 'Seraphine deals damage and charms enemies hit, refreshing the range with every allied or enemy champion hit.', 'SeraphineR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0147/ability_0147_R1.webm', 117),
(626, 'LONG HỎA', 'DRAGON PRACTICE', 'Đánh trúng tướng địch bằng Hơi Thở Bỏng Cháy cho 1 cộng dồn Long Hỏa. Mỗi cộng dồn sẽ tăng thêm sát thương cho các Kỹ Năng cơ bản của Smolder.', 'Hitting champions with Abilities and killing enemies with Super Scorcher Breath grants a stack of Dragon Practice. Stacks increase the damage of Smolders basic Abilities.', 'Icons_Smolder_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0901/ability_0901_P1.webm', 126),
(627, 'HƠI THỞ BỎNG CHÁY', 'SUPER SCORCHER BREATH', 'Smolder phun lửa vào một kẻ địch. Khi nhận thêm điểm cộng dồn, kỹ năng này sẽ được cường hóa.', 'Smolder breathes fire on an enemy. As he gains more stacks, this ability becomes more powerful.', 'SmolderQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0901/ability_0901_Q1.webm', 126),
(628, 'HẮT XÌII!', 'ACHOOO!', 'Smolder hắt xì, phun ra một ngọn lửa tí nị, phát nổ khi trúng tướng địch.', 'Smolder lets out an adorable flaming sneeze that explodes when hitting enemy champions.', 'SmolderW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0901/ability_0901_W1.webm', 126),
(629, 'VỖ CÁNH TUNG BAY', 'FLAP, FLAP, FLAP', 'Smolder cất cánh bay lên, di chuyển vượt địa hình và công phá kẻ địch thấp máu nhất.', 'Smolder takes flight ignoring terrain and bombarding the lowest health enemy.', 'SmolderE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0901/ability_0901_E1.webm', 126),
(630, 'MEEẸ ƠIII!', 'MMOOOMMMM!', 'Smolder gọi mẹ xuất hiện và phun lửa từ không trung, gây thêm sát thương và làm chậm kẻ địch ở chính giữa ngọn lửa.', 'Smolder calls his mom to breath fire from above, dealing extra damage and slowing enemies in the center of her fire.', 'SmolderR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0901/ability_0901_R1.webm', 126),
(661, 'LƯỚT TƯỜNG', 'ROCK SURFING', 'Taliyah nhận thêm tốc độ di chuyển gần tường.', 'Taliyah gains Move Speed near walls.', 'Taliyah_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0163/ability_0163_P1.webm', 133),
(662, 'PHI THẠCH', 'THREADED VOLLEY', 'Taliyah ném một loạt đá về hướng chỉ định, được di chuyển tự do trong khi tung kỹ năng. Kỹ năng này khiến biến mặt đất dưới chân cô thành Đất Bất Ổn. Nếu Taliyah sử dụng Phi Thạch trên Đất Bất Ổn, cô sẽ tiêu thụ nó để ném ra một tảng đá to làm chậm kẻ địch.', 'Taliyah throws a volley of rocks in a target direction, moving freely as she casts. This creates Worked Ground below her. If Taliyah casts Threaded Volley on Worked Ground, she consumes the ground to throw a stronger boulder that slows enemies.', 'TaliyahQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0163/ability_0163_Q1.webm', 133),
(663, 'QUĂNG ĐỊA CHẤN', 'SEISMIC SHOVE', 'Taliyah khiến một vùng đất trồi lên và ném kẻ địch đứng trên nó theo hướng cô chỉ định.', 'Taliyah causes an area of ground to erupt and throws enemies within it in a direction of her choosing.', 'TaliyahWVC.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0163/ability_0163_W1.webm', 133),
(664, 'TẤM THẢM BẤT ỔN', 'UNRAVELED EARTH', 'Taliyah tạo ra một bãi mìn đá gây làm chậm. Nếu kẻ địch lướt qua hoặc bị kéo/hất qua chúng, bãi mìn sẽ phát nổ và làm choáng chúng.', 'Taliyah creates a slowing minefield. If enemies dash through it or are knocked through it, the mines explode and stun them.', 'TaliyahE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0163/ability_0163_E1.webm', 133),
(665, 'MẶT ĐẤT DẬY SÓNG', 'WEAVER\'S WALL', 'Taliyah tạo ra một bức tường siêu dài và lướt trên nó.', 'Taliyah creates a very long wall and then surfs it.', 'TaliyahR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0163/ability_0163_R1.webm', 133),
(666, 'LƯỠI DAO KẾT LIỄU', 'BLADE\'S END', 'Kỹ năng của Talon làm tướng và quái khủng Trúng Thương, cộng dồn nhiều nhất 3 lần. Khi Talon tấn công một tướng có 3 cộng dồn Trúng Thương, kẻ đó bị chảy máu, chịu sát thương theo thời gian.', 'Talon\'s spells Wound champions and large monsters, stacking up to 3 times. When Talon attacks a champion with 3 stacks of Wound, they bleed for heavy damage over time.', 'TalonP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0091/ability_0091_P1.webm', 134),
(667, 'NGOẠI GIAO KIỂU NOXUS', 'NOXIAN DIPLOMACY', 'Talon đâm vào mục tiêu chỉ định. Nếu chúng trong tầm cận chiến, đòn đánh gây chí mạng. Nếu ngoài tầm cận chiến, Talon sẽ nhảy vào mục tiêu trước khi đâm chúng. Talon hoàn lại một phần máu và hồi chiêu nếu kỹ năng này hạ gục mục tiêu.', 'Talon stabs the target unit. If they are within melee range, this attack deals critical damage. If they are outside melee range, Talon will leap at his target before stabbing them. Talon refunds some health and cooldown if this ability kills the target.', 'TalonQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0091/ability_0091_Q1.webm', 134),
(668, 'ÁM KHÍ', 'RAKE', 'Talon phóng ra một loạt dao găm sẽ trở lại chỗ hắn, gây sát thương vật lý mỗi lần đi qua một kẻ địch. Dao găm trở về gây thêm sát thương và làm chậm các đơn vị trúng phải.', 'Talon sends out a volley of daggers that then return back to him, dealing physical damage every time it passes through an enemy. The returning blades deal bonus damage and slow units hit.', 'TalonW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0091/ability_0091_W1.webm', 134),
(669, 'CON ĐƯỜNG THÍCH KHÁCH', 'ASSASSIN\'S PATH', 'Talon nhào lộn qua địa hình hoặc công trình, có khoảng cách tối đa. Kỹ năng này có hồi chiêu thấp, nhưng phải mất rất lâu địa hình đã nhảy qua mới dùng lại được.', 'Talon vaults over any terrain or structure, up to a max distance. This ability has a low cooldown, but puts the used terrain on a long cooldown.', 'TalonE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0091/ability_0091_E1.webm', 134),
(670, 'SÁT THỦ VÔ HÌNH', 'SHADOW ASSAULT', 'Talon tung ra một vòng dao và trở nên Vô Hình, đồng thời tăng thêm Tốc độ Di chuyển. Khi Talon mất Vô Hình, các lưỡi dao tụ hội tại vị trí của hắn. Mỗi khi lưỡi dao di chuyển, Sát Thủ Vô Hình gây sát thương vật lý lên kẻ địch trúng ít nhất một lưỡi dao.', 'Talon disperses a ring of blades and becomes Invisible while gaining additional Move Speed. When Talon emerges from Invisibility, the blades converge on his location. Each time the blades move, Shadow Assault deals physical damage to enemies hit by at least one blade.', 'TalonR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0091/ability_0091_R1.webm', 134),
(676, 'CHIẾN TRANH DU KÍCH', 'GUERRILLA WARFARE', 'Nếu Teemo đứng yên và không thực hiện bất kì hành động nào trong thoáng chốc, cậu sẽ được Vô Hình. Nếu ở trong bụi, Teemo có thể giữ trạng thái vô hình này khi di chuyển. Sau khi thoát khỏi Vô Hình, Teemo có được Yếu Tố Bất Ngờ, tăng Tốc độ Đánh trong vòng vài giây.', 'If Teemo stands still and takes no actions for a short duration, he becomes Invisible indefinitely. If he\'s in brush, Teemo can enter and maintain his Invisibility while moving. After leaving Invisibility, Teemo gains the Element of Surprise, increasing his Attack Speed for a few seconds.', 'Teemo_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0017/ability_0017_P1.webm', 136),
(677, 'PHI TIÊU MÙ', 'BLINDING DART', 'Làm mờ tầm nhìn của kẻ địch bằng một chất độc cực mạnh, gây sát thương lên mục tiêu đồng thời khiến nạn nhân bị mù trong thời gian hiệu lực.', 'Obscures an enemy\'s vision with a powerful venom, dealing damage to the target unit and blinding it for the duration.', 'BlindingDart.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0017/ability_0017_Q1.webm', 136),
(678, 'CHẠY LẸ', 'MOVE QUICK', 'Teemo tăng tốc độ chạy của bản thân đến khi hắn bị tấn công bởi tướng địch hay trụ. Teemo có thể kích hoạt kỹ năng này để tăng tốc mà không bị ảnh hưởng bởi các đòn đánh trong một khoảng thời gian ngắn.', 'Teemo scampers around, passively increasing his Move Speed until he is struck by an enemy champion or turret. Teemo can sprint to gain bonus Move Speed that isn\'t stopped by being struck for a short time.', 'MoveQuick.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0017/ability_0017_W1.webm', 136),
(679, 'BẮN ĐỘC', 'TOXIC SHOT', 'Mỗi đòn đánh của Teemo sẽ làm mục tiêu bị nhiễm độc, gây sát thương khởi điểm, và sát thương mỗi giây trong 4 giây.', 'Each of Teemo\'s attacks will poison the target, dealing damage on impact and each second after for 4 seconds.', 'ToxicShot.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0017/ability_0017_E1.webm', 136),
(680, 'BẪY ĐỘC', 'NOXIOUS TRAP', 'Teemo ném một cái bẫy nấm độc từ trong túi của mình. Nếu kẻ dịch dẫm phải nó sẽ phun ra một làn khói độc, làm chậm và gây sát thương theo thời gian. Nếu Teemo ném nấm vào một nấm khác, nó sẽ nảy thêm một khoảng cách nữa.', 'Teemo throws an explosive poisonous trap using one of the mushrooms stored in his pack. If an enemy steps on the trap, it will release a poisonous cloud, slowing enemies and damaging them over time. If Teemo throws a mushroom onto another mushroom it will bounce, gaining additional range.', 'TeemoRCast.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0017/ability_0017_R1.webm', 136),
(686, 'NGẮM BẮN', 'DRAW A BEAD', 'Tristana được tăng thêm Tầm Đánh khi lên cấp.', 'Increases Tristana\'s Attack Range as she levels.', 'Tristana_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0018/ability_0018_P1.webm', 138),
(687, 'SÚNG LIÊN THANH', 'RAPID FIRE', 'Tốc độ Đánh của Tristana được tăng rất nhiều trong một khoảng thời gian ngắn.', 'Tristana fires her weapon rapidly, increasing her Attack Speed for a short time.', 'TristanaQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0018/ability_0018_Q1.webm', 138),
(688, 'PHÓNG NHẢY TÊN LỬA', 'ROCKET JUMP', 'Tristana nhảy đến vị trí mục tiêu, gây sát thương và làm chậm các đơn vị xung quanh nơi tiếp đất trong thoáng chốc.', 'Tristana fires at the ground to propel her to a distant location, dealing damage and slowing surrounding units for a brief period where she lands.', 'TristanaW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0018/ability_0018_W1.webm', 138),
(689, 'BỌC THUỐC SÚNG', 'EXPLOSIVE CHARGE', 'Khi Tristana tiêu diệt một đơn vị, phát đạn của cô bắn tung ra thành những mảnh nhỏ, gây sát thương lên kẻ địch xung quanh. Có thể kích hoạt để đặt một quả bom lên mục tiêu kẻ địch, thứ sẽ phát nổ sau một thời gian ngắn và gây sát thương lên những đơn vị xung quanh.', 'When Tristana kills a unit, her cannonballs burst into shrapnel, dealing damage to surrounding enemies. Can be activated to place a bomb on a target enemy that explodes after a short duration dealing damage to surrounding units.', 'TristanaE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0018/ability_0018_E1.webm', 138),
(690, 'ĐẠI BÁC ĐẨY LÙI', 'BUSTER SHOT', 'Tristana nạp một viên đạn cỡ bự vào vũ khí rồi bắn vào kẻ địch. Gây Sát thương Phép và đẩy lùi mục tiêu. Nếu mục tiêu đang mang trên mình Bọc Thuốc Súng, quả bom sẽ nổ với phạm vi gấp đôi.', 'Tristana loads a massive cannonball into her weapon and fires it at an enemy unit. This deals Magic Damage and knocks the target back. If the target is carrying the Explosive Charge bomb, the bomb detonation radius is doubled.', 'TristanaR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0018/ability_0018_R1.webm', 138),
(696, 'CUỒNG NỘ CHIẾN TRƯỜNG', 'BATTLE FURY', 'Tryndamere nhận điểm Nộ thông qua mỗi đòn đánh thường, đánh chí mạng, và các cú đánh kết liễu mục tiêu. Nộ tăng Tỉ lệ Chí mạng của hắn và có thể được tiêu thụ để sử dụng kĩ năng Say Máu.', 'Tryndamere gains Fury for each attack, critical strike, and killing blow he makes. Fury passively increases his Critical Strike Chance and can be consumed with his Bloodlust spell.', 'Tryndamere_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0023/ability_0023_P1.webm', 140),
(697, 'SAY MÁU', 'BLOODLUST', 'Tryndamere cảm nhận được sự kích thích trên chiến trường, tăng sát thương vật lí mỗi khi hắn chịu sát thương. Hắn có thể niệm Say Máu để sử dụng điểm nộ và hồi máu cho bản thân.', 'Tryndamere thrives on the thrills of combat, increasing his Attack Damage as he is more and more wounded. He can cast Bloodlust to consume his Fury and heal himself.', 'TryndamereQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0023/ability_0023_Q1.webm', 140),
(698, 'TIẾNG THÉT UY HIẾP', 'MOCKING SHOUT', 'Tryndamere gầm lên khiêu khích, giảm Sát thương Vật lí các tướng xung quanh. Kẻ địch quay lưng lại với Tryndamere bị làm chậm Tốc độ Di chuyển.', 'Tryndamere lets out an insulting cry, decreasing surrounding champions\' Attack Damage. Enemies with their backs turned to Tryndamere also have their Move Speed reduced.', 'TryndamereW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0023/ability_0023_W1.webm', 140),
(699, 'CHÉM XOÁY', 'SPINNING SLASH', 'Tryndamere lướt tới mục tiêu, gây sát thương lên những kẻ địch trên đường.', 'Tryndamere slices toward a target unit, dealing damage to enemies in his path.', 'TryndamereE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0023/ability_0023_E1.webm', 140),
(700, 'TỪ CHỐI TỬ THẦN', 'UNDYING RAGE', 'Sự khao khát chiến đấu của Tryndamere mạnh tới mức hắn không thể chết, bất kể bị thương tổn bao nhiêu.', 'Tryndamere\'s lust for battle becomes so strong that he is unable to die, no matter how wounded he becomes.', 'UndyingRage.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0023/ability_0023_R1.webm', 140),
(701, 'GIAN LẬN', 'LOADED DICE', 'Sau khi tiêu diệt một đơn vị, Twisted Fate lắc xúc xắc \'may mắn\' của mình để nhận thêm từ 1 đến 6 vàng.', 'Upon killing a unit, Twisted Fate rolls his \'lucky\' dice receiving 1 to 6 bonus gold.', 'Cardmaster_SealFate.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0004/ability_0004_P1.webm', 141),
(702, 'PHI BÀI', 'WILD CARDS', 'Phóng ba lá bài theo hình nón, gây sát thương cho mỗi đơn vị nó đi qua.', 'Twisted Fate throws three cards, dealing damage to each enemy unit they pass through.', 'WildCards.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0004/ability_0004_Q1.webm', 141),
(703, 'CHỌN BÀI', 'PICK A CARD', 'Twisted Fate chọn một lá bài ma thuật trong bộ bài của mình, sử dụng nó trong đòn đánh kế tiếp, gây thêm hiệu ứng.', 'Twisted Fate chooses a magic card from his deck, and uses that for his next attack, causing bonus effects.', 'PickACard.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0004/ability_0004_W1.webm', 141),
(704, 'TRÁO BÀI', 'STACKED DECK', 'Mỗi 4 cú đánh, Twisted Fate gây thêm sát thương. Ngoài ra, Tốc độ Đánh của hắn được gia tăng.', 'Every 4 attacks, Twisted Fate deals bonus damage. In addition, his Attack Speed is increased.', 'CardmasterStack.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0004/ability_0004_E1.webm', 141),
(705, 'ĐỊNH MỆNH', 'DESTINY', 'Twisted Fate đoán trước được số phận của kẻ địch, phát hiện tất cả tướng địch, và có khả năng sử dụng Dịch Chuyển, khiến cho Twisted Fate có thể biến đến bất kì vị trí nào trong 1.5 giây.', 'Twisted Fate predicts the fortunes of his foes, revealing all enemy champions and enabling the use of Gate, which teleports Twisted Fate to any target location in 1.5 seconds.', 'Destiny.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0004/ability_0004_R1.webm', 141),
(711, 'LIÊN KẾT LINH GIỚI', 'BRIDGE BETWEEN', 'Udyr có 4 Kỹ Năng cơ bản giúp thay đổi giữa các Thế Võ, và có thể Tái Kích Hoạt 1 Kỹ Năng để tái tạo nó kèm theo hiệu ứng Chiêu Cuối. Ngoài ra, sau khi sử dụng Kỹ Năng, 2 đòn đánh tiếp theo của Udyr được tăng Tốc Độ Đánh.', 'Udyr has four basic Abilities that swap between Stances and can Recast an Ability to renew it with Ultimate benefits. Additionally, after using an Ability, Udyr\'s next two Attacks gain Attack Speed.', 'Udyr_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0077/ability_0077_P1.webm', 143),
(712, 'VUỐT LÔI HÙNG', 'WILDING CLAW', 'Udyr nhận thêm tốc độ đánh và hai đòn đánh tiếp theo gây thêm sát thương vật lý. Tái Kích Hoạt: Nhận thêm tốc độ đánh, và hai đòn đánh tiếp theo của gã sẽ phóng sét vào kẻ địch.', 'Udyr gains attack speed and his next two attacks deal bonus physical damage. Recast: Gain even more attack speed, and the next two attacks call lightning to strike the target.', 'UdyrQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0077/ability_0077_Q1.webm', 143),
(713, 'GIÁP THIẾT TRƯ', 'IRON MANTLE', 'Udyr nhận một lá chắn và hai đòn đánh tiếp theo sẽ hồi máu cho gã Tái Kích Hoạt: Nhận một lá chắn lớn hơn và được hồi máu theo máu tối đa trong vài giây tiếp theo.', 'Udyr gains a shield and his next two attacks heal him. Recast: Gain an even bigger shield and heal based on max health over the next couple seconds.', 'UdyrW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0077/ability_0077_W1.webm', 143),
(714, 'SỪNG HỎA DƯƠNG', 'BLAZING STAMPEDE', 'Udyr nhận một lượng lớn tốc độ di chuyển, đòn đánh đầu tiên của gã lên mỗi mục tiêu sẽ gây Choáng. Tái Kích Hoạt: Nhận thêm tốc độ di chuyển và được miễn nhiễm hiệu ứng bất động trong vài giây.', 'Udyr gains movement speed and his first attack against each target will Stun them. Recast: Gain even more movement speed and immunity to immobilizing effects for a couple seconds.', 'UdyrE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0077/ability_0077_E1.webm', 143),
(715, 'CÁNH BĂNG PHƯỢNG', 'WINGBORNE STORM', 'Udyr triệu hồi một cơn bão tuyết bao bọc quanh bản thân, gây sát thương và làm chậm kẻ địch. Tái Kích Hoạt: Cường hóa và giải phóng cơn bão, khiến nó đuổi theo kẻ địch và gây thêm sát thương.', 'Udyr surrounds himself in a glacial storm, damaging and slowing nearby enemies. Recast: Empower and unleash the storm, causing it to track enemies and deal additional damage.', 'UdyrR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0077/ability_0077_R1.webm', 143),
(726, 'THỢ SĂN BÓNG ĐÊM', 'NIGHT HUNTER', 'Vayne săn lùng những kẻ xấu xa không ngừng nghỉ, cô được tăng Tốc Độ Di Chuyển khi đi về phía các tướng địch ở gần.', 'Vayne ruthlessly hunts evil-doers, gaining Move Speed when moving toward nearby enemy champions.', 'Vayne_NightHunter.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0067/ability_0067_P1.webm', 146),
(727, 'NHÀO LỘN', 'TUMBLE', 'Vayne lộn đi một quãng ngắn. Đòn đánh kế tiếp của cô gây thêm sát thương.', 'Vayne tumbles, maneuvering to carefully place her next shot. Her next attack deals bonus damage.', 'VayneTumble.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0067/ability_0067_Q1.webm', 146),
(728, 'MŨI TÊN BẠC', 'SILVER BOLTS', 'Đầu mũi tên của Vayne được bọc trong một thứ kim loại hiếm có thể khắc chế những thứ xấu xa. Ba đòn đánh hoặc kỹ năng liên tiếp lên cùng một mục tiêu gây thêm sát thương chuẩn theo phần trăm máu tối đa của mục tiêu.', 'Vayne tips her bolts with a rare metal, toxic to evil things. The third consecutive attack or ability against the same target deals a percentage of the target\'s max health as bonus true damage.', 'VayneSilveredBolts.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0067/ability_0067_W1.webm', 146),
(729, 'KẾT ÁN', 'CONDEMN', 'Vayne rút ra chiếc nỏ to bản từ sau lưng, bắn một mũi tên cực đại vào mục tiêu, gây sát thương và đẩy lùi nạn nhân. Nếu mục tiêu bị va phải địa hình, nạn nhân sẽ bị choáng và chịu thêm sát thương.', 'Vayne draws a heavy crossbow from her back, and fires a huge bolt at her target, knocking them back and dealing damage. If they collide with terrain, they are impaled, dealing bonus damage and stunning them.', 'VayneCondemn.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0067/ability_0067_E1.webm', 146),
(730, 'GIỜ KHẮC CUỐI CÙNG', 'FINAL HOUR', 'Chuẩn bị sẵn sàng cho cuộc đối đầu ác liệt, Vayne được tăng Sức mạnh Công kích, có thể Vô Hình khi Nhào Lộn, giảm hồi chiêu Nhào Lộn, và thêm Tốc độ Di chuyển từ Thợ Săn Bóng Đêm.', 'Readying herself for an epic confrontation, Vayne gains increased Attack Damage, Invisibility during Tumble, reduced Tumble cooldown, and more bonus Move Speed from Night Hunter', 'VayneInquisition.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0067/ability_0067_R1.webm', 146),
(746, 'GIÁP CHỐNG CHẤT NỔ', 'BLAST SHIELD', 'Lá chắn của Vi được tích tụ theo thời gian. Nó sẽ được kích hoạt khi Vi tấn công kẻ địch với một kỹ năng của mình.', 'Vi charges a shield over time. The shield can be activated by hitting an enemy with an ability.', 'ViPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0254/ability_0254_P1.webm', 150),
(747, 'CÚ ĐẤM BÙNG NỔ', 'VAULT BREAKER', 'Vi vận sức mạnh rồi tung một cú đấm bùng nổ đẩy cô về phía trước. Các kẻ địch trúng chiêu sẽ bị hất văng đi và chịu thêm một cộng dồn Cú Đấm Phá Giáp.', 'Vi charges her gauntlets and unleashes a vault shattering punch, carrying her forward. Enemies she hits are knocked back and receive a stack of Denting Blows.', 'ViQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0254/ability_0254_Q1.webm', 150),
(748, 'CÚ ĐẤM PHÁ GIÁP', 'DENTING BLOWS', 'Cú đấm của Vi phá Giáp của mục tiêu, gây thêm sát thương đồng thời tăng cho cô thêm Tốc độ Đánh.', 'Vi\'s punches break her opponent\'s Armor, dealing bonus damage and granting her Attack Speed.', 'ViW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0254/ability_0254_W1.webm', 150),
(749, 'CÚ ĐẤM XUYÊN THẤU', 'RELENTLESS FORCE', 'Đòn đánh kế tiếp của Vi xuyên thấu qua mục tiêu, gây sát thương lên cả những đơn vị sau nạn nhân chính.', 'Vi\'s next attack blasts through her target, dealing damage to enemies behind it.', 'ViE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0254/ability_0254_E1.webm', 150),
(750, 'TẢ XUNG HỮU ĐỘT', 'CEASE AND DESIST', 'Vi lao đến một kẻ địch, hất văng những kẻ cản đường sang hai bên. Khi đến bên kẻ địch cô hất văng mục tiêu lên không rồi nhảy bật lên nện nạn nhân xuống mặt đất.', 'Vi runs down an enemy, knocking aside anyone in the way. When she reaches her target she knocks it into the air, jumps after it, and slams it back into the ground.', 'ViR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0254/ability_0254_R1.webm', 150),
(766, 'BÃO TỐ CUỒNG NỘ', 'THE RELENTLESS STORM', 'Các đòn tấn công và kỹ năng sẽ tăng Tốc độ Đánh cho Volibear, rồi sau đó cường hóa nó, giúp gây thêm sát thương phép ở mỗi Đòn lên kẻ địch gần kề.', 'Volibear\'s Attacks and abilities grant Attack Speed, and eventually cause his Attacks to deal bonus magic damage to nearby enemies.', 'Volibear_Icon_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0106/ability_0106_P1.webm', 154),
(767, 'SẤM ĐỘNG', 'THUNDERING SMASH', 'Volibear tăng tốc khi di chuyển về phía kẻ địch, Làm Choáng và gây sát thương lên mục tiêu đầu tiên Tấn công.', 'Volibear gains speed towards enemies, Stunning and damaging the first one he Attacks.', 'VolibearQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0106/ability_0106_Q1.webm', 154),
(768, 'ĐIÊN CUỒNG CẮN XÉ', 'FRENZIED MAUL', 'Volibear tấn công kẻ địch, áp dụng hiệu ứng đòn đánh và đặt dấu ấn lên chúng. Tái sử dụng kỹ năng lên cùng mục tiêu sẽ giúp Volibear gây thêm sát thương và Hồi lại máu.', 'Volibear damages an enemy, applying on-hit effects and marking them. Casting this ability again on the same target deals bonus damage and Heals Volibear.', 'VolibearW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0106/ability_0106_W1.webm', 154),
(769, 'GỌI BÃO', 'SKY SPLITTER', 'Volibear triệu hồi sấm chớp tại khu vực chỉ định, gây sát thương và Làm chậm kẻ địch, đồng thời, tạo một lớp Lá chắn nếu Volibear đứng trong vùng ảnh hưởng.', 'Volibear summons a lightning bolt at a location, dealing damage and Slowing enemies while granting Volibear a Shield if he\'s inside the blast radius.', 'VolibearE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0106/ability_0106_E1.webm', 154),
(770, 'LÔI THẦN GIÁNG THẾ', 'STORMBRINGER', 'Volibear nhảy tới vị trí chỉ định, Làm chậm và gây sát thương lên kẻ địch phía dưới, đồng thời, nhận một lượng Máu cộng thêm. Trụ bảo vệ của phe địch gần khu vực Volibear nhảy tới sẽ tạm thời bị vô hiệu hóa.', 'Volibear leaps to a target location, Slowing and damaging enemies beneath him while gaining bonus Health. Enemy towers near his landing location become temporarily disabled.', 'VolibearR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0106/ability_0106_R1.webm', 154),
(776, 'NHÁT CẮT GỌN GÀNG', 'CLEAN CUTS', 'Sau khi dùng kỹ năng, các đòn đánh kế tiếp của Xayah sẽ đánh trúng tất cả mục tiêu trên đường bay và để lại Lông Vũ.', 'After using an ability, Xayah\'s next basic attacks will hit all targets along their path and leave a Feather.', 'XayahPassive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0498/ability_0498_P1.webm', 156),
(777, 'PHI DAO ĐÔI', 'DOUBLE DAGGERS', 'Xayah phi ra hai dao gây sát thương và để lại Lông Vũ trên đất.', 'Xayah throws two damaging daggers that also drop Feathers she can recall.', 'XayahQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0498/ability_0498_Q1.webm', 156),
(778, 'BỘ CÁNH CHẾT NGƯỜI', 'DEADLY PLUMAGE', 'Xayah tạo một cơn bão phi dao tăng Tốc Độ Đánh và sát thương, đồng thời cho cô Tốc Độ Di Chuyển nếu tấn công một tướng.', 'Xayah creates a storm of blades that increase her Attack Speed and damage while also granting her Move Speed if she attacks a champion.', 'XayahW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0498/ability_0498_W1.webm', 156),
(779, 'TRIỆU HỒI LÔNG VŨ', 'BLADECALLER', 'Xayah triệu hồi tất cả Lông Vũ rơi trên đất, gây sát thương và trói chân kẻ địch\'', 'Xayah calls back all her dropped Feathers dealing damage and rooting enemies.', 'XayahE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0498/ability_0498_E1.webm', 156),
(780, 'BÃO TỐ NỔI DẬY', 'FEATHERSTORM', 'Xayah nhảy lên không, không thể bị chỉ định và quăng ra một loạt phi dao, để lại Lông Vũ trên mặt đất.', 'Xayah leaps into the air becoming untargetable and throwing out a fan of daggers, dropping Feathers she can recall.', 'XayahR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0498/ability_0498_R1.webm', 156),
(781, 'ÁP XUNG NĂNG LƯỢNG', 'MANA SURGE', 'Đòn đánh thường của Xerath hồi Năng Lượng theo định kì. Mỗi khi Xerath kết liễu 1 đơn vị, hồi chiêu sẽ giảm đi.', 'Xerath\'s basic attacks periodically restore Mana. Whenever Xerath kills a unit, this cooldown is reduced.', 'Xerath_Passive1.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0101/ability_0101_P1.webm', 157),
(782, 'XUNG KÍCH NĂNG LƯỢNG', 'ARCANOPULSE', 'Bắn một dải năng lượng tầm xa, gây sát thương phép lên tất cả mục tiêu trúng phải.', 'Fires a long-range beam of energy, dealing magic damage to all targets hit.', 'XerathArcanopulseChargeUp.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0101/ability_0101_Q1.webm', 157),
(783, 'VỤ NỔ NĂNG LƯỢNG', 'EYE OF DESTRUCTION', 'Tạo ra một vùng năng lượng thần bí gây sát thương phép và làm chậm các kẻ địch đứng trong đó. Mục tiêu ở tâm chịu thêm sát thương và làm chậm.', 'Calls down a barrage of arcane energy, slowing and dealing magic damage to all enemies in an area. Targets in the middle receive additional damage and a stronger slow.', 'XerathArcaneBarrage2.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0101/ability_0101_W1.webm', 157),
(784, 'ĐIỆN TÍCH CẦU', 'SHOCKING ORB', 'Gây sát thương phép lên một kẻ địch và làm choáng nạn nhân đó.', 'Deals magic damage to an enemy and stuns them.', 'XerathMageSpear.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0101/ability_0101_E1.webm', 157),
(785, 'NGHI THỨC MA PHÁP', 'RITE OF THE ARCANE', 'Xerath tự cố định vị trí của bản thân rồi nhận được nhiều lần công kích ma pháp trên diện rộng với tầm thi triển cực xa.', 'Xerath immobilizes himself and gains numerous long-range barrages.', 'XerathLocusOfPower2.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0101/ability_0101_R1.webm', 157),
(786, 'QUYẾT ĐOÁN', 'DETERMINATION', 'Mỗi đòn đánh thứ ba gây thêm sát thương và hồi máu cho Xin Zhao.', 'Every third attack deals bonus damage and heals Xin Zhao.', 'XinZhaoP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0005/ability_0005_P1.webm', 158),
(787, 'LIÊN HOÀN TAM KÍCH', 'THREE TALON STRIKE', 'Ba đòn đánh kế tiếp của Xin Zhao gây thêm sát thương. Đòn đánh thứ ba hất tung kẻ địch.', 'Xin Zhao\'s next 3 standard attacks deal increased damage with the third attack knocking an opponent into the air.', 'XinZhaoQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0005/ability_0005_Q1.webm', 158),
(788, 'PHONG LÔI THƯƠNG', 'WIND BECOMES LIGHTNING', 'Xin Zhao dùng thương chém ra trước mặt, sau đó đâm tới, làm chậm kẻ địch bị trúng đòn và đánh dấu Thách Thức mục tiêu', 'Xin Zhao slashes in front of himself with his spear, then thrusts it forward, slowing affected enemies and marking them as Challenged.', 'XinZhaoW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0005/ability_0005_W1.webm', 158),
(789, 'CAN TRƯỜNG', 'AUDACIOUS CHARGE', 'Xin Zhao lao vào một kẻ địch, tăng cho bản thân Tốc Độ Đánh và gây sát thương lên tất cả kẻ địch xung quanh, thoáng làm chậm chúng. Can Trường được tăng thêm tầm lên mục tiêu bị Thách Thức.', 'Xin Zhao charges to an enemy, gaining increased Attack Speed and dealing damage to all enemies in the area, slowing them briefly. Audacious Charge gains increased range against Challenged targets.', 'XinZhaoE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0005/ability_0005_E1.webm', 158),
(790, 'BÁN NGUYỆT THƯƠNG', 'CRESCENT GUARD', 'Xin Zhao thách thức kẻ địch anh gây sát thương gần đây nhất. Xin Zhao gây sát thương lên các kẻ địch gần đó dựa theo máu hiện tại của chúng và đẩy lùi những mục tiêu không bị thách đấu. Xin Zhao được miễn nhiễm sát thương gây ra bởi tướng đừng ngoài vòng tròn được tạo thành.', 'Xin Zhao challenges an enemy he damaged recently. Xin Zhao deals damage to nearby enemies based on their current Health and knocks non-challenged targets back, becoming impervious to damage dealt by champions outside of the circle created.', 'XinZhaoR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0005/ability_0005_R1.webm', 158),
(791, 'ĐẠO CỦA LÃNG KHÁCH', 'WAY OF THE WANDERER', 'Tỉ lệ Chí mạng của Yasuo được gia tăng. Ngoài ra, Yasuo tích lá chắn mỗi khi di chuyển. Lá chắn kích hoạt khi anh chịu sát thương từ tướng hoặc quái.', 'Yasuo\'s Critical Strike Chance is increased. Additionally, Yasuo builds toward a shield whenever he is moving. The shield triggers when he takes damage from a champion or monster.', 'Yasuo_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0157/ability_0157_P1.webm', 159),
(792, 'BÃO KIẾM', 'STEEL TEMPEST', 'Đâm tới, sát thương tất cả kẻ địch trên đường thẳng. Khi trúng, cho một cộng dồn Tụ Bão trong vài giây. Ở 2 cộng dồn, Bão Kiếm bắn ra lốc xoáy hất kẻ địch Lên Không. Bão Kiếm được coi như đòn đánh thường.', 'Thrusts forward, damaging all enemies in a line. On hit, grants a stack of Gathering Storm for a few seconds. At 2 stacks, Steel Tempest fires a whirlwind that knocks Airborne. Steel Tempest is treated as a basic attack and scales with all the same things.', 'YasuoQ1Wrapper.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0157/ability_0157_Q1.webm', 159),
(793, 'TƯỜNG GIÓ', 'WIND WALL', 'Tạo tường gió chặn các luồng đạn của kẻ địch trong 4 giây.', 'Creates a moving wall that blocks all enemy projectiles for 4 seconds.', 'YasuoW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0157/ability_0157_W1.webm', 159),
(794, 'QUÉT KIẾM', 'SWEEPING BLADE', 'Lướt xuyên qua mục tiêu, gây sát thương phép. Mỗi lần lướt sẽ tăng sát thương của lần lướt kế tiếp (có giới hạn tối đa). Không thể dùng lên cùng một kẻ địch trong vài giây. Nếu dùng Bão Kiếm khi đang lướt, nó sẽ tấn công theo hình tròn.', 'Dashes through target enemy, dealing magic damage. Each cast increases subsequent dash\'s damage, up to a max amount. Cannot be re-cast on the same enemy for a few seconds. If Steel Tempest is cast while dashing, it will strike as a circle.', 'YasuoE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0157/ability_0157_E1.webm', 159),
(795, 'TRĂNG TRỐI', 'LAST BREATH', 'Dịch chuyển đến chỗ một tướng địch đang bị Hất Lên Không, gây sát thương vật lý và giữ tất cả những kẻ địch cùng bị Hất Lên Không ở xung quanh trên không thêm một chút. Hồi đầy thanh Nhịp nhưng xóa hết cộng dồn của Tụ Bão. Trong một khoảng thời gian sau đó, những đòn chí mạng của Yasuo sẽ nhận một l', 'Blinks to an Airborne enemy champion, dealing physical damage and holding all Airborne enemies in the area in the air. Grants maximum Flow but resets all stacks of Gathering Storm. For a moderate time afterwards, Yasuo\'s critical strikes gain significant Bonus Armor Penetration.', 'YasuoR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0157/ability_0157_R1.webm', 159),
(806, 'MÈO ĐỒNG HÀNH', 'FELINE FRIENDSHIP', 'Định kỳ, khi Yuumi đánh trúng tướng địch bằng đòn đánh hoặc kỹ năng, cô sẽ hồi máu cho bản thân và đồng minh tiếp theo mà cô Quấn Quýt cùng. Khi đang Quấn Quýt, Yuumi tạo một liên kết đặc biệt với đồng minh của cô. Đồng minh với liên kết mạnh mẽ nhất sẽ cường hóa các kỹ năng của Yuumi khi cô Quấn Qu', 'Periodically, when Yuumi strikes a champion with an attack or ability, she restores health to herself and the next ally she Attaches to. While Attached, Yuumi generates a special bond with her allies. The ally with the strongest bond enhances Yuumi\'s abilities while she is Attached to them.', 'YuumiP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0350/ability_0350_P1.webm', 162),
(807, 'MŨI TÊN THƠ THẨN', 'PROWLING PROJECTILE', 'Yuumi bắn ra một mũi tên, gây sát thương và làm chậm mục tiêu đầu tiên trúng phải. Sát thương và làm chậm sẽ được cường hóa nếu mất ít nhất 1,35 giây trước khi trúng vào mục tiêu. Khi đang bám theo Bạn Thân, hiệu ứng làm chậm sẽ luôn được cường hóa và cho đồng minh của cô sát thương Trên Đòn Đánh. K', 'Yuumi fires a missile, dealing damage and slowing first target hit. It deals bonus damage and an enchanced slow if it takes at least 1.35 seconds to get to its target. While on her Best Friend, the slow is always enhanced and grants bonus On-Hit damage to her ally. While Attached, the missile can be', 'YuumiQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0350/ability_0350_Q1.webm', 162),
(808, 'NHƯ HÌNH VỚI BÓNG!', 'YOU AND ME!', 'Yuumi lướt tới một đồng minh mục tiêu, trở nên không thể bị chỉ định bởi bất kỳ thứ gì ngoài trụ. Khi đang bám theo Bạn Thân, cô nhận thêm Sức Mạnh Hồi Máu & Tạo Lá Chắn, đồng thời cho Đồng Đội của cô hồi máu Trên Đòn Đánh.', 'Yuumi dashes to a target ally, becoming untargetable from everything except turrets. While on her Best Friend, she gains Heal & Shield Power and grants her Ally On-Hit healing.', 'YuumiW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0350/ability_0350_W1.webm', 162),
(809, 'TĂNG ĐỘNG', 'ZOOMIES', 'Tạo lá chắn cho Yuumi và tăng Tốc Độ Di Chuyển & Tốc Độ Đánh. Nếu đang Quấn Quýt, cô chuyển hiệu ứng này cho đồng minh.', 'Shields Yuumi and boosts Move Speed and Attack Speed. If she\'s attached, she passes it to her ally instead.', 'YuumiE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0350/ability_0350_E1.webm', 162),
(810, 'CHƯƠNG CUỐI', 'FINAL CHAPTER', 'Yuumi bắn ra 5 đợt sóng gây sát thương lên kẻ địch và hồi máu cho đồng minh. Yuumi có thể di chuyển, Quấn Quýt và dùng Tăng Động khi đang vận. Khi đang bám trên Bạn Thân, kỹ năng này đồng thời hướng theo con trỏ chuột của cô.', 'Yuumi channels five waves that damage enemies and heal allies. Yuumi can move, attach, and cast Zoomies! while channeling. While on her Best Friend, this spell also follows her mouse.', 'YuumiR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0350/ability_0350_R1.webm', 162),
(821, 'NGUỒN ĐIỆN DI ĐỘNG', 'LIVING BATTERY', 'Đòn Đánh của Zeri gây sát thương phép và được tính như một Kỹ Năng. Di chuyển và sử dụng Súng Điện Liên Hoàn sẽ tích trữ năng lượng cho Ba Lô Tụ Điện của Zeri. Khi sạc đầy, Đòn Đánh kế tiếp của cô sẽ gây thêm sát thương.', 'Zeri\'s Attacks deal magic damage and are treated as Abilities. Moving and casting Burst Fire stores up energy in Zeri\'s Sparkpack. When fully charged her next Attack deals bonus damage.', 'ZeriP.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0221/ability_0221_P1.webm', 165),
(822, 'SÚNG ĐIỆN LIÊN HOÀN', 'BURST FIRE', 'Súng Điện Liên Hoàn bắn ra một loạt 7 tia điện gây sát thương lên kẻ địch đầu tiên trúng phải. Kỹ Năng này được tính như một Đòn Đánh.', 'Burst Fire shoots a burst of 7 rounds that deal attack damage to the first enemy hit. This Ability is treated as an Attack.', 'ZeriQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0221/ability_0221_Q1.webm', 165),
(823, 'XUNG ĐIỆN CAO THẾ', 'ULTRASHOCK LASER', 'Zeri bắn ra một xung điện gây sát thương và làm chậm kẻ địch đầu tiên trúng chiêu. Nếu xung điện chạm vào một bức tường, nó sẽ mở rộng thành một luồng điện tầm xa.', 'Zeri fires an electric pulse that slows and damages the first enemy hit. If the pulse hits a wall it expands into a long range laser.', 'ZeriW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0221/ability_0221_W1.webm', 165),
(824, 'NHANH NHƯ ĐIỆN', 'SPARK SURGE', 'Zeri lướt đi một quãng ngắn và cường hóa cho 3 lần Súng Điện Liên Hoàn tiếp theo, khiến chúng xuyên qua kẻ địch. Cô sẽ nhào lộn qua và lướt dọc theo bất kì địa hình nào mà mình chạm vào.', 'Zeri dashes a short distance and energizes her next 3 shots of Burst Fire, causing them to pierce through enemies. She will vault and grind over any terrain she touches.', 'ZeriE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0221/ability_0221_E1.webm', 165),
(825, 'ĐIỆN ĐẠT ĐỈNH ĐIỂM', 'LIGHTNING CRASH', 'Zeri giải phóng một vụ nổ điện năng và khiến bản thân quá tải, tăng sát thương và Tốc Độ Di Chuyển cho bản thân. Hiệu ứng quá tải sẽ mạnh hơn mỗi khi cô tấn công tướng địch. Khi quá tải, Súng Điện Liên Hoàn sẽ trở thành một loạt ba tia điện được bắn ra nhanh hơn và phóng điện giữa các kẻ địch.', 'Zeri discharges a nova of electricity and overcharges herself, gaining increased damage and stacking Move Speed that refreshes and gets stronger every time she hits an enemy champion. While overcharged, Burst fire becomes a faster triple shot that chains lightning between enemies.', 'ZeriR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0221/ability_0221_R1.webm', 165),
(831, 'LỌ TRỮ THỜI GIAN', 'TIME IN A BOTTLE', 'Zilean tích trữ thời gian dưới dạng Kinh Nghiệm và có thể tăng nó cho đồng minh. Khi có đủ Kinh Nghiệm để lên cấp cho một đồng minh, ông có thể ấn phải vào tướng đó để bắt đầu quá trình. Zilean đồng thời nhận một lượng Kinh Nghiệm tương ứng.', 'Zilean stores time as Experience and can grant it to his allies. When he has enough Experience to finish an ally\'s level up, he can right-click them to impart it. Zilean receives as much Experience as he gives.', 'Zilean_Passive.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0026/ability_0026_P1.webm', 167),
(832, 'BOM HẸN GIỜ', 'TIME BOMB', 'Ném một quả bom vào vùng chỉ định, thứ sẽ bám dính lấy bất kì đơn vị nào dám lại gần nó (ưu tiên Tướng). Quả bom sẽ phát nổ sau 3 giây, gây sát thương ra xung quanh. Nếu Bom Hẹn Giờ được kích nổ sớm hơn bởi một quả Bom Hẹn Giờ khác, nó sẽ làm choáng các nạn nhân.', 'Tosses a bomb to target area that sticks to units that come near it (prioritizes Champions). It detonates after 3 seconds, dealing area of effect damage. If a Time Bomb is detonated early by another Time Bomb, it also stuns enemies.', 'ZileanQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0026/ability_0026_Q1.webm', 167),
(833, 'RÚT NGẮN THỜI GIAN', 'REWIND', 'Zilean có thể chuẩn bị trước cho những bất trắc trong tương lai, giảm thời gian hồi chiêu các kĩ năng cơ bản của nó.', 'Zilean can prepare himself for future confrontations, reducing the cooldowns of his other basic abilities.', 'ZileanW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0026/ability_0026_W1.webm', 167),
(834, 'BẺ CONG THỜI GIAN', 'TIME WARP', 'Zilean bẻ cong thời gian quanh một đơn vị, giảm tốc độ di chuyển nếu đó là kẻ địch và gia tăng nếu đó là đồng minh trong một thời gian ngắn.', 'Zilean bends time around any unit, decreasing an enemy\'s Move Speed or increasing an ally\'s Move Speed for a short time.', 'TimeWarp.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0026/ability_0026_E1.webm', 167),
(835, 'ĐẢO NGƯỢC THỜI GIAN', 'CHRONOSHIFT', 'Zilean ếm bùa bảo vệ lên tướng đồng minh, giúp hồi sinh nạn nhân nếu mục tiêu chẳng may bị hạ gục trong thời gian hiệu lực.', 'Zilean places a protective time rune on an allied champion, teleporting the champion back in time if they take lethal damage.', 'ChronoShift.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0026/ability_0026_R1.webm', 167),
(836, 'LẤP LA LẤP LÁNH!', 'MORE SPARKLES!', 'Đòn đánh kế tiếp của Zoe sau khi tung chiêu gây thêm sát thương phép.', 'Zoe\'s next basic attack after casting a spell deals bonus magic damage.', 'Zoe_P.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0142/ability_0142_P1.webm', 168);
INSERT INTO `skills` (`id`, `skill_name_vi`, `skill_name_en`, `skill_desc_vi`, `skill_desc_en`, `skill_image`, `skill_video_link`, `champion_id`) VALUES
(837, 'NGHỊCH SAO!', 'PADDLE STAR!', 'Zoe bắn ra một luồng phép có thể đổi hướng giữa chừng. Càng bay được nhiều càng gây thêm sát thương.', 'Zoe fires a missile that she can redirect in flight. Deals more damage the longer it flies in a straight line.', 'ZoeQ.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0142/ability_0142_Q1.webm', 168),
(838, 'KẺ TRỘM PHÉP', 'SPELL THIEF', 'Zoe có thể nhặt lấy những mẩu Phép Bổ Trợ và trang bị kích hoạt của kẻ địch rồi tự mình dùng chúng một lần. Mỗi khi dùng một Phép Bổ Trợ, cô gọi ra ba luồng phép bắn vào mục tiêu gần nhất.', 'Zoe can pick up the remnants of enemy summoner spell and active item casts and cast them once herself. Whenever she casts a summoner spell, she gains 3 missiles that fire at the nearest target.', 'ZoeW.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0142/ability_0142_W1.webm', 168),
(839, 'BONG BÓNG NGỦ', 'SLEEPY TROUBLE BUBBLE', 'Khiến mục tiêu trở nên lơ mơ, rồi chìm vào giấc ngủ. Khi trong trạng thái ngủ, mục tiêu sẽ bị giảm Kháng Phép. Nguồn sát thương đầu tiên phá vỡ giấc ngủ sẽ được nhân đôi (với giới hạn tối đa nhất định).', 'Causes the target to become drowsy, then fall asleep. While asleep, the target\'s Magic Resist is reduced. The first source of damage that breaks the sleep is doubled, up to a cap.', 'ZoeE.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0142/ability_0142_E1.webm', 168),
(840, 'VÒNG XUYÊN THẤU', 'PORTAL JUMP', 'Bay tới một vị trí gần đó trong 1 giây. Rồi bay trở về.', 'Blink to a nearby position for 1 second. Then blink back.', 'ZoeR.png', 'https://d28xe8vt774jo5.cloudfront.net/champion-abilities/0142/ability_0142_R1.webm', 168);
